      const searchParams = Object.fromEntries(url.searchParams.entries())

      try {
        // Simulate Zod validation
        if (!searchParams.q) {
          throw new z.ZodError([
            {
              code: 'invalid_type',
              expected: 'string',
              received: 'undefined',
              path: ['q'],
              message: 'Search query is required',
            },
          ])
        }

        return {
          q: searchParams.q,
          limit: searchParams.limit ? parseInt(searchParams.limit, 10) : 10,
        }
      } catch (error) {
        throw error
      }
    }),
    withValidation: vi.fn((handler: Function) => {
      return async (req: NextRequest) => {
        try {
          return await handler(req)
        } catch (error: any) {
          if (error instanceof z.ZodError) {
            return Response.json(
              {
                success: false,
                error: {
                  message: 'Validation error',
                  code: 'VALIDATION_ERROR',
                  details: error.errors,
                },
              },
              { status: 422 }
            )
          }

          return Response.json(
            {
              success: false,
              error: {
                message: error.message || 'An unexpected error occurred',
              },
            },
            { status: 500 }
          )
        }
      }
    }),
  }
})

// Util to simulate a request with query params
const makeRequest = (params: Record<string, string>): NextRequest => {
  const url = new URL('http://localhost/api/search')
  Object.entries(params).forEach(([k, v]) => url.searchParams.append(k, v))
  return new NextRequest(url)
}

