  description:
    'Discover thousands of properties for sale and rent across the UK. Find your perfect home with our easy-to-use search tools.',
  keywords: 'property, real estate, homes for sale, houses for rent, UK property market',
  openGraph: {
    title: 'Property Portal | Find Your Dream Home',
    description: 'Discover thousands of properties for sale and rent across the UK.',
    images: [
      {
        url: 'https://property-portal.com/og-image.jpg',
        width: 1200,
        height: 630,
        alt: 'Property Portal',
      },
    ],
    locale: 'en_GB',
    type: 'website',
  },
  twitter: {
    card: 'summary_large_image',
    title: 'Property Portal | Find Your Dream Home',
    description: 'Discover thousands of properties for sale and rent across the UK.',
    images: ['https://property-portal.com/twitter-image.jpg'],
  },
}

export default function RootLayout({ children }: { children: React.ReactNode }) {
  // Check if we're in development mode and should bypass Clerk
  const isDevelopmentMode = process.env.NEXT_PUBLIC_CLERK_DEVELOPMENT_MODE === 'true'

  // Wrap the content with ClerkProvider only if not in development mode
  const content = (
    <html lang="en">
      <body className="min-h-screen bg-gray-50">
        <GlobalErrorHandler
          sentryDsn={process.env.NEXT_PUBLIC_SENTRY_DSN}
          logRocketAppId={process.env.NEXT_PUBLIC_LOGROCKET_APP_ID}
          environment={process.env.NODE_ENV as 'development' | 'test' | 'production'}
          release={process.env.NEXT_PUBLIC_APP_VERSION}
        >
          <PerformanceMonitor>
            <RouteErrorBoundary>{children}</RouteErrorBoundary>
          </PerformanceMonitor>
        </GlobalErrorHandler>
      </body>
    </html>
  )

  // If in development mode, return content without ClerkProvider
  if (isDevelopmentMode) {
    return content
  }

  // Otherwise, wrap with ClerkProvider
  return (
    <ClerkProvider
      publishableKey={process.env.NEXT_PUBLIC_CLERK_PUBLISHABLE_KEY}
      appearance={{
        elements: {
          formButtonPrimary: 'bg-blue-600 hover:bg-blue-700 text-white',
          card: 'rounded-lg shadow-md',
        },
      }}
    >
      {content}
    </ClerkProvider>
  )
}

'use client'

import { useRouter, usePathname } from 'next/navigation'
import React, { useEffect } from 'react'

import GitHubPagesLink from '@/components/common/GitHubPagesLink'

/**
 * Custom 404 page for Next.js with GitHub Pages support
 */
export default function NotFoundPage() {
  const router = useRouter()
  const pathname = usePathname()

  // For GitHub Pages, handle client-side routing
  useEffect(() => {
    // Check if we're on GitHub Pages
    const isGitHubPages =
      typeof window !== 'undefined' && window.location.hostname.includes('github.io')

    if (isGitHubPages) {
      // Strip the basePath for routing
      const path = pathname.replace('/property-portal', '')

      // If path exists in our app, navigate to it
      if (path && path !== pathname) {
        router.push(path)
      }
    }
  }, [pathname, router])

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-gray-50">
      <div className="max-w-md w-full p-8 bg-white rounded-lg shadow-lg text-center">
        <h1 className="text-9xl font-bold text-blue-600">404</h1>
        <h2 className="mt-4 text-2xl font-bold text-gray-800">Page Not Found</h2>

        <p className="mt-4 text-gray-600">
          The page you are looking for doesn't exist or has been moved.
        </p>

        <div className="mt-8">
          <GitHubPagesLink
            href="/"
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
          >
            Go to Home
          </GitHubPagesLink>
        </div>
      </div>
    </div>
  )
}

