'use client'

import { useState, useRef, useEffect, DragEvent, ChangeEvent } from 'react'

import { Button } from '../../src/ui'

interface FileValidationResult {
  valid: boolean
  message: string
}

interface UploadZoneProps {
  onUpload: (file: File) => void
  acceptedFileTypes?: string | string[]
  maxSizeInMB?: number
  multiple?: boolean
  disabled?: boolean
  showPreview?: boolean
  customValidator?: (file: File) => Promise<FileValidationResult> | FileValidationResult
  className?: string
  description?: string
}

export default function UploadZone({
  onUpload,
  acceptedFileTypes = '.csv',
  maxSizeInMB = 10,
  multiple = false,
  disabled = false,
  showPreview = false,
  customValidator,
  className = '',
  description,
}: UploadZoneProps) {
  const [isDragging, setIsDragging] = useState(false)
  const [selectedFile, setSelectedFile] = useState<File | null>(null)
  const [previewUrl, setPreviewUrl] = useState<string | null>(null)
  const [validationError, setValidationError] = useState<string | null>(null)
  const fileInputRef = useRef<HTMLInputElement>(null)
  const dropZoneRef = useRef<HTMLDivElement>(null)

  // Convert acceptedFileTypes to array for easier handling
  const acceptedTypesArray = Array.isArray(acceptedFileTypes)
    ? acceptedFileTypes
    : [acceptedFileTypes]

  // Clean up preview URL when component unmounts
  useEffect(() => {
    return () => {
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl)
      }
    }
  }, [previewUrl])

  // Handle drag events
  const handleDragOver = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault()
    e.stopPropagation()
    if (!disabled) {
      setIsDragging(true)
    }
  }

  const handleDragLeave = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault()
    e.stopPropagation()
    setIsDragging(false)
  }

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault()
    e.stopPropagation()
    setIsDragging(false)

    if (disabled) return

    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const file = e.dataTransfer.files[0]
      processFile(file)
    }
  }

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      const file = e.target.files[0]
      processFile(file)
    }
  }

  const validateFileType = (file: File): boolean => {
    // If no specific types are required, accept all
    if (acceptedTypesArray.length === 0 || acceptedTypesArray[0] === '*') {
      return true
    }

    // Check if file extension or MIME type matches any accepted type
    return acceptedTypesArray.some(type => {
      // Check by extension (e.g., .csv)
      if (type.startsWith('.')) {
        return file.name.toLowerCase().endsWith(type.toLowerCase())
      }
      // Check by MIME type (e.g., text/csv)
      return file.type === type
    })
  }

  const validateFileSize = (file: File): boolean => {
    const fileSizeInMB = file.size / (1024 * 1024)
    return fileSizeInMB <= maxSizeInMB
  }

  const processFile = async (file: File) => {
    setValidationError(null)

    // Validate file type
    if (!validateFileType(file)) {
      setValidationError(
        `Invalid file type. Please upload ${acceptedTypesArray.join(' or ')} files.`
      )
      return
    }

    // Validate file size
    if (!validateFileSize(file)) {
      setValidationError(`File is too large. Maximum size is ${maxSizeInMB}MB.`)
      return
    }

    // Run custom validator if provided
    if (customValidator) {
      try {
        const result = await Promise.resolve(customValidator(file))
        if (!result.valid) {
          setValidationError(result.message)
          return
        }
      } catch (error) {
        setValidationError('File validation failed.')
        return
      }
    }

    // Create preview for image files
    if (showPreview && file.type.startsWith('image/')) {
      const url = URL.createObjectURL(file)
      setPreviewUrl(url)
    } else {
      setPreviewUrl(null)
    }

    setSelectedFile(file)
    onUpload(file)
  }

  const handleButtonClick = () => {
    if (!disabled && fileInputRef.current) {
      fileInputRef.current.click()
    }
  }

  const clearFile = (e: React.MouseEvent<HTMLButtonElement>) => {
    e.stopPropagation()
    setSelectedFile(null)
    setPreviewUrl(null)
    if (fileInputRef.current) {
      fileInputRef.current.value = ''
    }
  }

  return (
    <div className={`${className}`}>
      <div
        ref={dropZoneRef}
        className={`border-2 border-dashed rounded-lg p-6 text-center transition-colors ${
          disabled
            ? 'bg-gray-100 border-gray-300 cursor-not-allowed opacity-60'
            : isDragging
              ? 'border-purple-500 bg-purple-50'
              : 'border-gray-300 hover:border-purple-400 cursor-pointer'
        }`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        onClick={disabled ? undefined : handleButtonClick}
      >
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          accept={
            Array.isArray(acceptedFileTypes) ? acceptedFileTypes.join(',') : acceptedFileTypes
          }
          multiple={multiple}
          disabled={disabled}
          className="hidden"
        />

        {/* Preview area */}
        {showPreview && previewUrl && (
          <div className="mb-4">
            <img
              src={previewUrl}
              alt="File preview"
              className="max-h-40 max-w-full mx-auto rounded"
            />
          </div>
        )}

        <div className="flex flex-col items-center justify-center">
          {!selectedFile ? (
            <>
              <svg
                className="w-12 h-12 text-gray-400 mb-3"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"
                ></path>
              </svg>
              <p className="mb-2 text-sm text-gray-700">
                <span className="font-semibold">Click to upload</span> or drag and drop
              </p>
              <p className="text-xs text-gray-500">
                {Array.isArray(acceptedFileTypes)
                  ? acceptedFileTypes.join(', ')
                  : acceptedFileTypes}{' '}
                files up to {maxSizeInMB}MB
              </p>
              {description && <p className="mt-2 text-xs text-gray-500">{description}</p>}
            </>
          ) : (
            <div className="flex flex-col items-center">
              <div className="flex items-center mb-2">
                <svg
                  className="w-8 h-8 text-green-500 mr-2"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                  ></path>
                </svg>
                <span className="text-sm font-medium">{selectedFile.name}</span>
              </div>
              <p className="text-xs text-gray-500 mb-3">
                {(selectedFile.size / (1024 * 1024)).toFixed(2)} MB
              </p>
              <Button
                variant="secondary"
                size="small"
                onClick={() => {
                  if (fileInputRef.current) {
                    setSelectedFile(null)
                    setPreviewUrl(null)
                    fileInputRef.current.value = ''
                  }
                }}
                disabled={disabled}
              >
                Choose Different File
              </Button>
            </div>
          )}
        </div>
      </div>

      {/* Error message */}
      {validationError && <div className="mt-2 text-sm text-red-600">{validationError}</div>}
    </div>
  )
}

// components/error-handling/RouteErrorBoundary.tsx
'use client'

import { useRouter } from 'next/navigation'
import React from 'react'

import logger from '@/lib/logging/logger'

import EnhancedErrorBoundary from './EnhancedErrorBoundary'

interface RouteErrorBoundaryProps {
  children: React.ReactNode
  fallback?: React.ReactNode | ((error: Error, resetError: () => void) => React.ReactNode)
  onError?: (error: Error) => void
}

/**
 * Error boundary specifically for handling route errors
 */
const RouteErrorBoundary: React.FC<RouteErrorBoundaryProps> = ({ children, fallback, onError }) => {
  const router = useRouter()

  // Default fallback UI for route errors
  const defaultFallback = (error: Error, resetError: () => void) => {
    return (
      <div className="p-6 bg-blue-50 border border-blue-200 rounded-lg">
        <h2 className="text-xl font-semibold text-blue-700 mb-2">Navigation Error</h2>

        <p className="text-blue-600 mb-4">
          There was a problem loading this page. Please try again or go back to the home page.
        </p>

        <div className="mb-4 p-3 bg-white rounded border border-blue-100 text-sm text-gray-700 overflow-auto max-h-32">
          <p className="font-semibold">
            {error.name}: {error.message}
          </p>
        </div>

        <div className="flex space-x-4">
          <button
            onClick={resetError}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
          >
            Try again
          </button>

          <button
            onClick={() => router.push('/')}
            className="px-4 py-2 border border-blue-300 text-blue-700 rounded hover:bg-blue-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
          >
            Go to Home
          </button>
        </div>
      </div>
    )
  }

  // Handle route errors
  const handleError = (error: Error) => {
    // Log the error
    logger.error(
      'Route error caught by boundary',
      error,
      {
        url: typeof window !== 'undefined' ? window.location.href : undefined,
        pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
      },
      ['error-boundary', 'route-error']
    )

    // Call the optional onError callback
    if (onError) {
      onError(error)
    }
  }

  return (
    <EnhancedErrorBoundary fallback={fallback || defaultFallback} onError={handleError}>
      {children}
    </EnhancedErrorBoundary>
  )
}

export default RouteErrorBoundary

'use client'

import React, { Component, ErrorInfo, ReactNode } from 'react'

import { ApiError } from '@/lib/api/error-handling'
import { ValidationError } from '@/lib/api/validation'
import logger from '@/lib/logging/logger'

interface ErrorBoundaryProps {
  children: ReactNode
  fallback?: ReactNode
}

interface ErrorBoundaryState {
  hasError: boolean
  error: Error | null
}

/**
 * Base error boundary component that catches errors in its child component tree
 */
class EnhancedErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props)
    this.state = { hasError: false, error: null }
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return { hasError: true, error }
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    // Log the error to our monitoring service
    logger.error('Error caught by error boundary:', error, {
      componentStack: errorInfo.componentStack,
      errorName: error.name,
      errorMessage: error.message,
    })
  }

  render(): ReactNode {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback
      }

      return (
        <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
          <h3 className="text-lg font-semibold text-red-800 mb-2">Something went wrong</h3>
          <p className="text-red-700 mb-3">
            {this.state.error?.message || 'An unexpected error occurred'}
          </p>
          <button
            onClick={() => this.setState({ hasError: false, error: null })}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Try again
          </button>
        </div>
      )
    }

    return this.props.children
  }
}

/**
 * Specialized error boundary for API errors
 */
class ApiErrorBoundary extends EnhancedErrorBoundary {
  render(): ReactNode {
    if (this.state.hasError) {
      const error = this.state.error
      const isApiError = error instanceof ApiError
      const statusCode = isApiError ? (error as ApiError).statusCode : 500
      const errorCode = isApiError ? (error as ApiError).code : 'UNKNOWN_ERROR'
      const details = isApiError ? (error as ApiError).details : undefined

      return (
        <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
          <h3 className="text-lg font-semibold text-red-800 mb-2">API Error</h3>
          <p className="text-red-700 mb-2">{error?.message || 'Failed to fetch data'}</p>

          <div className="bg-white p-3 rounded mb-3 text-sm font-mono">
            <p>Status: {statusCode}</p>
            <p>Code: {errorCode}</p>
            {details && <p>Details: {JSON.stringify(details)}</p>}
          </div>

          <button
            onClick={() => this.setState({ hasError: false, error: null })}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Retry
          </button>
        </div>
      )
    }

    return this.props.children
  }
}

/**
 * Specialized error boundary for form validation errors
 */
class FormErrorBoundary extends EnhancedErrorBoundary {
  render(): ReactNode {
    if (this.state.hasError) {
      const error = this.state.error
      const isValidationError = error instanceof ValidationError
      const fieldErrors = isValidationError ? (error as ValidationError).fieldErrors : {}

      return (
        <div className="p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
          <h3 className="text-lg font-semibold text-yellow-800 mb-2">Validation Error</h3>
          <p className="text-yellow-700 mb-3">{error?.message || 'Form validation failed'}</p>

          {Object.keys(fieldErrors).length > 0 && (
            <div className="bg-white p-3 rounded mb-3">
              <h4 className="font-semibold mb-2">Field Errors:</h4>
              <ul className="list-disc pl-5">
                {Object.entries(fieldErrors).map(([field, message]) => (
                  <li key={field} className="text-red-600">
                    <span className="font-semibold">{field}:</span> {message}
                  </li>
                ))}
              </ul>
            </div>
          )}

          <button
            onClick={() => this.setState({ hasError: false, error: null })}
            className="px-4 py-2 bg-yellow-600 text-white rounded hover:bg-yellow-700"
          >
            Fix and retry
          </button>
        </div>
      )
    }

    return this.props.children
  }
}

