// lib/logging/simplified-logger.ts

/**
 * Sensitive fields that should never be logged
 * Add any field names that might contain sensitive information
 */
const SENSITIVE_FIELDS = [
  'password',
  'token',
  'secret',
  'authorization',
  'apiKey',
  'api_key',
  'key',
  'credential',
  'ssn',
  'socialSecurity',
  'creditCard',
  'cardNumber',
  'cvv',
]

export enum LogLevel {
  DEBUG = 'debug',
  INFO = 'info',
  WARN = 'warn',
  ERROR = 'error',
  FATAL = 'fatal',
}

interface LoggerConfig {
  enableConsole?: boolean
  minLevel?: LogLevel
  sentryDsn?: string
  logRocketAppId?: string
  userId?: string
  userEmail?: string
  requestId?: string
  release?: string
  environment?: 'development' | 'test' | 'production'
}

/**
 * Sanitize an object to remove sensitive data before logging
 * @param obj The object to sanitize
 * @returns A sanitized copy of the object
 */
function sanitizeForLogging(obj: unknown, seen = unknown {
  if (!obj || typeof obj !== 'object') {
    return obj
  }

  // Handle circular references
  if (seen.has(obj as object)) {
    return '[Circular]'
  }
  seen.add(obj as object)

  // Handle Error objects specially
  if (obj instanceof Error) {
    const errorObj = {
      name: obj.name,
      message: obj.message,
      stack: obj.stack,
    }

    // Also include any custom properties that might be on the error
    const customProps = Object.getOwnPropertyNames(obj)
      .filter(prop => !['name', 'message', 'stack'].includes(prop))
      .reduce((acc, prop) => {
        acc[prop] = (obj as any)[prop]
        return acc
      }, {} as Record<string, unknown>)

    return sanitizeForLogging({ ...errorObj, ...customProps }, seen)
  }

  // Handle arrays
  if (Array.isArray(obj)) {
    return obj.map(item => sanitizeForLogging(item, seen))
  }

  // Handle objects
  const sanitized = { ...obj }
  for (const key in sanitized) {
    const lowerKey = key.toLowerCase()

    // Check if this key contains any sensitive terms using regex
    if (SENSITIVE_FIELDS_REGEX.test(lowerKey)) {
      sanitized[key] = '[REDACTED]'
    } else if (typeof sanitized[key] === 'object' && sanitized[key] !== null) {
      // Recursively sanitize nested objects
      sanitized[key] = sanitizeForLogging(sanitized[key], seen)
    }
  }

  return sanitized
}

/**
 * Validate environment string to ensure it's one of the expected values
 */
function validateEnvironment(env?: string): 'development' | 'test' | 'production' {
  if (!env || !['development', 'test', 'production'].includes(env)) {
    return 'development'
  }
  return env as 'development' | 'test' | 'production'
}

const defaultConfig: LoggerConfig = {
  enableConsole: true,
  // Set different default log levels based on environment
  minLevel: process.env.NODE_ENV === 'production' ? LogLevel.WARN : LogLevel.DEBUG,
  environment: validateEnvironment(process.env.NODE_ENV),
  release: process.env.NEXT_PUBLIC_APP_VERSION,
}

// Global logger configuration
// TODO: Consider refactoring to use a class or singleton pattern if this module grows more complex
// This would help encapsulate state and provide better testability
let loggerConfig: LoggerConfig = { ...defaultConfig }
let sentryInitialized = false
let logRocketInitialized = false

/**
 * Configure the logger
 */
export function configureLogger(config: Partial<LoggerConfig>) {
  // If reconfiguring with different external service credentials, clean up first
  if (
    (sentryInitialized && config.sentryDsn && config.sentryDsn !== loggerConfig.sentryDsn) ||
    (logRocketInitialized && config.logRocketAppId && config.logRocketAppId !== loggerConfig.logRocketAppId)
  ) {
    shutdownLogger()
  }

  loggerConfig = { ...loggerConfig, ...config }

  // Use environment variables for secrets if not explicitly provided
  const sentryDsn = loggerConfig.sentryDsn || process.env.NEXT_PUBLIC_SENTRY_DSN
  const logRocketAppId = loggerConfig.logRocketAppId || process.env.NEXT_PUBLIC_LOGROCKET_APP_ID

  // Initialize external logging services if configured
  if (sentryDsn && typeof window !== 'undefined') {
    initSentry(sentryDsn, loggerConfig)
  }

  if (logRocketAppId && typeof window !== 'undefined') {
    initLogRocket(logRocketAppId, loggerConfig)
  }

  // In production, ensure we're not logging too verbosely
  if (process.env.NODE_ENV === 'production' && !config.minLevel) {
    loggerConfig.minLevel = LogLevel.WARN
  }
}

/**
 * Shutdown and cleanup logger resources
 * This is useful when reconfiguring the logger or when the application is shutting down
 */
export async function shutdownLogger() {
  if (sentryInitialized && typeof window !== 'undefined') {
    try {
      const Sentry = await import('@sentry/nextjs')
      Sentry.close()
      sentryInitialized = false
    } catch (err) {
      console.error('Failed to shutdown Sentry:', err)
    }
  }

  // LogRocket doesn't have an explicit shutdown method, but we can mark it as not initialized
  logRocketInitialized = false
}

/**
 * Initialize Sentry for error tracking
 * @returns The Sentry instance if initialization was successful, null otherwise
 */
async function initSentry(dsn: string, config: LoggerConfig) {
  // Skip initialization if already initialized or if dsn is missing/empty
  if (sentryInitialized) {
    console.warn('Sentry already initialized, skipping initialization')
    return
  }

  if (!dsn || dsn.trim() === '') {
    console.warn('Sentry DSN is empty or missing, skipping initialization')
    return null
  }

  try {
    const Sentry = await import('@sentry/nextjs')

    Sentry.init({
      dsn,
      environment: config.environment,
      release: config.release,
      tracesSampleRate: config.environment === 'production' ? 0.2 : 1.0,
    })

    // Set user information if available
    if (config.userId || config.userEmail) {
      Sentry.setUser({
        id: config.userId,
        email: config.userEmail,
      })
    }

    sentryInitialized = true
    return Sentry
  } catch (error) {
    console.error('Failed to initialize Sentry:', error)
    // Log detailed error information to help with debugging
    if (error instanceof Error) {
      console.error(`Sentry initialization error: ${error.name}: ${error.message}`)
      if (error.stack) console.error(`Stack trace: ${error.stack}`)
    }
    // Log environment information to help diagnose deployment issues
    console.error(`Environment: ${process.env.NODE_ENV}, Release: ${config.release}`)
    return null
  }
}

/**
 * Initialize LogRocket for session replay
 * @returns The LogRocket instance if initialization was successful, null otherwise
 */
async function initLogRocket(appId: string, config: LoggerConfig) {
  // Skip initialization if already initialized or if appId is missing/empty
  if (logRocketInitialized) {
    console.warn('LogRocket already initialized, skipping initialization')
    return
  }

  if (!appId || appId.trim() === '') {
    console.warn('LogRocket App ID is empty or missing, skipping initialization')
    return null
  }

  try {
    const LogRocket = (await import('logrocket')).default

    LogRocket.init(appId, {
      release: config.release,
      console: {
        isEnabled: {
          error: true,
          warn: true,
        },
      },
      network: {
        isEnabled: true,
        requestSanitizer: request => {
          // Don't log request bodies for sensitive endpoints
          if (request.url.includes('/api/auth') || request.url.includes('/api/user')) {
            request.body = null
          }
          return request
        },
        responseSanitizer: response => {
          // Don't log response bodies for sensitive endpoints
          if (response.url.includes('/api/auth') || response.url.includes('/api/user')) {
            response.body = null
          }
          return response
        },
      },
    })

    // Set user information if available
    if (config.userId || config.userEmail) {
      LogRocket.identify(config.userId || 'anonymous', {
        email: config.userEmail,
      })
    }

    // Connect LogRocket with Sentry if both are enabled
    if (sentryInitialized) {
      const Sentry = await import('@sentry/nextjs')
      LogRocket.getSessionURL(sessionURL => {
        Sentry.configureScope(scope => {
          scope.setExtra('logRocketSessionURL', sessionURL)

          // Add request ID to Sentry scope if available
          if (loggerConfig.requestId) {
            scope.setTag('requestId', loggerConfig.requestId)
          }
        })
      })
    }

    // Add request ID to LogRocket metadata if available
    if (loggerConfig.requestId) {
      LogRocket.setMeta('requestId', loggerConfig.requestId)
    }

    logRocketInitialized = true
    return LogRocket
  } catch (error) {
    console.error('Failed to initialize LogRocket:', error)
    // Log detailed error information to help with debugging
    if (error instanceof Error) {
      console.error(`LogRocket initialization error: ${error.name}: ${error.message}`)
      if (error.stack) console.error(`Stack trace: ${error.stack}`)
    }
    // Log environment information to help diagnose deployment issues
    console.error(
      `Environment: ${process.env.NODE_ENV}, Release: ${config.release}, AppId: ${appId}`
    )
    return null
  }
}

/**
 * Set user information for logging
 */
export async function setLogUser(userId?: string, userEmail?: string) {
  loggerConfig.userId = userId
  loggerConfig.userEmail = userEmail

  // Update user information in external services
  if (sentryInitialized) {
    const Sentry = await import('@sentry/nextjs')
    Sentry.setUser({
      id: userId,
      email: userEmail,
    })
  }

  if (logRocketInitialized && typeof window !== 'undefined') {
    const LogRocket = (await import('logrocket')).default
    LogRocket.identify(userId || 'anonymous', {
      email: userEmail,
    })
  }
}

/**
 * Set request ID for logging
 * This is useful for tracking logs across a single request lifecycle
 * and correlating logs between different services
 */
export async function setRequestId(requestId: string) {
  loggerConfig.requestId = requestId

  // Update request ID in external services
  if (sentryInitialized && typeof window !== 'undefined') {
    try {
      const Sentry = await import('@sentry/nextjs')
      Sentry.configureScope(scope => {
        scope.setTag('requestId', requestId)
      })
    } catch (err) {
      console.error('Failed to set request ID in Sentry:', err)
    }
  }

  if (logRocketInitialized && typeof window !== 'undefined') {
    try {
      const LogRocket = (await import('logrocket')).default
      LogRocket.setMeta('requestId', requestId)
    } catch (err) {
      console.error('Failed to set request ID in LogRocket:', err)
    }
  }
}

/**
 * Log a message with the specified level
 */
async function log(
  level: LogLevel,
  message: string,
  context?: Record<string, unknown>,
  tags?: string[],
  error?: Error
) {
  // Skip if below minimum log level
  if (loggerConfig.minLevel && shouldSkipLog(level, loggerConfig.minLevel)) {
    return
  }

  // Enhance context with requestId and userId if available in config
  const enhancedContext = sanitizeForLogging({
    ...(context || {}),
    ...(loggerConfig.requestId ? { requestId: loggerConfig.requestId } : {}),
    ...(loggerConfig.userId ? { userId: loggerConfig.userId } : {}),
  })

  // Log to console if enabled
  if (loggerConfig.enableConsole !== false) {
    logToConsole(level, message, enhancedContext, tags, error)
  }

  // Log to external services
  await logToExternalServices(level, message, enhancedContext, tags, error)
}

/**
 * Priority map for log levels to make comparisons more maintainable
 */
const LOG_LEVEL_PRIORITY: Record<LogLevel, number> = {
  [LogLevel.DEBUG]: 0,
  [LogLevel.INFO]: 1,
  [LogLevel.WARN]: 2,
  [LogLevel.ERROR]: 3,
  [LogLevel.FATAL]: 4,
}

/**
 * Determine if a log should be skipped based on minimum level
 */
function shouldSkipLog(level: LogLevel, minLevel: LogLevel): boolean {
  return LOG_LEVEL_PRIORITY[level] < LOG_LEVEL_PRIORITY[minLevel]
}

/**
 * Log to the console with appropriate formatting
 */
function logToConsole(
  level: LogLevel,
  message: string,
  context?: Record<string, unknown>,
  tags?: string[],
  error?: Error
) {
  const timestamp = new Date().toISOString()
  const formattedMessage = `[${timestamp}] [${level.toUpperCase()}] ${message}`
  const logData = { ...(context || {}), tags }

  // Use the appropriate console method based on level
  switch (level) {
    case LogLevel.DEBUG:
      console.debug(formattedMessage, logData)
      break
    case LogLevel.INFO:
      console.info(formattedMessage, logData)
      break
    case LogLevel.WARN:
      console.warn(formattedMessage, logData)
      break
    case LogLevel.ERROR:
    case LogLevel.FATAL:
      console.error(formattedMessage, error || '', logData)
      break
  }
}

/**
 * Log to external services like Sentry and LogRocket
 */
async function logToExternalServices(
  level: LogLevel,
  message: string,
  context?: Record<string, unknown>,
  tags?: string[],
  error?: Error
): Promise<void> {
  // Send to Sentry if configured and level is ERROR or FATAL
  if (sentryInitialized && (level === LogLevel.ERROR || level === LogLevel.FATAL)) {
    try {
      const Sentry = await import('@sentry/nextjs')

      // Set extra context
      Sentry.configureScope(scope => {
        if (context) {
          Object.entries(context).forEach(([key, value]) => {
            scope.setExtra(key, value)
          })
        }

        if (tags) {
          tags.forEach(tag => {
            scope.setTag(tag, 'true')
          })
        }
      })

      // Capture the error or message
      if (error) {
        Sentry.captureException(error)
      } else {
        Sentry.captureMessage(message, level)
      }
    } catch (err) {
      console.error('Failed to log to Sentry:', err)
      if (err instanceof Error) {
        console.error(`Sentry logging error: ${err.name}: ${err.message}`)
        // Don't log stack trace here to avoid recursive error logging
      }
    }
  }

  // Send to LogRocket if configured
  if (logRocketInitialized) {
    try {
      const LogRocket = (await import('logrocket')).default

      // Log the message
      if (level === LogLevel.ERROR || level === LogLevel.FATAL) {
        LogRocket.captureException(error || new Error(message), {
          tags: tags?.reduce((acc, tag) => ({ ...acc, [tag]: true }), {}),
          extra: context,
        })
      } else if (level === LogLevel.WARN) {
        LogRocket.warn(message, context)
      } else {
        LogRocket.log(message, context)
      }
    } catch (err) {
      console.error('Failed to log to LogRocket:', err)
      if (err instanceof Error) {
        console.error(`LogRocket logging error: ${err.name}: ${err.message}`)
        // Don't log stack trace here to avoid recursive error logging
      }
    }
  }
}

/**
 * Log a debug message
 * @returns A Promise that resolves when logging is complete
 */
export function debug(message: string, context?: Record<string, unknown>, tags?: string[]): Promise<void> {
  return log(LogLevel.DEBUG, message, context, tags)
}

/**
 * Log an info message
 * @returns A Promise that resolves when logging is complete
 */
export function info(message: string, context?: Record<string, unknown>, tags?: string[]): Promise<void> {
  return log(LogLevel.INFO, message, context, tags)
}

/**
 * Log a warning message
 * @returns A Promise that resolves when logging is complete
 */
export function warn(message: string, context?: Record<string, unknown>, tags?: string[]): Promise<void> {
  return log(LogLevel.WARN, message, context, tags)
}

/**
 * Log an error message
 * @returns A Promise that resolves when logging is complete
 */
export function error(
  message: string,
  errorObj?: Error,
  context?: Record<string, unknown>,
  tags?: string[]
): Promise<void> {
  return log(LogLevel.ERROR, message, context, tags, errorObj)
}

/**
 * Log a fatal error message
 * @returns A Promise that resolves when logging is complete
 */
export function fatal(
  message: string,
  errorObj?: Error,
  context?: Record<string, unknown>,
  tags?: string[]
): Promise<void> {
  return log(LogLevel.FATAL, message, context, tags, errorObj)
}

// Export a default logger object
const logger = {
  debug,
  info,
  warn,
  error,
  fatal,
  configureLogger,
  shutdownLogger,
  setLogUser,
  setRequestId,
}

export default logger

// lib/monitoring/performance.ts
import logger from '@/lib/logging/logger'

/**
 * Performance monitoring utility
 *
 * This module provides utilities for monitoring performance metrics
 * and reporting them to the logging system.
 */

// Performance metric types
export enum MetricType {
  NAVIGATION = 'navigation',
  RESOURCE = 'resource',
  PAINT = 'paint',
  LAYOUT = 'layout',
  FIRST_INPUT = 'first-input',
  CUSTOM = 'custom',
}

// Performance metric interface
export interface PerformanceMetric {
  name: string
  type: MetricType
  value: number
  unit: 'ms' | 'bytes' | 'count'
  timestamp: number
}

/**
 * Initialize performance monitoring
 */
export function initPerformanceMonitoring() {
  if (typeof window === 'undefined' || !window.performance) {
    return
  }

  // Report navigation timing metrics
  reportNavigationTiming()

  // Set up performance observers
  setupPerformanceObservers()

  // Report metrics on page unload
  window.addEventListener('unload', () => {
    reportResourceTiming()
  })
}

/**
 * Report navigation timing metrics
 */
function reportNavigationTiming() {
  if (typeof window === 'undefined' || !window.performance) {
    return
  }

  // Wait for the page to be fully loaded
  window.addEventListener('load', () => {
    setTimeout(() => {
      const navigation = performance.getEntriesByType(
        'navigation'
      )[0] as PerformanceNavigationTiming

      if (!navigation) {
        return
      }

      const metrics: PerformanceMetric[] = [
        {
          name: 'time_to_first_byte',
          type: MetricType.NAVIGATION,
          value: navigation.responseStart - navigation.requestStart,
          unit: 'ms',
          timestamp: Date.now(),
        },
        {
          name: 'dom_load',
          type: MetricType.NAVIGATION,
          value: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
          unit: 'ms',
          timestamp: Date.now(),
        },
        {
          name: 'page_load',
          type: MetricType.NAVIGATION,
          value: navigation.loadEventEnd - navigation.loadEventStart,
          unit: 'ms',
          timestamp: Date.now(),
        },
        {
          name: 'total_page_load',
          type: MetricType.NAVIGATION,
          value: navigation.loadEventEnd - navigation.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        },
      ]

      // Log the metrics
      logger.info(
        'Navigation timing metrics',
        {
          metrics,
          url: window.location.href,
          pathname: window.location.pathname,
        },
        ['performance', 'navigation']
      )
    }, 0)
  })
}

/**
 * Report resource timing metrics
 */
function reportResourceTiming() {
  if (typeof window === 'undefined' || !window.performance) {
    return
  }

  const resources = performance.getEntriesByType('resource')

  if (!resources || resources.length === 0) {
    return
  }

  // Group resources by type
  const resourcesByType: Record<string, PerformanceResourceTiming[]> = {}

  resources.forEach(resource => {
    const resourceTiming = resource as PerformanceResourceTiming
    const url = new URL(resourceTiming.name)
    const fileExtension = url.pathname.split('.').pop() || 'unknown'

    if (!resourcesByType[fileExtension]) {
      resourcesByType[fileExtension] = []
    }

    resourcesByType[fileExtension].push(resourceTiming)
  })

  // Calculate metrics for each resource type
  const metrics: Record<
    string,
    {
      count: number
      totalSize: number
      totalDuration: number
      averageDuration: number
    }
  > = {}

  Object.entries(resourcesByType).forEach(([type, resources]) => {
    const totalSize = resources.reduce((sum, resource) => {
      return sum + (resource.transferSize || 0)
    }, 0)

    const totalDuration = resources.reduce((sum, resource) => {
      return sum + (resource.responseEnd - resource.startTime)
    }, 0)

    metrics[type] = {
      count: resources.length,
      totalSize,
      totalDuration,
      averageDuration: totalDuration / resources.length,
    }
  })

  // Log the metrics
  logger.info(
    'Resource timing metrics',
    {
      metrics,
      url: window.location.href,
      pathname: window.location.pathname,
    },
    ['performance', 'resource']
  )
}

/**
 * Set up performance observers
 */
function setupPerformanceObservers() {
  if (typeof window === 'undefined' || !window.PerformanceObserver) {
    return
  }

  // Observe paint metrics (FP, FCP)
  try {
    const paintObserver = new PerformanceObserver(entries => {
      entries.getEntries().forEach(entry => {
        const metric: PerformanceMetric = {
          name: entry.name,
          type: MetricType.PAINT,
          value: entry.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        }

        logger.info(
          `Paint metric: ${entry.name}`,
          {
            metric,
            url: window.location.href,
            pathname: window.location.pathname,
          },
          ['performance', 'paint']
        )
      })
    })

    paintObserver.observe({ entryTypes: ['paint'] })
  } catch (error) {
    logger.warn('Failed to observe paint metrics', { error })
  }

  // Observe layout shift metrics (CLS)
  try {
    let cumulativeLayoutShift = 0

    const layoutShiftObserver = new PerformanceObserver(entries => {
      entries.getEntries().forEach((entry: any) => {
        // Only count layout shifts without recent user input
        if (!entry.hadRecentInput) {
          cumulativeLayoutShift += entry.value
        }
      })

      const metric: PerformanceMetric = {
        name: 'cumulative_layout_shift',
        type: MetricType.LAYOUT,
        value: cumulativeLayoutShift,
        unit: 'count',
        timestamp: Date.now(),
      }

      logger.info(
        'Layout shift metric',
        {
          metric,
          url: window.location.href,
          pathname: window.location.pathname,
        },
        ['performance', 'layout']
      )
    })

    layoutShiftObserver.observe({ entryTypes: ['layout-shift'] })
  } catch (error) {
    logger.warn('Failed to observe layout shift metrics', { error })
  }

  // Observe first input delay (FID)
  try {
    const firstInputObserver = new PerformanceObserver(entries => {
      entries.getEntries().forEach(entry => {
        const metric: PerformanceMetric = {
          name: 'first_input_delay',
          type: MetricType.FIRST_INPUT,
          value: entry.processingStart - entry.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        }

        logger.info(
          'First input delay metric',
          {
            metric,
            url: window.location.href,
            pathname: window.location.pathname,
          },
          ['performance', 'first-input']
        )
      })
    })

    firstInputObserver.observe({ entryTypes: ['first-input'] })
  } catch (error) {
    logger.warn('Failed to observe first input delay metrics', { error })
  }
}

/**
 * Measure the execution time of a function
 * @param fn Function to measure
 * @param name Name of the metric
 * @returns Result of the function
 */
export function measureExecutionTime<T>(fn: () => T, name: string): T {
  const start = performance.now()
  const result = fn()
  const duration = performance.now() - start

  const metric: PerformanceMetric = {
    name,
    type: MetricType.CUSTOM,
    value: duration,
    unit: 'ms',
    timestamp: Date.now(),
  }

  logger.debug(
    `Execution time for ${name}`,
    {
      metric,
      url: typeof window !== 'undefined' ? window.location.href : undefined,
      pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
    },
    ['performance', 'execution-time']
  )

  return result
}

/**
 * Measure the execution time of an async function
 * @param fn Async function to measure
 * @param name Name of the metric
 * @returns Promise that resolves to the result of the function
 */
export async function measureAsyncExecutionTime<T>(fn: () => Promise<T>, name: string): Promise<T> {
  const start = performance.now()

  try {
    const result = await fn()
    const duration = performance.now() - start

    const metric: PerformanceMetric = {
      name,
      type: MetricType.CUSTOM,
      value: duration,
      unit: 'ms',
      timestamp: Date.now(),
    }

    logger.debug(
      `Async execution time for ${name}`,
      {
        metric,
        url: typeof window !== 'undefined' ? window.location.href : undefined,
        pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
      },
      ['performance', 'execution-time']
    )

    return result
  } catch (error) {
    const duration = performance.now() - start

    const metric: PerformanceMetric = {
      name: `${name}_error`,
      type: MetricType.CUSTOM,
      value: duration,
      unit: 'ms',
      timestamp: Date.now(),
    }

    logger.warn(
      `Error in async execution for ${name}`,
      {
        metric,
        error,
        url: typeof window !== 'undefined' ? window.location.href : undefined,
        pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
      },
      ['performance', 'execution-time', 'error']
    )

    throw error
  }
}

/**
 * Create a performance monitoring hook for React components
 * @param componentName Name of the component
 * @returns Object with performance monitoring methods
 */
export function usePerformanceMonitoring(componentName: string) {
  const renderStart = performance.now()

  return {
    /**
     * Report component render time
     */
    reportRenderTime: () => {
      const renderTime = performance.now() - renderStart

      const metric: PerformanceMetric = {
        name: `${componentName}_render`,
        type: MetricType.CUSTOM,
        value: renderTime,
        unit: 'ms',
        timestamp: Date.now(),
      }

      logger.debug(
        `Render time for ${componentName}`,
        {
          metric,
          url: typeof window !== 'undefined' ? window.location.href : undefined,
          pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
        },
        ['performance', 'render-time']
      )
    },

    /**
     * Measure the execution time of a function
     * @param fn Function to measure
     * @param name Name of the metric
     * @returns Result of the function
     */
    measureExecutionTime: <T>(fn: () => T, name: string): T => {
      return measureExecutionTime(fn, `${componentName}_${name}`)
    },

    /**
     * Measure the execution time of an async function
     * @param fn Async function to measure
     * @param name Name of the metric
     * @returns Promise that resolves to the result of the function
     */
    measureAsyncExecutionTime: <T>(fn: () => Promise<T>, name: string): Promise<T> => {
      return measureAsyncExecutionTime(fn, `${componentName}_${name}`)
    },
  }
}

// ApiCallExample.tsx
import React, { useState, useEffect } from 'react'

import {
  fetchWithRetry,
  NetworkError,
  TimeoutError,
  ApiError,
  createCircuitBreaker,
  withCircuitBreaker,
} from '../lib/api/fetchUtils'

import ErrorBoundary from './ErrorBoundary'

interface Property {
  id: string
  address: string
  price: number
  bedrooms: number
  type: string
}

// Create a circuit breaker for the properties API
const propertiesCircuitBreaker = createCircuitBreaker({
  name: 'properties-api',
  failureThreshold: 3,
  resetTimeout: 15000, // 15 seconds
})

const PropertyList = () => {
  const [properties, setProperties] = useState<Property[]>([])
  const [loading, setLoading] = useState<boolean>(true)
  const [error, setError] = useState<string | null>(null)
  const [retryCount, setRetryCount] = useState<number>(0)
  const [circuitStatus, setCircuitStatus] = useState<'CLOSED' | 'OPEN' | 'HALF_OPEN'>(
    propertiesCircuitBreaker.getStatus()
  )

  useEffect(() => {
    // Create an AbortController for cancelling the fetch request
    const controller = new AbortController()
    const signal = controller.signal

    const fetchProperties = async () => {
      try {
        setLoading(true)

        // Check circuit breaker status and update UI
        const currentStatus = propertiesCircuitBreaker.getStatus()
        setCircuitStatus(currentStatus)

        if (currentStatus === 'OPEN') {
          setError('Service is temporarily unavailable. Please try again later.')
          setLoading(false)
          return
        }

        // Using circuit breaker pattern with our fetch utility
        const data = await withCircuitBreaker(
          // Primary function
          () =>
            fetchWithRetry<Property[]>(
              '/api/properties',
              { signal },
              3, // 3 retries
              300, // 300ms initial backoff
              5000 // 5 second timeout
            ),
          // Circuit breaker
          propertiesCircuitBreaker,
          // Fallback function (optional - returns empty array if API is down)
          () => Promise.resolve([])
        )

        setProperties(data)
        setError(null)

        // Update circuit status after successful request
        setCircuitStatus(propertiesCircuitBreaker.getStatus())
      } catch (err) {
        // Type guard for different error types
        if (err instanceof TimeoutError) {
          setError('Request timed out. Please check your connection and try again.')
        } else if (err instanceof NetworkError) {
          setError('Network error occurred. Please check your connection and try again.')
        } else if (err instanceof ApiError) {
          if (err.status === 401 || err.status === 403) {
            setError('You are not authorized to access this resource.')
          } else if (err.status === 404) {
            setError('The requested resource was not found.')
          } else if (err.status >= 500) {
            setError('Server error. Please try again later.')
          } else {
            setError(`API error: ${err.message}`)
          }
        } else if (err instanceof Error) {
          // Don't update state if the request was aborted
          if (err.name === 'AbortError') {
            console.log('Fetch aborted')
            return
          }

          setError(`Error: ${err.message}`)
          console.error('Error fetching properties:', err)
        } else {
          setError('An unknown error occurred. Please try again later.')
          console.error('Unknown error:', err)
        }

        // Update circuit status after failed request
        setCircuitStatus(propertiesCircuitBreaker.getStatus())
      } finally {
        setLoading(false)
      }
    }

    fetchProperties()

    // Cleanup function to abort fetch when component unmounts
    return () => {
      controller.abort()
      console.log('Property fetch aborted')
    }
  }, [retryCount]) // Dependency on retryCount to trigger refetch

  // Function to retry loading data with proper error handling
  const handleRetry = () => {
    setError(null)

    // If circuit is OPEN, try to reset it after the timeout period
    if (circuitStatus === 'OPEN') {
      const now = Date.now()
      const lastFailure = propertiesCircuitBreaker['lastFailure'] as unknown as number
      const resetTimeout = 15000 // Same as in circuit breaker creation

      if (lastFailure && now - lastFailure > resetTimeout) {
        // Manually reset the circuit breaker for demonstration purposes
        propertiesCircuitBreaker.reset()
        setCircuitStatus(propertiesCircuitBreaker.getStatus())
      }
    }

    setRetryCount(prev => prev + 1) // Increment retry count to trigger useEffect
  }

  // Helper function to get circuit status badge
  const getCircuitStatusBadge = () => {
    let className = 'circuit-status'
    let label = ''

    switch (circuitStatus) {
      case 'CLOSED':
        className += ' closed'
        label = 'API: Healthy'
        break
      case 'HALF_OPEN':
        className += ' half-open'
        label = 'API: Testing'
        break
      case 'OPEN':
        className += ' open'
        label = 'API: Down'
        break
    }

    return <div className={className}>{label}</div>
  }

  if (loading) {
    return (
      <div className="loading-container">
        {getCircuitStatusBadge()}
        <div className="loading-spinner"></div>
        <p>Loading properties...</p>
      </div>
    )
  }

  if (error) {
    return (
      <div className="error-container">
        {getCircuitStatusBadge()}
        <h3>Error</h3>
        <p>{error}</p>
        <button onClick={handleRetry} className="retry-button" disabled={circuitStatus === 'OPEN'}>
          {circuitStatus === 'OPEN' ? 'Service Unavailable' : 'Try Again'}
        </button>
        {circuitStatus === 'OPEN' && (
          <p className="circuit-message">
            The service is temporarily unavailable. Please try again later.
          </p>
        )}
      </div>
    )
  }

  return (
    <div className="property-list">
      <div className="property-header">
        <h2>Available Properties</h2>
        {getCircuitStatusBadge()}
      </div>

      {properties.length === 0 ? (
        <div className="empty-state">
          <p>No properties found.</p>
          <button onClick={handleRetry} className="refresh-button">
            Refresh
          </button>
        </div>
      ) : (
        <ul>
          {properties.map(property => (
            <li key={property.id} className="property-card">
              <h3>{property.address}</h3>
              <p>Price: ${property.price.toLocaleString()}</p>
              <p>Bedrooms: {property.bedrooms}</p>
              <p>Type: {property.type}</p>
            </li>
