// components/error-handling/EnhancedErrorBoundary.tsx
import React, { Component, ErrorInfo, ReactNode, useState, useEffect } from 'react'

import logger from '@/lib/logging/logger'

interface ErrorBoundaryProps {
  children: ReactNode
  fallback?: ReactNode | ((error: Error, resetError: () => void) => ReactNode)
  onError?: (error: Error, errorInfo: ErrorInfo) => void
  errorTypes?: Array<string>
}

interface ErrorBoundaryState {
  hasError: boolean
  error: Error | null
}

/**
 * Enhanced error boundary component that catches render errors
 */
class ErrorBoundaryBase extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props)
    this.state = {
      hasError: false,
      error: null,
    }
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return {
      hasError: true,
      error,
    }
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    // Log the error with structured logging
    logger.error(
      'Error caught by boundary',
      error,
      {
        componentStack: errorInfo.componentStack,
        errorName: error.name,
        errorMessage: error.message,
      },
      ['error-boundary', 'render-error']
    )

    // Call the optional onError callback
    if (this.props.onError) {
      this.props.onError(error, errorInfo)
    }
  }

  resetError = () => {
    this.setState({
      hasError: false,
      error: null,
    })
  }

  render(): ReactNode {
    const { hasError, error } = this.state
    const { children, fallback, errorTypes } = this.props

    if (hasError && error) {
      // Check if we should handle this type of error
      if (errorTypes && !errorTypes.includes(error.name)) {
        // Re-throw errors we don't want to handle
        throw error
      }

      // Render custom fallback if provided
      if (fallback) {
        if (typeof fallback === 'function') {
          return fallback(error, this.resetError)
        }
        return fallback
      }

      // Default error UI
      return (
        <div className="p-6 bg-red-50 border border-red-200 rounded-lg">
          <h2 className="text-xl font-semibold text-red-700 mb-2">Something went wrong</h2>
          <p className="text-red-600 mb-4">
            Please try again later or contact support if the problem persists.
          </p>
          <div className="mb-4 p-3 bg-white rounded border border-red-100 text-sm text-gray-700 overflow-auto max-h-32">
            <p className="font-semibold">
              {error.name}: {error.message}
            </p>
          </div>
          <button
            onClick={this.resetError}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
          >
            Try again
          </button>
        </div>
      )
    }

    return children
  }
}

/**
 * Hook to catch and handle async errors
 */
function useAsyncErrorHandler(onError?: (error: Error) => void, errorTypes?: Array<string>) {
  const [asyncError, setAsyncError] = useState<Error | null>(null)

  // Set up global error handler for unhandled promise rejections
  useEffect(() => {
    const handleUnhandledRejection = (event: PromiseRejectionEvent) => {
      const error = event.reason

      // Check if we should handle this type of error
      if (errorTypes && error.name && !errorTypes.includes(error.name)) {
        return
      }

      // Log the error
      logger.error(
        'Unhandled promise rejection',
        error,
        {
          errorName: error.name,
          errorMessage: error.message,
        },
        ['error-boundary', 'async-error', 'unhandled-rejection']
      )

      // Set the error state
      setAsyncError(error)

      // Call the optional onError callback
      if (onError) {
        onError(error)
      }

      // Prevent the default handler
      event.preventDefault()
    }

    // Add event listener
    window.addEventListener('unhandledrejection', handleUnhandledRejection)

    // Clean up
    return () => {
      window.removeEventListener('unhandledrejection', handleUnhandledRejection)
    }
  }, [onError, errorTypes])

  // Reset the error state
  const resetAsyncError = () => {
    setAsyncError(null)
  }

  return { asyncError, resetAsyncError }
}

/**
 * Enhanced error boundary that catches both render errors and async errors
 */
const EnhancedErrorBoundary: React.FC<ErrorBoundaryProps> = props => {
  const { children, fallback, onError, errorTypes } = props

  // Use the async error handler hook
  const { asyncError, resetAsyncError } = useAsyncErrorHandler(error => {
    if (onError) {
      onError(error, { componentStack: '' })
    }
  }, errorTypes)

  // If there's an async error, render the fallback
  if (asyncError) {
    if (fallback) {
      if (typeof fallback === 'function') {
        return <>{fallback(asyncError, resetAsyncError)}</>
      }
      return <>{fallback}</>
    }

    // Default error UI for async errors
    return (
      <div className="p-6 bg-red-50 border border-red-200 rounded-lg">
        <h2 className="text-xl font-semibold text-red-700 mb-2">Something went wrong</h2>
        <p className="text-red-600 mb-4">
          An error occurred while processing your request. Please try again.
        </p>
        <div className="mb-4 p-3 bg-white rounded border border-red-100 text-sm text-gray-700 overflow-auto max-h-32">
          <p className="font-semibold">
            {asyncError.name}: {asyncError.message}
          </p>
        </div>
        <button
          onClick={resetAsyncError}
          className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
        >
          Try again
        </button>
      </div>
    )
  }

  // If no async error, use the base error boundary for render errors
  return <ErrorBoundaryBase {...props} />
}

export default EnhancedErrorBoundary

'use client'

import React, { useState } from 'react'
import { z } from 'zod'

import { ApiError } from '@/lib/api/error-handling'

import { TextField, FormError } from '@/components/forms'
import { useApi } from '@/hooks/useApi'
import { useFormValidation } from '@/hooks/useFormValidation'

// Define the form schema using Zod
const loginFormSchema = z.object({
  email: z.string().min(1, 'Email is required').email('Please enter a valid email address'),
  password: z
    .string()
    .min(1, 'Password is required')
    .min(8, 'Password must be at least 8 characters'),
})

// Infer the form data type from the schema
type LoginFormData = z.infer<typeof loginFormSchema>

// Initial form values
const initialValues: LoginFormData = {
  email: '',
  password: '',
}

/**
 * API form example component
 */
const ApiFormExample: React.FC = () => {
  const [serverError, setServerError] = useState<string | null>(null)

  // Use our API hook
  const api = useApi({
    onError: (error: ApiError) => {
      // Handle API errors
      if (error.isValidationError()) {
        // Get validation errors from the API
        const validationErrors = error.getValidationErrors()

        // Set field errors
        Object.entries(validationErrors).forEach(([field, message]) => {
          setFieldError(field as keyof LoginFormData, message)
        })
      } else {
        // Set a generic server error
        setServerError(error.message)
      }
    },
  })

  // Use our form validation hook
  const {
    values,
    errors,
    touched,
    isSubmitting,
    handleChange,
    handleBlur,
    handleSubmit,
    setFieldError,
  } = useFormValidation({
    initialValues,
    schema: loginFormSchema,
    onSubmit: async data => {
      setServerError(null)

      // Simulate API call
      try {
        // This is a simulated API call that will always fail with validation errors
        // In a real app, you would use api.post('/api/login', data)
        await new Promise(resolve => setTimeout(resolve, 1000))

        // Simulate a validation error from the API
        throw new ApiError('Validation failed', 422, 'VALIDATION_ERROR', {
          email: 'This email is not registered',
          password: 'Incorrect password',
        })
      } catch (error) {
        if (error instanceof ApiError) {
          if (error.isValidationError()) {
            // Get validation errors from the API
            const validationErrors = error.getValidationErrors()

            // Set field errors
            Object.entries(validationErrors).forEach(([field, message]) => {
              setFieldError(field as keyof LoginFormData, message)
            })
          } else {
            // Set a generic server error
            setServerError(error.message)
          }
        } else {
          setServerError('An unexpected error occurred. Please try again.')
        }
      }
    },
  })

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6">Login Example</h2>

      {/* Form-level errors */}
      <FormError error={errors._form || serverError} />

      {/* API loading state */}
      {api.isLoading && (
        <div className="p-3 mb-4 bg-blue-50 border border-blue-200 text-blue-700 rounded-md">
          <div className="flex items-center">
            <svg
              className="animate-spin h-5 w-5 mr-3 text-blue-500"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle
                className="opacity-25"
                cx="12"
                cy="12"
                r="10"
                stroke="currentColor"
                strokeWidth="4"
              ></circle>
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
              ></path>
            </svg>
            <span>Processing request...</span>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit} noValidate className="space-y-4">
        <TextField
          id="email"
          name="email"
          label="Email"
          type="email"
          value={values.email}
          onChange={handleChange}
          onBlur={handleBlur}
          error={errors.email}
          touched={touched.email}
          required
          placeholder="you@example.com"
          autoComplete="email"
        />

        <TextField
          id="password"
          name="password"
          label="Password"
          type="password"
          value={values.password}
          onChange={handleChange}
          onBlur={handleBlur}
          error={errors.password}
          touched={touched.password}
          required
          placeholder=""
          autoComplete="current-password"
        />

        <div className="pt-2">
          <button
            type="submit"
            className="w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            disabled={isSubmitting || api.isLoading}
          >
            {isSubmitting || api.isLoading ? 'Logging in...' : 'Log In'}
          </button>
        </div>
      </form>

      <div className="mt-6 text-sm text-gray-600">
        <p>This example demonstrates handling API validation errors.</p>
        <p className="mt-2">Try submitting the form to see simulated API validation errors.</p>
      </div>
    </div>
  )
}

