interface PropertyFormProps {
  onSubmit?: (data: PropertyFormData) => Promise<void>
  initialData?: Partial<PropertyFormData>
}

/**
 * Property form with validation
 */
const PropertyForm: React.FC<PropertyFormProps> = ({ onSubmit, initialData }) => {
  const [serverError, setServerError] = useState<string | null>(null)
  const [submitSuccess, setSubmitSuccess] = useState(false)

  // Merge initial data with default values
  const mergedInitialValues = {
    ...initialValues,
    ...initialData,
  }

  // Use our form validation hook
  const {
    values,
    errors,
    touched,
    isSubmitting,
    handleChange,
    handleBlur,
    handleSubmit,
    setFieldValue,
    resetForm,
  } = useFormValidation({
    initialValues: mergedInitialValues,
    schema: propertyFormSchema,
    onSubmit: async data => {
      setServerError(null)
      setSubmitSuccess(false)

      try {
        if (onSubmit) {
          await onSubmit(data)
        } else {
          // Simulate API call
          await new Promise(resolve => setTimeout(resolve, 1000))
          console.log('Form submitted:', data)
        }

        setSubmitSuccess(true)
        resetForm()
      } catch (error) {
        console.error('Form submission error:', error)
        setServerError(
          error instanceof Error ? error.message : 'An unexpected error occurred. Please try again.'
        )
      }
    },
  })

  return (
    <form onSubmit={handleSubmit} noValidate className="space-y-6">
      {/* Form-level errors */}
      <FormError error={errors._form || serverError} />

      {/* Success message */}
      {submitSuccess && (
        <div
          className="p-3 mb-4 bg-green-50 border border-green-200 text-green-700 rounded-md"
          role="alert"
        >
          <div className="flex">
            <div className="flex-shrink-0">
              <svg
                className="h-5 w-5 text-green-400"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
                fill="currentColor"
                aria-hidden="true"
              >
                <path
                  fillRule="evenodd"
                  d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                  clipRule="evenodd"
                />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm font-medium">Property submitted successfully!</p>
            </div>
          </div>
        </div>
      )}

      {/* Property address section */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
        <h2 className="text-lg font-semibold mb-4">Property Location</h2>

        <div className="grid grid-cols-1 gap-4">
          <TextField
            id="address"
            name="address"
            label="Street Address"
            value={values.address}
            onChange={handleChange}
            onBlur={handleBlur}
            error={errors.address}
            touched={touched.address}
            required
            placeholder="123 Main St"
          />

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <TextField
              id="city"
              name="city"
              label="City"
              value={values.city}
              onChange={handleChange}
              onBlur={handleBlur}
              error={errors.city}
              touched={touched.city}
              required
              placeholder="New York"
            />

            <SelectField
              id="state"
              name="state"
              label="State"
              value={values.state}
              onChange={handleChange}
              onBlur={handleBlur}
              error={errors.state}
              touched={touched.state}
              required
              options={stateOptions}
              placeholder="Select a state"
            />

            <TextField
              id="zipCode"
              name="zipCode"
              label="ZIP Code"
              value={values.zipCode}
              onChange={handleChange}
              onBlur={handleBlur}
              error={errors.zipCode}
              touched={touched.zipCode}
              required
              placeholder="12345"
            />
          </div>

          {/* UK-specific address fields */}
          <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-md">
            <h3 className="text-sm font-medium text-blue-800 mb-2">UK Property Details</h3>
            <div className="grid grid-cols-1 gap-4">
              <PostcodeLookup
                postcode={values.postcode || ''}
                onPostcodeChange={handleChange}
                onCoordinatesFound={(lat, lng) => {
                  setFieldValue('lat', lat)
                  setFieldValue('lng', lng)
                  setServerError(null)
                }}
                error={errors.postcode}
                touched={touched.postcode}
              />

              {/* Display coordinates if available */}
              {values.lat !== undefined && values.lng !== undefined && (
                <div className="text-sm text-gray-600 mt-1">
                  <p>
                    Coordinates: {values.lat.toFixed(6)}, {values.lng.toFixed(6)}
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Property details section */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
        <h2 className="text-lg font-semibold mb-4">Property Details</h2>

        <div className="grid grid-cols-1 gap-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <TextField
              id="price"
              name="price"
              label="Price"
              type="number"
              value={values.price === undefined ? '' : values.price}
              onChange={handleChange}
              onBlur={handleBlur}
              error={errors.price}
              touched={touched.price}
              placeholder="300000"
              min={0}
              step={1000}
            />

            <TextField
              id="bedrooms"
              name="bedrooms"
              label="Bedrooms"
              type="number"
              value={values.bedrooms === undefined ? '' : values.bedrooms}
              onChange={handleChange}
              onBlur={handleBlur}
              error={errors.bedrooms}
              touched={touched.bedrooms}
              placeholder="3"
              min={0}
              step={1}
            />

            <TextField
              id="bathrooms"
              name="bathrooms"
              label="Bathrooms"
              type="number"
              value={values.bathrooms === undefined ? '' : values.bathrooms}
              onChange={handleChange}
              onBlur={handleBlur}
              error={errors.bathrooms}
              touched={touched.bathrooms}
              placeholder="2"
              min={0}
              step={0.5}
            />
          </div>

          <SelectField
            id="propertyType"
            name="propertyType"
            label="Property Type"
            value={values.propertyType}
            onChange={handleChange}
            onBlur={handleBlur}
            error={errors.propertyType}
            touched={touched.propertyType}
            required
            options={propertyTypeOptions}
          />

          <TextAreaField
            id="description"
            name="description"
            label="Description"
            value={values.description || ''}
            onChange={handleChange}
            onBlur={handleBlur}
            error={errors.description}
            touched={touched.description}
            rows={4}
            placeholder="Describe the property..."
            helpText="Provide a detailed description of the property to attract potential buyers."
          />

          {/* UK-specific property details */}
          <div className="p-3 bg-blue-50 border border-blue-200 rounded-md">
            <h3 className="text-sm font-medium text-blue-800 mb-2">UK Property Information</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <SelectField
                id="councilTaxBand"
                name="councilTaxBand"
                label="Council Tax Band"
                value={values.councilTaxBand}
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.councilTaxBand}
                touched={touched.councilTaxBand}
                options={councilTaxBandOptions}
                helpText="UK property tax band"
              />

              <SelectField
                id="tenure"
                name="tenure"
                label="Tenure"
                value={values.tenure}
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.tenure}
                touched={touched.tenure}
                options={tenureOptions}
                helpText="Ownership type"
              />

              <SelectField
                id="epcRating"
                name="epcRating"
                label="EPC Rating"
                value={values.epcRating}
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.epcRating}
                touched={touched.epcRating}
                options={epcRatingOptions}
                helpText="Energy Performance Certificate rating"
              />
            </div>
          </div>

          <CheckboxField
            id="featured"
            name="featured"
            label="Feature this property in search results"
            checked={values.featured}
            onChange={handleChange}
            onBlur={handleBlur}
            error={errors.featured}
            touched={touched.featured}
            helpText="Featured properties appear at the top of search results."
          />
        </div>
      </div>

      {/* Terms and submission */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
        <CheckboxField
          id="agreeToTerms"
          name="agreeToTerms"
          label="I agree to the terms and conditions"
          checked={values.agreeToTerms}
          onChange={handleChange}
          onBlur={handleBlur}
          error={errors.agreeToTerms}
          touched={touched.agreeToTerms}
          required
        />

        <div className="mt-6 flex items-center justify-end gap-4">
          <button
            type="button"
            onClick={() => resetForm()}
            className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            disabled={isSubmitting}
          >
            Reset
          </button>

          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Submitting...' : 'Submit Property'}
          </button>
        </div>
      </div>
    </form>
  )
}

export default PropertyForm

// components/forms/FormField.tsx
import React from 'react'

interface FormFieldProps {
  id: string
  name: string
  label: string
  error?: string
  touched?: boolean
  required?: boolean
  children: React.ReactNode
  helpText?: string
  className?: string
}

/**
 * Form field wrapper component with label and error handling
 */
const FormField: React.FC<FormFieldProps> = ({
  id,
  name,
  label,
  error,
  touched,
  required,
  children,
  helpText,
  className = '',
}) => {
  const showError = error && touched

  return (
    <div className={`mb-4 ${className}`}>
      <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-1">
        {label}
        {required && <span className="text-red-500 ml-1">*</span>}
      </label>

      {children}

      {helpText && !showError && <p className="mt-1 text-sm text-gray-500">{helpText}</p>}

      {showError && (
        <p className="mt-1 text-sm text-red-600" id={`${id}-error`}>
          {error}
        </p>
      )}
    </div>
  )
}

export default FormField

import axios from 'axios'
import React, { useState } from 'react'

// Define types
interface Property {
  id: string
  title: string
  description: string
  price: number
  location: string
  bedrooms: number
  bathrooms: number
  squareFeet: number
  propertyType: string
  status: string
  similarity?: number
}

interface SearchFilters {
  minPrice?: number
  maxPrice?: number
  location?: string
  bedrooms?: number
  bathrooms?: number
  propertyType?: string
}

interface SearchOptions {
  limit?: number
  offset?: number
  similarityThreshold?: number
}

// Semantic Search Component
const SemanticSearch: React.FC = () => {
  // State
  const [query, setQuery] = useState('')
  const [filters, setFilters] = useState<SearchFilters>({})
  const [options, setOptions] = useState<SearchOptions>({ limit: 10 })
  const [results, setResults] = useState<Property[]>([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)

  // Handle search
  const handleSearch = async () => {
    if (!query.trim()) {
      setError('Please enter a search query')
      return
    }

    setLoading(true)
    setError(null)

    try {
      const response = await axios.post('/api/search/semantic', {
        query,
        filters,
        options,
      })

      setResults(response.data.data.results)
    } catch (err: any) {
      if (err.response?.status === 429) {
        setError('Too many requests. Please try again later.')
      } else {
        setError(err.message || 'An error occurred')
      }
    } finally {
      setLoading(false)
    }
  }

  // Handle filter changes
  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target as HTMLInputElement

    // Convert numeric values
    const parsedValue = type === 'number' && value ? parseFloat(value) : value

    setFilters(prev => ({
      ...prev,
      [name]: parsedValue === '' ? undefined : parsedValue,
    }))
  }

  // Handle retry after rate limit
  const handleRetry = () => {
    handleSearch()
  }

  return (
    <div className="semantic-search">
      <h2>Semantic Property Search</h2>

      {/* Search query input */}
      <div className="search-input">
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Describe your ideal property..."
          className="query-input"
          data-testid="search-input"
        />
        <button
          onClick={handleSearch}
          disabled={loading}
          className="search-button"
          data-testid="search-button"
        >
          {loading ? 'Searching...' : 'Search'}
        </button>
      </div>

      {/* Filters */}
      <div className="filters">
        <h3>Filters</h3>
        <div className="filter-grid">
          <div className="filter-item">
            <label htmlFor="minPrice">Min Price</label>
            <input
              type="number"
              id="minPrice"
              name="minPrice"
              value={filters.minPrice || ''}
              onChange={handleFilterChange}
              placeholder="Min Price"
              data-testid="min-price"
            />
          </div>

          <div className="filter-item">
            <label htmlFor="maxPrice">Max Price</label>
            <input
              type="number"
              id="maxPrice"
              name="maxPrice"
              value={filters.maxPrice || ''}
              onChange={handleFilterChange}
              placeholder="Max Price"
              data-testid="max-price"
            />
          </div>

          <div className="filter-item">
            <label htmlFor="location">Location</label>
            <input
              type="text"
              id="location"
              name="location"
              value={filters.location || ''}
              onChange={handleFilterChange}
              placeholder="Location"
              data-testid="location"
            />
          </div>

          <div className="filter-item">
            <label htmlFor="bedrooms">Bedrooms</label>
            <select
              id="bedrooms"
              name="bedrooms"
              value={filters.bedrooms || ''}
              onChange={handleFilterChange}
              data-testid="bedrooms"
            >
              <option value="">Any</option>
              <option value="1">1+</option>
              <option value="2">2+</option>
              <option value="3">3+</option>
              <option value="4">4+</option>
            </select>
          </div>

          <div className="filter-item">
            <label htmlFor="bathrooms">Bathrooms</label>
            <select
              id="bathrooms"
              name="bathrooms"
              value={filters.bathrooms || ''}
              onChange={handleFilterChange}
              data-testid="bathrooms"
            >
              <option value="">Any</option>
              <option value="1">1+</option>
              <option value="2">2+</option>
              <option value="3">3+</option>
            </select>
          </div>

          <div className="filter-item">
            <label htmlFor="propertyType">Property Type</label>
            <select
              id="propertyType"
              name="propertyType"
              value={filters.propertyType || ''}
              onChange={handleFilterChange}
              data-testid="property-type"
            >
              <option value="">Any Type</option>
              <option value="apartment">Apartment</option>
              <option value="house">House</option>
              <option value="condo">Condo</option>
              <option value="townhouse">Townhouse</option>
              <option value="land">Land</option>
            </select>
          </div>
        </div>
      </div>

      {/* Error message */}
      {error && (
        <div className="error-message" data-testid="error-message">
          {error}
          {error.includes('Too many requests') && (
            <button onClick={handleRetry} className="retry-button" data-testid="retry-button">
              Retry
            </button>
          )}
        </div>
      )}

      {/* Results */}
      <div className="search-results">
        <h3>Results ({results.length})</h3>

        {results.length === 0 && !loading && !error ? (
          <p className="no-results" data-testid="no-results">
            No properties found. Try a different search.
          </p>
        ) : (
          <div className="results-grid">
            {results.map(property => (
              <div key={property.id} className="property-card" data-testid="property-card">
                <h4>{property.title}</h4>
                <p className="property-location" data-testid="property-location">
                  {property.location}
                </p>
                <p className="property-price" data-testid="property-price">
                  {property.price.toLocaleString()}
                </p>
                <p className="property-details">
                  {property.bedrooms} beds  {property.bathrooms} baths  {property.squareFeet} sq
                  ft
                </p>
                {property.similarity !== undefined && (
                  <div className="similarity-score" data-testid="similarity-score">
                    {Math.round(property.similarity * 100)}% match
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      <style jsx>{`
        .semantic-search {
          max-width: 1200px;
          margin: 0 auto;
          padding: 20px;
        }

        .search-input {
          display: flex;
          margin-bottom: 20px;
        }

        .query-input {
          flex: 1;
          padding: 12px;
          font-size: 16px;
          border: 1px solid #ddd;
          border-radius: 4px 0 0 4px;
        }

        .search-button {
          padding: 12px 24px;
          background-color: #0070f3;
          color: white;
          border: none;
          border-radius: 0 4px 4px 0;
          cursor: pointer;
        }

        .search-button:disabled {
          background-color: #ccc;
        }

        .filters {
          margin-bottom: 20px;
          padding: 15px;
          background-color: #f9f9f9;
          border-radius: 4px;
        }

        .filter-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
          gap: 15px;
        }

        .filter-item {
          display: flex;
          flex-direction: column;
        }

        .filter-item label {
          margin-bottom: 5px;
          font-weight: 500;
        }

        .filter-item input,
        .filter-item select {
          padding: 8px;
          border: 1px solid #ddd;
          border-radius: 4px;
        }

        .error-message {
          padding: 10px;
          background-color: #ffebee;
          color: #c62828;
          border-radius: 4px;
          margin-bottom: 20px;
        }

        .retry-button {
          margin-left: 10px;
          padding: 5px 10px;
          background-color: #0070f3;
          color: white;
          border: none;
          border-radius: 4px;
          cursor: pointer;
        }

        .results-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
          gap: 20px;
        }

        .property-card {
          border: 1px solid #eee;
          border-radius: 4px;
          padding: 15px;
          position: relative;
        }

        .property-card h4 {
          margin-top: 0;
          margin-bottom: 10px;
        }

        .property-location {
          color: #666;
          margin-bottom: 10px;
        }

        .property-price {
          font-weight: bold;
          font-size: 18px;
          margin-bottom: 10px;
        }

        .property-details {
          color: #666;
          margin-bottom: 0;
        }

        .similarity-score {
          position: absolute;
          top: 15px;
          right: 15px;
          background-color: #0070f3;
          color: white;
          padding: 4px 8px;
          border-radius: 4px;
          font-size: 12px;
          font-weight: bold;
        }

        .no-results {
          text-align: center;
          padding: 20px;
          color: #666;
        }
      `}</style>
    </div>
  )
}

export default SemanticSearch

import axios from 'axios'
import React, { useState, useEffect } from 'react'

// Define types
interface Property {
  id: string
  title: string
  description: string
  price: number
  location: string
  bedrooms: number
  bathrooms: number
  squareFeet: number
  propertyType: string
  status: string
  similarity?: number
}

interface SimilarPropertiesProps {
  propertyId: string
  limit?: number
}

// Similar Properties Component
const SimilarProperties: React.FC<SimilarPropertiesProps> = ({ propertyId, limit = 4 }) => {
  // State
  const [similarProperties, setSimilarProperties] = useState<Property[]>([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)

  // Fetch similar properties
  useEffect(() => {
    const fetchSimilarProperties = async () => {
      if (!propertyId) return

      setLoading(true)
      setError(null)

      try {
        const response = await axios.get(`/api/search/similar/${propertyId}?limit=${limit}`)
        setSimilarProperties(response.data.data.similarProperties)
      } catch (err: any) {
        setError(err.message || 'Failed to load similar properties')
      } finally {
        setLoading(false)
      }
    }

    fetchSimilarProperties()
  }, [propertyId, limit])

  // If loading
  if (loading) {
    return <div className="loading">Loading similar properties...</div>
  }

  // If error
  if (error) {
    return <div className="error">{error}</div>
  }

  // If no similar properties
  if (similarProperties.length === 0) {
    return null
  }

  return (
    <div className="similar-properties" data-testid="similar-properties">
      <h3>Similar Properties</h3>

      <div className="similar-grid">
        {similarProperties.map(property => (
          <div key={property.id} className="similar-card" data-testid="similar-property-card">
            <h4>{property.title}</h4>
            <p className="property-location">{property.location}</p>
            <p className="property-price">{property.price.toLocaleString()}</p>
            <p className="property-details">
              {property.bedrooms} beds  {property.bathrooms} baths
            </p>
            {property.similarity !== undefined && (
              <div className="similarity-badge" data-testid="similarity-score">
                {Math.round(property.similarity * 100)}% similar
              </div>
            )}
          </div>
        ))}
      </div>

      <style jsx>{`
        .similar-properties {
          margin-top: 30px;
          padding: 20px;
          background-color: #f9f9f9;
          border-radius: 4px;
        }

        .similar-properties h3 {
          margin-top: 0;
          margin-bottom: 20px;
        }

        .similar-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
          gap: 15px;
        }

        .similar-card {
          border: 1px solid #eee;
          border-radius: 4px;
          padding: 15px;
          position: relative;
          background-color: white;
        }

        .similar-card h4 {
          margin-top: 0;
          margin-bottom: 10px;
          font-size: 16px;
        }

        .property-location {
          color: #666;
          margin-bottom: 10px;
        }

        .property-price {
          font-weight: bold;
          font-size: 18px;
          margin-bottom: 10px;
        }

        .property-details {
          color: #666;
          margin-bottom: 0;
        }

        .similarity-badge {
          position: absolute;
          top: 10px;
          right: 10px;
          background-color: #4caf50;
          color: white;
          padding: 3px 6px;
          border-radius: 4px;
          font-size: 11px;
        }

        .loading,
        .error {
          padding: 20px;
          text-align: center;
        }

        .error {
          color: #c62828;
        }
      `}</style>
    </div>
  )
}

