// Check if node_modules exists
const nodeModulesPath = path.join(__dirname, 'node_modules')
if (!fs.existsSync(nodeModulesPath)) {
  fs.mkdirSync(nodeModulesPath, { recursive: true })
}

// Create a simple package.json if it doesn't exist
const packageJsonPath = path.join(__dirname, 'package.json')
if (!fs.existsSync(packageJsonPath)) {
  const packageJson = {
    name: '@property-portal/hooks',
    version: '1.0.0',
    private: true,
    dependencies: {
      leaflet: '^1.9.4',
    },
    devDependencies: {
      eslint: '^8.57.1',
    },
  }

  fs.writeFileSync(packageJsonPath, JSON.stringify(packageJson, null, 2))
}

// Create a symlink for ESLint
const eslintPath = path.join(__dirname, '..', 'node_modules', 'eslint')
const localEslintPath = path.join(nodeModulesPath, 'eslint')

if (fs.existsSync(eslintPath) && !fs.existsSync(localEslintPath)) {
  try {
    // On Windows, we need to use junction instead of symlink for directories
    fs.symlinkSync(eslintPath, localEslintPath, 'junction')
    console.log('ESLint symlink created successfully')
  } catch (error) {
    console.error('Error creating symlink:', error)
  }
}

console.log('Setup complete')

// hooks/useAnimation.ts
import { useAnimation, AnimationControls, Variants } from 'framer-motion'
import { useEffect, useRef, useState } from 'react'

interface UseAnimationOptions {
  variants?: Variants
  initial?: string
  animate?: string
  exit?: string
  custom?: any
  onComplete?: () => void
  onStart?: () => void
  autoPlay?: boolean
  delay?: number
}

/**
 * Custom hook for managing Framer Motion animations with proper cleanup
 * to prevent memory leaks
 */
export function useAnimationWithCleanup(options: UseAnimationOptions = {}) {
  const {
    variants,
    initial = 'hidden',
    animate = 'visible',
    exit = 'exit',
    custom,
    onComplete,
    onStart,
    autoPlay = true,
    delay = 0,
  } = options

  const controls = useAnimation()
  const [isAnimating, setIsAnimating] = useState(false)

  // Use a ref to track mounted state to prevent memory leaks
  const isMounted = useRef(false)

  // Initialize animation state
  useEffect(() => {
    isMounted.current = true

    return () => {
      isMounted.current = false
      controls.stop()
    }
  }, [controls])

  // Function to play animation
  const play = async (animationName?: string) => {
    if (!isMounted.current) return

    setIsAnimating(true)

    if (onStart) {
      onStart()
    }

    try {
      if (delay > 0) {
        await new Promise(resolve => setTimeout(resolve, delay))
        if (!isMounted.current) return
      }

      await controls.start(animationName || animate, { custom })

      if (!isMounted.current) return

      if (onComplete) {
        onComplete()
      }
    } catch (error) {
      // Only log errors if component is still mounted
      if (isMounted.current) {
        console.error('Animation error:', error)
      }
    } finally {
      if (isMounted.current) {
        setIsAnimating(false)
      }
    }
  }

  // Function to stop animation
  const stop = () => {
    if (!isMounted.current) return

    controls.stop()
    setIsAnimating(false)
  }

  // Auto-play animation if enabled
  useEffect(() => {
    if (autoPlay) {
      play()
    }

    return () => {
      stop()
    }
  }, [autoPlay]) // eslint-disable-line react-hooks/exhaustive-deps

  return {
    controls,
    isAnimating,
    play,
    stop,
    initial,
    animate: controls,
    exit,
    variants,
    custom,
  }
}

/**
 * Custom hook for scroll-triggered animations with proper cleanup
 */
export function useScrollAnimation(
  options: UseAnimationOptions & {
    threshold?: number
    rootMargin?: string
  } = {}
) {
  const { threshold = 0.1, rootMargin = '0px', ...animationOptions } = options

  const animation = useAnimationWithCleanup({
    ...animationOptions,
    autoPlay: false,
  })

  const ref = useRef<HTMLElement>(null)
  const observerRef = useRef<IntersectionObserver | null>(null)

  useEffect(() => {
    const element = ref.current
    if (!element) return

    // Cleanup previous observer
    if (observerRef.current) {
      observerRef.current.disconnect()
    }

    // Create new observer
    observerRef.current = new IntersectionObserver(
      entries => {
        const [entry] = entries
        if (entry.isIntersecting) {
          animation.play()

          // Optionally unobserve after animation is triggered
          // observerRef.current?.unobserve(element);
        }
      },
      { threshold, rootMargin }
    )

    // Start observing
    observerRef.current.observe(element)

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect()
        observerRef.current = null
      }
    }
  }, [threshold, rootMargin, animation])

  return {
    ...animation,
    ref,
  }
}

export default useAnimationWithCleanup

// hooks/useApi.ts
import { ApiError, createApiClient } from '@/lib/api/error-handling'
import { useState, useCallback } from 'react'

interface UseApiOptions {
  baseUrl?: string
  onError?: (error: ApiError) => void
}

interface ApiState<T> {
  data: T | null
  isLoading: boolean
  error: ApiError | null
}

/**
 * Hook for making API requests with error handling
 * @param options API options
 * @returns API state and request functions
 */
export function useApi<T = any>(options: UseApiOptions = {}) {
  const { baseUrl = '', onError } = options

  const [state, setState] = useState<ApiState<T>>({
    data: null,
    isLoading: false,
    error: null,
  })

  const apiClient = createApiClient(baseUrl)

  /**
   * Make a GET request
   * @param url API endpoint URL
   * @param params Query parameters
   * @returns Response data
   */
  const get = useCallback(
    async (url: string, params?: Record<string, string>): Promise<T | null> => {
      setState(prev => ({ ...prev, isLoading: true, error: null }))

      try {
        // Add query parameters to URL
        const urlWithParams = params ? `${url}?${new URLSearchParams(params)}` : url

        const data = await apiClient<T>(urlWithParams)

        setState({ data, isLoading: false, error: null })
        return data
      } catch (error) {
        const apiError =
          error instanceof ApiError
            ? error
            : new ApiError(error instanceof Error ? error.message : 'Unknown error', 500)

        setState({ data: null, isLoading: false, error: apiError })

        if (onError) {
          onError(apiError)
        }

        return null
      }
    },
    [apiClient, onError]
  )

  /**
   * Make a POST request
   * @param url API endpoint URL
   * @param data Request body data
   * @returns Response data
   */
  const post = useCallback(
    async (url: string, data?: any): Promise<T | null> => {
      setState(prev => ({ ...prev, isLoading: true, error: null }))

      try {
        const responseData = await apiClient<T>(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: data ? JSON.stringify(data) : undefined,
        })

        setState({ data: responseData, isLoading: false, error: null })
        return responseData
      } catch (error) {
        const apiError =
          error instanceof ApiError
            ? error
            : new ApiError(error instanceof Error ? error.message : 'Unknown error', 500)

        setState({ data: null, isLoading: false, error: apiError })

        if (onError) {
          onError(apiError)
        }

        return null
      }
    },
    [apiClient, onError]
  )

  /**
   * Make a PUT request
   * @param url API endpoint URL
   * @param data Request body data
   * @returns Response data
   */
  const put = useCallback(
    async (url: string, data?: any): Promise<T | null> => {
      setState(prev => ({ ...prev, isLoading: true, error: null }))

      try {
        const responseData = await apiClient<T>(url, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: data ? JSON.stringify(data) : undefined,
        })

        setState({ data: responseData, isLoading: false, error: null })
        return responseData
      } catch (error) {
        const apiError =
          error instanceof ApiError
            ? error
            : new ApiError(error instanceof Error ? error.message : 'Unknown error', 500)

        setState({ data: null, isLoading: false, error: apiError })

        if (onError) {
          onError(apiError)
        }

        return null
      }
    },
    [apiClient, onError]
  )

  /**
   * Make a DELETE request
   * @param url API endpoint URL
   * @returns Response data
   */
  const del = useCallback(
    async (url: string): Promise<T | null> => {
      setState(prev => ({ ...prev, isLoading: true, error: null }))

      try {
        const responseData = await apiClient<T>(url, {
          method: 'DELETE',
        })

        setState({ data: responseData, isLoading: false, error: null })
        return responseData
      } catch (error) {
        const apiError =
          error instanceof ApiError
            ? error
            : new ApiError(error instanceof Error ? error.message : 'Unknown error', 500)

        setState({ data: null, isLoading: false, error: apiError })

        if (onError) {
          onError(apiError)
        }

        return null
      }
    },
    [apiClient, onError]
  )

  /**
   * Reset API state
   */
  const reset = useCallback(() => {
    setState({
      data: null,
      isLoading: false,
      error: null,
    })
  }, [])

  return {
    ...state,
    get,
    post,
    put,
    delete: del,
    reset,
  }
}

// hooks/useCentralizedErrorHandler.ts
import { ApiError } from '@/lib/api/error-handling'
import { usePathname } from 'next/navigation'
import { useState, useCallback, useEffect } from 'react'

import { ValidationError } from '@/lib/api/validation'
import { errorService, ErrorContext, ErrorType, ErrorSeverity } from '@/lib/error/error-service'

interface ErrorState {
  hasError: boolean
  error: Error | null
  errorMessage: string
  errorCode?: string
  errorDetails?: Record<string, unknown>
  isApiError: boolean
  isValidationError: boolean
  errorType: ErrorType
  errorSeverity: ErrorSeverity
}

interface UseCentralizedErrorHandlerOptions {
  component?: string
  action?: string
  tags?: string[]
  onError?: (error: Error, context: ErrorContext) => void
}

/**
 * Hook for handling errors using the centralized error service
 * @param options Error handler options
 * @returns Error state and handler functions
 */
export function useCentralizedErrorHandler(options: UseCentralizedErrorHandlerOptions = {}) {
  const { component, action, tags, onError } = options
  const pathname = usePathname()

  const [errorState, setErrorState] = useState<ErrorState>({
    hasError: false,
    error: null,
    errorMessage: '',
    errorCode: undefined,
    errorDetails: undefined,
    isApiError: false,
    isValidationError: false,
    errorType: ErrorType.UNKNOWN,
    errorSeverity: ErrorSeverity.LOW,
  })

  // Create a default context for all errors
  const defaultContext = {
    component,
    action,
    tags,
    url: typeof window !== 'undefined' ? window.location.href : undefined,
    pathname,
  }

  /**
   * Handle an error
   * @param error Error to handle
   * @param additionalContext Additional context for the error
   */
  const handleError = useCallback(
    (error: unknown, additionalContext?: Omit<ErrorContext, 'type' | 'severity'>) => {
      // Convert to Error object if needed
      const errorObject =
        error instanceof Error
          ? error
          : new Error(typeof error === 'string' ? error : 'Unknown error')

      // Determine error type and details
      const isApiError = errorObject instanceof ApiError
      const isValidationError = errorObject instanceof ValidationError
      const errorType = isApiError
        ? ErrorType.API
        : isValidationError
          ? ErrorType.VALIDATION
          : ErrorType.UNKNOWN

      // Get error details
      const errorMessage = errorObject.message
      const errorCode = isApiError
        ? (errorObject as ApiError).code
        : isValidationError
          ? (errorObject as ValidationError).code
          : undefined
      const errorDetails = isApiError
        ? (errorObject as ApiError).details
        : isValidationError
          ? (errorObject as ValidationError).details
          : undefined

      // Create context for the error service
      const context: ErrorContext = {
        ...defaultContext,
        ...additionalContext,
        type: errorType,
        metadata: {
          ...(additionalContext?.metadata || {}),
          errorCode,
          errorDetails,
        },
      }

      // Update error state
      setErrorState({
        hasError: true,
        error: errorObject,
        errorMessage,
        errorCode,
        errorDetails,
        isApiError,
        isValidationError,
        errorType,
        errorSeverity: context.severity || ErrorSeverity.LOW,
      })

      // Handle the error with the error service
      errorService.handleError(errorObject, context)

      // Call the optional onError callback
      if (onError) {
        onError(errorObject, context)
      }
    },
    [defaultContext, onError]
  )

  /**
   * Reset the error state
   */
  const resetError = useCallback(() => {
    setErrorState({
      hasError: false,
      error: null,
      errorMessage: '',
      errorCode: undefined,
      errorDetails: undefined,
      isApiError: false,
      isValidationError: false,
      errorType: ErrorType.UNKNOWN,
      errorSeverity: ErrorSeverity.LOW,
    })
  }, [])

  /**
   * Create a try/catch wrapper for a function
   * @param fn Function to wrap
   * @param additionalContext Additional context for errors
   * @returns Wrapped function
   */
  const withErrorHandling = useCallback(
    <T extends Array<unknown>, R>(
      fn: (...args: T) => R,
      additionalContext?: Omit<ErrorContext, 'type' | 'severity'>
    ) => {
      return (...args: T): R | undefined => {
        try {
          return fn(...args)
        } catch (error) {
          handleError(error, additionalContext)
          return undefined
        }
      }
    },
    [handleError]
  )

  /**
   * Create a try/catch wrapper for an async function
   * @param fn Async function to wrap
   * @param additionalContext Additional context for errors
   * @returns Wrapped async function
   */
  const withAsyncErrorHandling = useCallback(
    <T extends Array<unknown>, R>(
      fn: (...args: T) => Promise<R>,
      additionalContext?: Omit<ErrorContext, 'type' | 'severity'>
    ) => {
      return async (...args: T): Promise<R | undefined> => {
        try {
          return await fn(...args)
        } catch (error) {
          handleError(error, additionalContext)
          return undefined
        }
      }
    },
    [handleError]
  )

  // Reset error state when component or pathname changes
  useEffect(() => {
    resetError()
  }, [component, pathname, resetError])

  return {
    ...errorState,
    handleError,
    resetError,
    withErrorHandling,
    withAsyncErrorHandling,
  }
}

// hooks/useErrorHandler.ts
import { ApiError } from '@/lib/api/error-handling'
import { useState, useCallback } from 'react'

import { ValidationError } from '@/lib/api/validation'
import logger from '@/lib/logging/logger'

interface ErrorState {
  hasError: boolean
  error: Error | null
  errorMessage: string
  errorCode?: string
  errorDetails?: any
  isApiError: boolean
  isValidationError: boolean
}

interface UseErrorHandlerOptions {
  logErrors?: boolean
  rethrow?: boolean
  onError?: (error: Error) => void
}

/**
 * Hook for handling errors in React components
 * @param options Error handler options
 * @returns Error state and handler functions
 */
export function useErrorHandler(options: UseErrorHandlerOptions = {}) {
  const { logErrors = true, rethrow = false, onError } = options

  const [errorState, setErrorState] = useState<ErrorState>({
    hasError: false,
    error: null,
    errorMessage: '',
    errorCode: undefined,
    errorDetails: undefined,
    isApiError: false,
    isValidationError: false,
  })

  /**
   * Handle an error
   * @param error Error to handle
   * @param context Additional context for logging
   * @param tags Tags for categorizing logs
   */
  const handleError = useCallback(
    (error: unknown, context?: Record<string, any>, tags?: string[]) => {
      // Convert to Error object if needed
      const errorObject =
        error instanceof Error
          ? error
          : new Error(typeof error === 'string' ? error : 'Unknown error')

      // Determine error type
      const isApiError = errorObject instanceof ApiError
      const isValidationError = errorObject instanceof ValidationError

      // Get error details
      const errorMessage = errorObject.message
      const errorCode = isApiError
        ? (errorObject as ApiError).code
        : isValidationError
          ? (errorObject as ValidationError).code
          : undefined
      const errorDetails = isApiError
        ? (errorObject as ApiError).details
        : isValidationError
          ? (errorObject as ValidationError).details
          : undefined

      // Update error state
      setErrorState({
        hasError: true,
        error: errorObject,
        errorMessage,
        errorCode,
        errorDetails,
        isApiError,
        isValidationError,
      })

      // Log the error
      if (logErrors) {
        logger.error(
          'Error caught by useErrorHandler',
          errorObject,
          {
            ...context,
            isApiError,
            isValidationError,
            errorCode,
            errorDetails,
          },
          [...(tags || []), 'error-handler']
        )
      }

      // Call the optional onError callback
      if (onError) {
        onError(errorObject)
      }

      // Rethrow the error if requested
      if (rethrow) {
        throw errorObject
      }
    },
    [logErrors, rethrow, onError]
  )

  /**
   * Reset the error state
   */
  const resetError = useCallback(() => {
    setErrorState({
      hasError: false,
      error: null,
      errorMessage: '',
      errorCode: undefined,
      errorDetails: undefined,
      isApiError: false,
      isValidationError: false,
    })
  }, [])

  /**
   * Create a try/catch wrapper for a function
   * @param fn Function to wrap
   * @param context Additional context for logging
   * @param tags Tags for categorizing logs
   * @returns Wrapped function
   */
  const withErrorHandling = useCallback(
    <T extends any[], R>(fn: (...args: T) => R, context?: Record<string, any>, tags?: string[]) => {
      return (...args: T): R | undefined => {
        try {
          return fn(...args)
        } catch (error) {
          handleError(error, context, tags)
          return undefined
        }
      }
    },
    [handleError]
  )

  /**
   * Create a try/catch wrapper for an async function
   * @param fn Async function to wrap
   * @param context Additional context for logging
   * @param tags Tags for categorizing logs
   * @returns Wrapped async function
   */
  const withAsyncErrorHandling = useCallback(
    <T extends any[], R>(
      fn: (...args: T) => Promise<R>,
      context?: Record<string, any>,
      tags?: string[]
    ) => {
      return async (...args: T): Promise<R | undefined> => {
        try {
          return await fn(...args)
        } catch (error) {
          handleError(error, context, tags)
          return undefined
        }
      }
    },
    [handleError]
  )

  return {
    ...errorState,
    handleError,
    resetError,
    withErrorHandling,
    withAsyncErrorHandling,
  }
}

// hooks/useFormValidation.ts
import { useState, useCallback, useEffect } from 'react'
import { z } from 'zod'

import { validateForm, FormErrors, ValidationResult } from '@/lib/validation/form-validation'

interface UseFormValidationOptions<T> {
  initialValues: T
  schema: z.ZodType<T>
  onSubmit?: (data: T) => void | Promise<void>
  validateOnChange?: boolean
  validateOnBlur?: boolean
}

interface UseFormValidationResult<T> {
  values: T
  errors: FormErrors<T>
  touched: Partial<Record<keyof T, boolean>>
  isSubmitting: boolean
  isValid: boolean
  isDirty: boolean
  handleChange: (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => void
  handleBlur: (
    e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => void
  handleSubmit: (e: React.FormEvent) => Promise<void>
  setFieldValue: (field: keyof T, value: any) => void
  setFieldTouched: (field: keyof T, isTouched?: boolean) => void
  setFieldError: (field: keyof T, error?: string) => void
  resetForm: () => void
  validateField: (field: keyof T) => string | undefined
  validateForm: () => boolean
}

/**
 * Custom hook for form validation using Zod schemas
 * @param options Form validation options
 * @returns Form validation state and handlers
 */
export function useFormValidation<T extends Record<string, any>>(
  options: UseFormValidationOptions<T>
): UseFormValidationResult<T> {
  const {
    initialValues,
    schema,
    onSubmit,
    validateOnChange = false,
    validateOnBlur = true,
  } = options

  const [values, setValues] = useState<T>(initialValues)
  const [errors, setErrors] = useState<FormErrors<T>>({})
  const [touched, setTouched] = useState<Partial<Record<keyof T, boolean>>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [isDirty, setIsDirty] = useState(false)

  // Reset form to initial values
  const resetForm = useCallback(() => {
    setValues(initialValues)
    setErrors({})
    setTouched({})
    setIsSubmitting(false)
    setIsDirty(false)
  }, [initialValues])

  // Validate the entire form
  const validateFormData = useCallback((): ValidationResult<T> => {
    return validateForm(schema, values)
  }, [schema, values])

  // Check if the form is valid
  const isValid = Object.keys(errors).length === 0

  // Validate a single field
  const validateField = useCallback(
    (field: keyof T): string | undefined => {
      try {
        // Create a schema for just this field
        const fieldSchema = z.object({ [field]: schema.shape[field] })
        const result = fieldSchema.safeParse({ [field]: values[field] })

        if (!result.success) {
          const fieldErrors = result.error.flatten().fieldErrors
          const errorMessage = fieldErrors[field as string]?.[0]
          return errorMessage
        }

        return undefined
      } catch (error) {
        console.error(`Error validating field ${String(field)}:`, error)
        return 'Validation error'
      }
    },
    [schema, values]
  )

  // Set a field value
  const setFieldValue = useCallback(
    (field: keyof T, value: any) => {
      setValues(prev => {
        const newValues = { ...prev, [field]: value }
        return newValues
      })
      setIsDirty(true)

      if (validateOnChange) {
        const errorMessage = validateField(field)
        setErrors(prev => ({
          ...prev,
          [field]: errorMessage,
        }))
      }
    },
    [validateField, validateOnChange]
  )

  // Set a field as touched
  const setFieldTouched = useCallback(
    (field: keyof T, isTouched: boolean = true) => {
      setTouched(prev => ({
        ...prev,
        [field]: isTouched,
      }))

      if (validateOnBlur && isTouched) {
        const errorMessage = validateField(field)
        setErrors(prev => ({
          ...prev,
          [field]: errorMessage,
        }))
      }
    },
    [validateField, validateOnBlur]
  )

  // Set a field error manually
  const setFieldError = useCallback((field: keyof T, error?: string) => {
    setErrors(prev => ({
      ...prev,
      [field]: error,
    }))
  }, [])

  // Handle input change
  const handleChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
      const { name, value, type } = e.target

      // Handle different input types
      let parsedValue: any = value

      if (type === 'number') {
        parsedValue = value === '' ? '' : Number(value)
      } else if (type === 'checkbox') {
        parsedValue = (e.target as HTMLInputElement).checked
      }

      setFieldValue(name as keyof T, parsedValue)
    },
    [setFieldValue]
  )

  // Handle input blur
  const handleBlur = useCallback(
    (e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
      const { name } = e.target
      setFieldTouched(name as keyof T, true)
    },
    [setFieldTouched]
  )

  // Validate the entire form
  const validateFormValues = useCallback(() => {
    const result = validateFormData()

    if (!result.success && result.errors) {
      setErrors(result.errors)
      return false
    }

    setErrors({})
    return true
  }, [validateFormData])

  // Handle form submission
  const handleSubmit = useCallback(
    async (e: React.FormEvent) => {
      e.preventDefault()

      // Mark all fields as touched
      const allTouched = Object.keys(values).reduce(
        (acc, key) => {
          acc[key as keyof T] = true
          return acc
        },
        {} as Record<keyof T, boolean>
      )

      setTouched(allTouched)

      // Validate the form
      const isValid = validateFormValues()

      if (!isValid || !onSubmit) {
        return
      }

      setIsSubmitting(true)

      try {
        await onSubmit(values)
      } catch (error) {
        console.error('Form submission error:', error)

        // Set a generic form error
        setErrors(prev => ({
          ...prev,
          _form: 'An error occurred during submission. Please try again.',
        }))
      } finally {
        setIsSubmitting(false)
      }
    },
    [values, validateFormValues, onSubmit]
  )

  // Validate form when schema changes
  useEffect(() => {
    if (isDirty) {
      validateFormValues()
    }
  }, [schema, isDirty, validateFormValues])

  return {
    values,
    errors,
    touched,
    isSubmitting,
    isValid,
    isDirty,
    handleChange,
    handleBlur,
    handleSubmit,
    setFieldValue,
    setFieldTouched,
    setFieldError,
    resetForm,
    validateField,
    validateForm: validateFormValues,
  }
}

