} as const

import { NextRequest } from 'next/server'
import { z } from 'zod'

import { errorResponse, HttpStatus } from './response'

/**
 * Custom error class for JSON parsing errors
 */
export class JsonParseError extends Error {
  constructor(
    message: string,
    public originalError: Error
  ) {
    super(message)
    this.name = 'JsonParseError'
  }
}

/**
 * Custom error class for validation errors
 */
export class ValidationError extends Error {
  constructor(
    message: string,
    public code: string,
    public details?: any
  ) {
    super(message)
    this.name = 'ValidationError'
  }
}

/**
 * Format Zod validation errors into a more user-friendly format
 * @param error Zod error object
 * @returns Formatted error object with field paths and messages
 */
export function formatZodError(error: z.ZodError): Record<string, string> {
  const formattedErrors: Record<string, string> = {}

  error.errors.forEach(err => {
    const path = err.path.join('.')
    const fieldName = path || 'value'

    // Create more specific error messages based on error code
    let message = err.message

    switch (err.code) {
      case 'invalid_type':
        if (err.expected === 'string' && err.received === 'undefined') {
          message = 'This field is required'
        } else {
          message = `Expected ${err.expected}, received ${err.received}`
        }
        break
      case 'too_small':
        if (err.type === 'string') {
          if (err.minimum === 1) {
            message = 'This field cannot be empty'
          } else {
            message = `Must be at least ${err.minimum} characters`
          }
        } else if (err.type === 'number') {
          message = `Must be greater than or equal to ${err.minimum}`
        } else if (err.type === 'array') {
          message = `Must have at least ${err.minimum} item(s)`
        }
        break
      case 'too_big':
        if (err.type === 'string') {
          message = `Must be at most ${err.maximum} characters`
        } else if (err.type === 'number') {
          message = `Must be less than or equal to ${err.maximum}`
        } else if (err.type === 'array') {
          message = `Must have at most ${err.maximum} item(s)`
        }
        break
      case 'invalid_string':
        if (err.validation === 'email') {
          message = 'Must be a valid email address'
        } else if (err.validation === 'url') {
          message = 'Must be a valid URL'
        } else if (err.validation === 'uuid') {
          message = 'Must be a valid UUID'
        }
        break
      case 'invalid_enum_value':
        message = `Must be one of: ${err.options.map(o => `'${o}'`).join(', ')}`
        break
      case 'invalid_date':
        message = 'Must be a valid date'
        break
    }

    formattedErrors[fieldName] = message
  })

  return formattedErrors
}

/**
 * Validate request query parameters against a Zod schema
 * @param request The Next.js request object
 * @param schema The Zod schema to validate against
 * @returns The validated query parameters
 * @throws ValidationError if validation fails
 */
export async function validateQuery<T extends z.ZodType>(
  request: NextRequest,
  schema: T
): Promise<z.infer<T>> {
  try {
    const searchParams = Object.fromEntries(request.nextUrl.searchParams.entries())
    return schema.parse(searchParams)
  } catch (error) {
    if (error instanceof z.ZodError) {
      const formattedErrors = formatZodError(error)
      throw new ValidationError(
        'Invalid query parameters',
        'INVALID_QUERY_PARAMETERS',
        formattedErrors
      )
    }
    throw new ValidationError('Failed to process query parameters', 'QUERY_PROCESSING_ERROR')
  }
}

/**
 * Validate request body against a Zod schema
 * @param request The Next.js request object
 * @param schema The Zod schema to validate against
 * @returns The validated body
 * @throws JsonParseError if JSON parsing fails
 * @throws ValidationError if validation fails
 */
export async function validateBody<T extends z.ZodType>(
  request: NextRequest,
  schema: T
): Promise<z.infer<T>> {
  let body: any

  try {
    body = await request.json()
  } catch (error) {
    // Handle JSON parsing errors specifically
    const originalError = error instanceof Error ? error : new Error('Unknown error')

    // Check for common JSON parsing error patterns
    const errorMessage = originalError.message || ''

    if (errorMessage.includes('Unexpected end of JSON input')) {
      throw new JsonParseError(
        'Incomplete JSON data received. Please check that your request body is complete.',
        originalError
      )
    } else if (errorMessage.includes('Unexpected token')) {
      throw new JsonParseError(
        'Malformed JSON data received. Please check your request syntax.',
        originalError
      )
    } else {
      throw new JsonParseError(
        'Failed to parse request body as JSON. Please ensure your request contains valid JSON data.',
        originalError
      )
    }
  }

  try {
    return schema.parse(body)
  } catch (error) {
    if (error instanceof z.ZodError) {
      const formattedErrors = formatZodError(error)
      throw new ValidationError('Invalid request data', 'VALIDATION_ERROR', formattedErrors)
    }
    throw new ValidationError('Failed to validate request data', 'VALIDATION_PROCESSING_ERROR')
  }
}

/**
 * Safely validate form data against a Zod schema
 * @param request The Next.js request object
 * @param schema The Zod schema to validate against
 * @returns The validated form data
 * @throws ValidationError if validation fails
 */
export async function validateFormData<T extends z.ZodType>(
  request: NextRequest,
  schema: T
): Promise<z.infer<T>> {
  try {
    const formData = await request.formData()
    const data = Object.fromEntries(formData.entries())

    // Convert numeric strings to numbers if they look like numbers
    const processedData = Object.entries(data).reduce(
      (acc, [key, value]) => {
        if (typeof value === 'string' && /^-?\d+(\.\d+)?$/.test(value.trim())) {
          acc[key] = Number(value)
        } else {
          acc[key] = value
        }
        return acc
      },
      {} as Record<string, any>
    )

    return schema.parse(processedData)
  } catch (error) {
    if (error instanceof z.ZodError) {
      const formattedErrors = formatZodError(error)
      throw new ValidationError('Invalid form data', 'FORM_VALIDATION_ERROR', formattedErrors)
    }

    const errorMessage = error instanceof Error ? error.message : 'Unknown error'
    throw new ValidationError(
      `Failed to process form data: ${errorMessage}`,
      'FORM_PROCESSING_ERROR'
    )
  }
}

/**
 * Handle validation errors in API routes
 * @param handler The API route handler function
 * @returns A wrapped handler that handles validation errors
 */
export function withValidation(handler: (request: NextRequest) => Promise<Response>) {
  return async (request: NextRequest) => {
    try {
      return await handler(request)
    } catch (error) {
      // Handle Zod validation errors
      if (error instanceof z.ZodError) {
        const formattedErrors = formatZodError(error)
        return errorResponse(
          'Validation error',
          HttpStatus.UNPROCESSABLE_ENTITY,
          'VALIDATION_ERROR',
          formattedErrors
        )
      }

      // Handle custom validation errors
      if (error instanceof ValidationError) {
        return errorResponse(
          error.message,
          HttpStatus.UNPROCESSABLE_ENTITY,
          error.code,
          error.details
        )
      }

      // Handle JSON parsing errors
      if (error instanceof JsonParseError) {
        return errorResponse(error.message, HttpStatus.BAD_REQUEST, 'INVALID_JSON', {
          originalError: error.originalError.message,
        })
      }

      // Handle other errors
      console.error('API error:', error)
      return errorResponse(
        error instanceof Error ? error.message : 'An unexpected error occurred',
        HttpStatus.INTERNAL_SERVER_ERROR,
        'SERVER_ERROR'
      )
    }
  }
}

// lib/auth/api-auth.ts
import { auth } from '@clerk/nextjs/server'
import { NextRequest, NextResponse } from 'next/server'

// HTTP status codes
export const HttpStatus = {
  OK: 200,
  CREATED: 201,
  BAD_REQUEST: 400,
  UNAUTHORIZED: 401,
  FORBIDDEN: 403,
  NOT_FOUND: 404,
  INTERNAL_SERVER_ERROR: 500,
  SERVICE_UNAVAILABLE: 503,
}

// Error response type
interface ErrorResponse {
  message: string
  code?: string
  details?: any
}

/**
 * Create an error response
 * @param message Error message
 * @param status HTTP status code
 * @param code Error code
 * @param details Additional error details
 * @returns NextResponse with error details
 */
export function errorResponse(
  message: string,
  status: number = HttpStatus.INTERNAL_SERVER_ERROR,
  code?: string,
  details?: any
): NextResponse {
  const error: ErrorResponse = {
    message,
  }

  if (code) error.code = code
  if (details) error.details = details

  return NextResponse.json({ error }, { status })
}

/**
 * Create a success response
 * @param data Response data
 * @param status HTTP status code
 * @returns NextResponse with data
 */
export function successResponse<T>(data: T, status: number = HttpStatus.OK): NextResponse {
  return NextResponse.json(data, { status })
}

/**
 * Authentication options
 */
interface AuthOptions {
  requireAuth?: boolean
  requiredRoles?: string[]
}

/**
 * Authenticated handler result
 */
interface AuthResult {
  userId: string | null
  sessionClaims: any | null
  roles: string[]
  isAuthenticated: boolean
  isAuthorized: boolean
  error?: NextResponse
}

/**
 * Check authentication and authorization for an API route
 * @param req Next.js request
 * @param options Authentication options
 * @returns Authentication result
 */
export async function checkAuth(req: NextRequest, options: AuthOptions = {}): Promise<AuthResult> {
  const { requireAuth = true, requiredRoles = [] } = options

  try {
    // Get authentication data from Clerk
    const authResult = await auth()
    const { userId, sessionClaims } = authResult

    // Extract roles from session claims
    const roles = (sessionClaims?.roles as string[]) || []

    // Check if user is authenticated
    const isAuthenticated = !!userId

    // If authentication is required but user is not authenticated
    if (requireAuth && !isAuthenticated) {
      return {
        userId,
        sessionClaims,
        roles,
        isAuthenticated,
        isAuthorized: false,
        error: errorResponse('Authentication required', HttpStatus.UNAUTHORIZED, 'UNAUTHORIZED'),
      }
    }

    // Check if user has required roles
    const isAuthorized = !requiredRoles.length || roles.some(role => requiredRoles.includes(role))

    // If user doesn't have required roles
    if (requireAuth && isAuthenticated && !isAuthorized) {
      return {
        userId,
        sessionClaims,
        roles,
        isAuthenticated,
        isAuthorized,
        error: errorResponse('Insufficient permissions', HttpStatus.FORBIDDEN, 'FORBIDDEN', {
          requiredRoles,
        }),
      }
    }

    // Authentication and authorization successful
    return {
      userId,
      sessionClaims,
      roles,
      isAuthenticated,
      isAuthorized,
    }
  } catch (error) {
    console.error('Authentication error:', error)

    // Return authentication error
    return {
      userId: null,
      sessionClaims: null,
      roles: [],
      isAuthenticated: false,
      isAuthorized: false,
      error: errorResponse(
        'Authentication service error',
        HttpStatus.SERVICE_UNAVAILABLE,
        'AUTH_SERVICE_ERROR'
      ),
    }
  }
}

/**
 * Higher-order function to wrap API route handlers with authentication
 * @param handler API route handler
 * @param options Authentication options
 * @returns Wrapped handler with authentication
 */
export function withAuth(
  handler: (req: NextRequest, authResult: AuthResult) => Promise<NextResponse>,
  options: AuthOptions = {}
) {
  return async (req: NextRequest): Promise<NextResponse> => {
    try {
      // Check authentication
      const authResult = await checkAuth(req, options)

      // If authentication failed, return error response
      if (authResult.error) {
        return authResult.error
      }

      // Call handler with authentication result
      return await handler(req, authResult)
    } catch (error) {
      console.error('API route error:', error)

      // Return generic error response
      return errorResponse(
        'An unexpected error occurred',
        HttpStatus.INTERNAL_SERVER_ERROR,
        'INTERNAL_SERVER_ERROR'
      )
    }
  }
}

import { auth } from '@clerk/nextjs/server'
import { NextResponse } from 'next/server'

/**
 * Checks if a user has any of the allowed roles
 * @param sessionClaims The session claims object from Clerk auth
 * @param allowed Array of allowed role names
 * @returns Boolean indicating if the user has any of the allowed roles
 */
export function userHasRole(sessionClaims: any, allowed: string[]): boolean {
  const roles = (sessionClaims?.roles as string[]) || []
  return roles.some(role => allowed.includes(role))
}

/**
 * Validates user authentication and checks for specific roles
 * @param requiredRoles Array of roles that are allowed to access the resource
 * @returns Object containing authentication result and response (if auth failed)
 */
export async function validateUserRoles(requiredRoles: string[] = ['agent', 'admin']) {
  // Get the user ID and authentication data from Clerk
  const authResult = await auth()
  const userId = authResult.userId
  const { sessionClaims } = authResult

  // Check if user is authenticated
  if (!userId) {
    console.warn('Unauthorized access attempt')
    return {
      isAuthorized: false,
      userId: null,
      response: NextResponse.json(
        { message: 'Unauthorized - Authentication required' },
        { status: 401 }
      ),
    }
  }

  // Check if user has any of the required roles
  const hasRequiredRole = userHasRole(sessionClaims, requiredRoles)

  if (!hasRequiredRole) {
    console.warn(`User ${userId} attempted access without required role`)
    return {
      isAuthorized: false,
      userId,
      response: NextResponse.json(
        {
          message: 'Forbidden - Insufficient permissions',
          details: `This action requires one of these roles: ${requiredRoles.join(', ')}`,
        },
        { status: 403 }
      ),
    }
  }

  return {
    isAuthorized: true,
    userId,
    response: null,
  }
}

/**
 * Validates user authentication and role permissions for property uploads
 * @returns Object containing authentication result and response (if auth failed)
 */
export async function validateUserAuth() {
  // Use the more generic validateUserRoles function with default roles for property uploads
  return validateUserRoles(['agent', 'admin'])
}

// lib/auth/clerk-wrapper.tsx
import { ClerkProvider, useUser as useClerkUser, SignedIn, SignedOut, useAuth } from '@clerk/nextjs'
import { useRouter } from 'next/navigation'
import React, { useState, useEffect, useCallback, createContext, useContext } from 'react'

// Define the authentication state
interface AuthState {
  isLoaded: boolean
  isSignedIn: boolean | null
  isError: boolean
  errorMessage: string | null
  retryCount: number
  isRetrying: boolean
}

// Define the authentication context
interface AuthContextType {
  authState: AuthState
  user: ReturnType<typeof useClerkUser>['user'] | null
  retryAuth: () => void
  signOut: () => Promise<void>
  isAuthorized: (requiredRoles?: string[]) => boolean
}

// Create the authentication context
const AuthContext = createContext<AuthContextType | null>(null)

// Custom hook to use the authentication context
export const useAuthWrapper = () => {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error('useAuthWrapper must be used within an AuthProvider')
  }
  return context
}

// Authentication provider component
export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { isLoaded: clerkLoaded, isSignedIn, user } = useClerkUser()
  const { signOut: clerkSignOut } = useAuth()
  const router = useRouter()

  // Authentication state
  const [authState, setAuthState] = useState<AuthState>({
    isLoaded: false,
    isSignedIn: null,
    isError: false,
    errorMessage: null,
    retryCount: 0,
    isRetrying: false,
  })

  // Maximum number of retry attempts
  const MAX_RETRY_ATTEMPTS = 3

  // Retry authentication
  const retryAuth = useCallback(() => {
    if (authState.retryCount >= MAX_RETRY_ATTEMPTS) {
      setAuthState(prev => ({
        ...prev,
        isError: true,
        errorMessage: 'Maximum retry attempts reached. Please refresh the page or try again later.',
        isRetrying: false,
      }))
      return
    }

    setAuthState(prev => ({
      ...prev,
      isRetrying: true,
      retryCount: prev.retryCount + 1,
    }))

    // Simulate a retry by forcing a re-render
    // In a real app, you might want to call a specific Clerk method to refresh the session
    setTimeout(() => {
      setAuthState(prev => ({
        ...prev,
        isRetrying: false,
      }))
    }, 1000)
  }, [authState.retryCount])

  // Sign out with error handling
  const handleSignOut = useCallback(async () => {
    try {
      if (clerkSignOut) {
        await clerkSignOut()
        router.push('/')
      }
    } catch (error) {
      console.error('Error signing out:', error)
      // If sign out fails, we can still clear local state
      setAuthState(prev => ({
        ...prev,
        isSignedIn: false,
        isError: true,
        errorMessage: 'Failed to sign out properly. Please refresh the page.',
      }))
    }
  }, [clerkSignOut, router])

  // Check if user has required roles
  const isAuthorized = useCallback(
    (requiredRoles?: string[]) => {
      if (!isSignedIn || !user) return false
      if (!requiredRoles || requiredRoles.length === 0) return true

      // Get user roles from public metadata
      // This assumes roles are stored in publicMetadata.roles
      const userRoles = (user.publicMetadata?.roles as string[]) || []

      // Check if user has any of the required roles
      return requiredRoles.some(role => userRoles.includes(role))
    },
    [isSignedIn, user]
  )

  // Update auth state when Clerk state changes
  useEffect(() => {
    // If Clerk is still loading, don't update state yet
    if (!clerkLoaded) return

    setAuthState(prev => ({
      ...prev,
      isLoaded: true,
      isSignedIn,
      // Clear error state if authentication succeeds
      ...(isSignedIn && { isError: false, errorMessage: null }),
    }))
  }, [clerkLoaded, isSignedIn])

  // Handle offline/online status
  useEffect(() => {
    const handleOnline = () => {
      // When coming back online, check if we had an error and retry
      if (authState.isError) {
        retryAuth()
      }
    }

    const handleOffline = () => {
      // When going offline, set an appropriate error message
      setAuthState(prev => ({
        ...prev,
        isError: true,
        errorMessage: 'You are currently offline. Authentication services may be unavailable.',
      }))
    }

    window.addEventListener('online', handleOnline)
    window.addEventListener('offline', handleOffline)

    return () => {
      window.removeEventListener('online', handleOnline)
      window.removeEventListener('offline', handleOffline)
    }
  }, [authState.isError, retryAuth])

  // Context value
  const contextValue: AuthContextType = {
    authState,
    user,
    retryAuth,
    signOut: handleSignOut,
    isAuthorized,
  }

  return <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>
}

// Enhanced ClerkProvider with error handling
export const EnhancedClerkProvider: React.FC<{
  children: React.ReactNode
  publishableKey?: string
  appearance?: any
}> = ({ children, publishableKey, appearance }) => {
  return (
    <ClerkProvider publishableKey={publishableKey} appearance={appearance}>
      <AuthProvider>{children}</AuthProvider>
    </ClerkProvider>
  )
}

// Authentication required component
export const AuthRequired: React.FC<{
  children: React.ReactNode
  fallback?: React.ReactNode
  requiredRoles?: string[]
}> = ({ children, fallback, requiredRoles }) => {
  const { authState, user, retryAuth, isAuthorized } = useAuthWrapper()

  // If still loading, show loading state
  if (!authState.isLoaded) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-700"></div>
      </div>
    )
  }

  // If authentication error, show error state with retry button
  if (authState.isError) {
    return (
      <div className="flex flex-col justify-center items-center min-h-screen p-4">
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md text-center">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-12 w-12 text-red-500 mx-auto mb-4"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
            />
          </svg>
          <h2 className="text-xl font-semibold text-red-700 mb-2">Authentication Error</h2>
          <p className="text-red-600 mb-4">
            {authState.errorMessage || 'There was a problem authenticating your account.'}
          </p>
          <button
            onClick={retryAuth}
            disabled={authState.isRetrying}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 disabled:opacity-50"
          >
            {authState.isRetrying ? 'Retrying...' : 'Retry Authentication'}
          </button>
          <p className="mt-4 text-sm text-gray-600">
            If the problem persists, please try refreshing the page or contact support.
          </p>
        </div>
      </div>
    )
  }

  // If not signed in, show fallback or default message
  if (!authState.isSignedIn) {
    return (
      <>
        <SignedOut>
          {fallback || (
            <div className="flex flex-col justify-center items-center min-h-screen p-4">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-6 max-w-md text-center">
                <h2 className="text-xl font-semibold text-blue-700 mb-2">
                  Authentication Required
                </h2>
                <p className="text-blue-600 mb-4">Please sign in to access this page.</p>
                <a
                  href="/sign-in"
                  className="inline-block px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                >
                  Sign In
                </a>
              </div>
            </div>
          )}
        </SignedOut>
      </>
    )
  }

  // If roles are required but user doesn't have them
  if (requiredRoles && requiredRoles.length > 0 && !isAuthorized(requiredRoles)) {
    return (
      <div className="flex flex-col justify-center items-center min-h-screen p-4">
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 max-w-md text-center">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-12 w-12 text-yellow-500 mx-auto mb-4"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 15v2m0 0v2m0-2h2m-2 0H9m3-3V9m0 0V7m0 2h2m-2 0H9"
            />
          </svg>
          <h2 className="text-xl font-semibold text-yellow-700 mb-2">Access Denied</h2>
          <p className="text-yellow-600 mb-4">
            You don't have the required permissions to access this page.
          </p>
          <a
            href="/"
            className="inline-block px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-yellow-500 focus:ring-offset-2"
          >
            Go to Home
          </a>
        </div>
      </div>
    )
  }

  // If signed in and authorized, show children
  return <SignedIn>{children}</SignedIn>
}

// Export a hook for checking authorization
export const useAuthorization = () => {
  const { isAuthorized } = useAuthWrapper()
  return { isAuthorized }
}

// Export a hook for getting authentication state
export const useAuthentication = () => {
  const { authState, user, retryAuth, signOut } = useAuthWrapper()
  return {
    isLoaded: authState.isLoaded,
    isSignedIn: authState.isSignedIn,
    isError: authState.isError,
    errorMessage: authState.errorMessage,
    isRetrying: authState.isRetrying,
    user,
    retryAuth,
    signOut,
  }
}

import * as csvParse from 'csv-parse/sync'
import { NextResponse } from 'next/server'

import logger from '../../lib/logging/logger'

// Security constants
export const MAX_FILE_SIZE = 10 * 1024 * 1024 // 10MB
export const VALID_FILENAME_REGEX = /^[\w\.-]+$/ // Only allow alphanumeric, underscore, dot, and hyphen
export const STREAMING_THRESHOLD = 5 * 1024 * 1024 // 5MB threshold for streaming

export interface CsvRecord {
  [key: string]: string | number | boolean | null
}

export interface CsvValidationResult {
  isValid: boolean
  records?: CsvRecord[]
  response?: NextResponse
}

/**
 * Validates a file for CSV upload
 * @param file The file to validate
 * @returns Validation result with error response if invalid
 */
export function validateCsvFile(file: File): { isValid: boolean; response?: NextResponse } {
  // Check if file exists
  if (!file) {
    return {
      isValid: false,
      response: NextResponse.json({ message: 'No file provided' }, { status: 400 }),
    }
  }

  // Validate filename to prevent path traversal attacks
  const fileName = file.name
  if (!fileName.match(VALID_FILENAME_REGEX)) {
    console.warn(`Rejected file with invalid filename: ${fileName}`)
    return {
      isValid: false,
      response: NextResponse.json(
        {
          message: 'Invalid filename',
          details: 'Filename can only contain letters, numbers, underscores, dots, and hyphens',
        },
        { status: 400 }
      ),
    }
  }

  // Validate file size
  if (file.size > MAX_FILE_SIZE) {
    console.warn(`Rejected oversized file: ${fileName} (${file.size} bytes)`)
    return {
      isValid: false,
      response: NextResponse.json(
        {
          message: 'File too large',
          details: `Maximum file size is ${MAX_FILE_SIZE / (1024 * 1024)}MB, received ${(
            file.size /
            (1024 * 1024)
          ).toFixed(2)}MB`,
        },
        { status: 400 }
      ),
    }
  }

  // Check file type
  if (!file.name.endsWith('.csv')) {
    console.warn(`Rejected non-CSV file: ${fileName} (${file.type})`)
    return {
      isValid: false,
      response: NextResponse.json(
        {
          message: 'Only CSV files are allowed',
          details: 'Please upload a file with .csv extension',
        },
        { status: 400 }
      ),
    }
  }

  // Verify the file is not empty
  if (file.size === 0) {
    console.warn(`Rejected empty file: ${fileName} (0 bytes)`)
    return {
      isValid: false,
      response: NextResponse.json(
        {
          message: 'File is empty',
          details: 'The uploaded file contains no data',
        },
        { status: 400 }
      ),
    }
  }

  // Basic size validation
  if (file.size < 10) {
    // Arbitrary minimum for a valid CSV with headers
    console.warn(`Rejected file with insufficient content: ${fileName} (${file.size} bytes)`)
    return {
      isValid: false,
      response: NextResponse.json(
        {
          message: 'Invalid CSV content',
          details: 'The file appears to be too small to be a valid CSV with headers',
        },
        { status: 400 }
      ),
    }
  }

  return { isValid: true }
}

/**
 * Parses a CSV file and returns the records
 * @param file The CSV file to parse
 * @returns Parsed records or error response
 */
export async function parseCsvFile(file: File): Promise<CsvValidationResult> {
  logger.info(`Starting to process CSV file: ${file.name} (${file.size} bytes)`)

  try {
    let records: CsvRecord[] = []

    // For files under the threshold, use the direct parsing approach
    if (file.size < STREAMING_THRESHOLD) {
      // Use the direct parsing approach for smaller files
      const fileBuffer = await file.arrayBuffer()
      const fileContent = new TextDecoder().decode(fileBuffer)

      // Parse CSV
      records = parseCsv(fileContent)
      logger.info(`Parsed ${records.length} records from CSV file using direct parsing`)
    } else {
      // For larger files, use a chunked processing approach
      records = await parseChunkedCsvFile(file)
    }

    // Log successful parsing
    logger.info(`Successfully parsed CSV with ${records.length} records from file: ${file.name}`)

    // Validate CSV structure
    if (records.length === 0) {
      logger.warn(`Rejected empty CSV file: ${file.name}`)
      return {
        isValid: false,
        response: NextResponse.json(
          { message: 'CSV file is empty. Please provide a file with at least one record.' },
          { status: 400 }
        ),
      }
    }

    return { isValid: true, records }
  } catch (error) {
    logger.error(`Error parsing CSV file: ${file.name}`, error as Error)
    return {
      isValid: false,
      response: NextResponse.json(
        {
          message: 'Failed to parse CSV file',
          details: error instanceof Error ? error.message : 'Unknown error',
        },
        { status: 400 }
      ),
    }
  }
}

/**
 * Parse a CSV string into records
 * @param content CSV content as string
 * @returns Parsed records
 */
export function parseCsv(content: string): CsvRecord[] {
  return csvParse.parse(content, {
    columns: true,
    skipEmptyLines: true,
    trim: true,
  })
}

/**
 * Validates that a CSV record has all required headers
 * @param record The first record from the CSV to check headers
 * @param requiredFields Array of required field names
 * @returns Array of missing field names (empty if all fields are present)
 */
export function validateCsvHeaders(record: CsvRecord, requiredFields: string[]): string[] {
  if (!record || typeof record !== 'object') {
    return requiredFields // All fields are missing if record is invalid
  }

  const missingFields: string[] = []

  for (const field of requiredFields) {
    if (!(field in record)) {
      missingFields.push(field)
    }
  }

  return missingFields
}

/**
 * Validates that all required fields exist in the CSV records
 * @param records The CSV records to validate
 * @param requiredFields Array of required field names
 * @returns Validation result with error response if invalid
 */
export function validateCsvRecords(
  records: CsvRecord[],
  requiredFields: string[]
): { isValid: boolean; response?: NextResponse } {
  // First, check if all required fields exist in the CSV structure (using first record)
  const firstRecord = records[0]

  // Log the first record structure for debugging (excluding sensitive data)
  logger.info('First record structure:', { fields: Object.keys(firstRecord) })

  // Check if all required fields exist in the CSV structure
  for (const field of requiredFields) {
    if (!(field in firstRecord)) {
      logger.warn(`CSV missing required field: ${field}`, {
        availableFields: Object.keys(firstRecord),
        requiredFields,
      })
      return {
        isValid: false,
        response: NextResponse.json(
          {
            message: `CSV is missing required field: ${field}`,
            availableFields: Object.keys(firstRecord),
            requiredFields: requiredFields,
          },
          { status: 400 }
        ),
      }
    }
  }

  // Now validate all records to ensure consistency throughout the file
  logger.info(`Validating all ${records.length} records for required fields...`)

  for (let i = 0; i < records.length; i++) {
    const record = records[i]

    for (const field of requiredFields) {
      if (
        !(field in record) ||
        record[field] === null ||
        record[field] === undefined ||
        record[field] === ''
      ) {
        logger.warn(`Record at index ${i} is missing required field "${field}"`, {
          recordIndex: i,
          fieldName: field,
        })
        return {
          isValid: false,
          response: NextResponse.json(
            {
              message: `Missing or empty required field "${field}" in record ${i + 1}`,
              details: `All records must contain non-empty values for fields: ${requiredFields.join(
                ', '
              )}`,
              recordIndex: i,
              fieldName: field,
            },
            { status: 400 }
          ),
        }
      }
    }

    // Validate price field is a valid number
    if ('price' in record && isNaN(parseInt(String(record.price), 10))) {
      logger.warn(`Record at index ${i} has invalid price value`, {
        recordIndex: i,
        priceValue: record.price,
      })
      return {
        isValid: false,
        response: NextResponse.json(
          {
            message: `Invalid price value in record ${i + 1}`,
            details: `Price must be a valid number, found: "${record.price}"`,
            recordIndex: i,
          },
          { status: 400 }
        ),
      }
    }
  }

  logger.info('All records successfully validated for required fields')
  return { isValid: true }
}

/**
 * Processes a large CSV file in chunks to avoid memory issues
 * @param file The large CSV file to process
 * @returns Array of parsed records
 */
async function parseChunkedCsvFile(file: File): Promise<CsvRecord[]> {
  logger.info(`Using chunked processing for large file: ${file.name} (${file.size} bytes)`)

  // Get the file as an ArrayBuffer but process it in chunks
  const fileBuffer = await file.arrayBuffer()
  const CHUNK_SIZE = 1024 * 1024 // 1MB chunks
  const decoder = new TextDecoder()

  let csvContent = ''
  let processedBytes = 0
  let records: CsvRecord[] = []

  // Process the first chunk to extract headers
  const firstChunkSize = Math.min(CHUNK_SIZE, fileBuffer.byteLength)
  const firstChunk = new Uint8Array(fileBuffer, 0, firstChunkSize)
  const firstChunkText = decoder.decode(firstChunk, { stream: true })

  // Extract the header row
  const headerEndIndex = firstChunkText.indexOf('\n')
  if (headerEndIndex === -1) {
    throw new Error('Could not find header row in CSV file')
  }

  // We only need to extract the header to validate the file structure
  const headerLine = firstChunkText.substring(0, headerEndIndex).trim()
  logger.debug('CSV header line extracted', { headerLine })

  csvContent = firstChunkText
  processedBytes = firstChunkSize

  // Process the rest of the file in chunks
  while (processedBytes < fileBuffer.byteLength) {
    const chunkSize = Math.min(CHUNK_SIZE, fileBuffer.byteLength - processedBytes)
    const chunk = new Uint8Array(fileBuffer, processedBytes, chunkSize)
    const chunkText = decoder.decode(chunk, {
      stream: processedBytes + chunkSize < fileBuffer.byteLength,
    })

    csvContent += chunkText
    processedBytes += chunkSize

    // Log progress for very large files
    if (processedBytes % (10 * CHUNK_SIZE) === 0) {
      logger.info(`Processed ${processedBytes} of ${fileBuffer.byteLength} bytes`, {
        processedBytes,
        totalBytes: fileBuffer.byteLength,
        percentComplete: Math.round((processedBytes / fileBuffer.byteLength) * 100),
      })
    }

    // If we've accumulated enough data, parse and clear the buffer
    if (csvContent.length > 5 * CHUNK_SIZE) {
      // Make sure we break at a newline to avoid splitting records
      const lastNewlineIndex = csvContent.lastIndexOf('\n')
      if (lastNewlineIndex !== -1) {
        const contentToParse = csvContent.substring(0, lastNewlineIndex + 1)
        csvContent = csvContent.substring(lastNewlineIndex + 1)

        // Parse this chunk of CSV data
        try {
          const chunkRecords = parseCsv(contentToParse)
          records = records.concat(chunkRecords)
          logger.info(`Parsed records from chunk`, {
            chunkRecords: chunkRecords.length,
            totalRecords: records.length,
          })
        } catch (chunkError) {
          logger.error('Error parsing CSV chunk', chunkError as Error)
          throw chunkError
        }
      }
    }
  }

  // Parse any remaining content
  if (csvContent.length > 0) {
    try {
      const finalRecords = parseCsv(csvContent)
      records = records.concat(finalRecords)
      logger.info(`Parsed records from final chunk`, {
        finalRecords: finalRecords.length,
        totalRecords: records.length,
      })
    } catch (finalError) {
      logger.error('Error parsing final CSV chunk', finalError as Error)
      throw finalError
    }
  }

  return records
}

// db/connection-manager.ts
import { drizzle } from 'drizzle-orm/postgres-js'
import postgres from 'postgres'

import * as schema from '../../drizzle/schema'

import { DatabaseConnectionError } from './error-handler'

// Connection pool status
type ConnectionStatus = 'INITIALIZING' | 'CONNECTED' | 'DISCONNECTED' | 'ERROR'

// Connection manager singleton
class ConnectionManager {
  private static instance: ConnectionManager
  private client: postgres.Sql<{}> | null = null
  private status: ConnectionStatus = 'INITIALIZING'
  private connectionError: Error | null = null
  private reconnectAttempts = 0
  private maxReconnectAttempts = 10
  private reconnectDelay = 1000 // Start with 1 second
  private reconnectTimer: NodeJS.Timeout | null = null
  private healthCheckInterval: NodeJS.Timeout | null = null

  private constructor() {
    // Private constructor to enforce singleton pattern
  }

  public static getInstance(): ConnectionManager {
    if (!ConnectionManager.instance) {
      ConnectionManager.instance = new ConnectionManager()
    }
    return ConnectionManager.instance
  }

  /**
   * Initialize the database connection
   * @param connectionString Database connection string
   * @param options Connection options
   */
  public async initialize(
    connectionString: string = process.env.DATABASE_URL || '',
    options: {
      ssl?: boolean
      max?: number
      idle_timeout?: number
      connect_timeout?: number
      max_lifetime?: number
      healthCheckIntervalMs?: number
    } = {}
  ): Promise<void> {
    if (!connectionString) {
      throw new DatabaseConnectionError('DATABASE_URL is not defined')
    }

    try {
      this.status = 'INITIALIZING'

      // Create postgres connection with connection pooling
      this.client = postgres(connectionString, {
        ssl: options.ssl ?? process.env.NODE_ENV === 'production',
        max: options.max ?? 10,
        idle_timeout: options.idle_timeout ?? 30,
        connect_timeout: options.connect_timeout ?? 10,
        max_lifetime: options.max_lifetime ?? 60 * 60,
        debug: process.env.NODE_ENV === 'development' ? console.log : undefined,
        onnotice: notice => console.log('Database notice:', notice),
        onclose: () => {
          console.log('Database connection closed')
          this.status = 'DISCONNECTED'
          this.scheduleReconnect()
        },
      })

      // Test the connection
      await this.client`SELECT 1`

      this.status = 'CONNECTED'
      this.connectionError = null
      this.reconnectAttempts = 0
      console.log('Database connection established successfully')

      // Start health check interval
      this.startHealthCheck(options.healthCheckIntervalMs ?? 30000)
    } catch (error) {
      this.status = 'ERROR'
      this.connectionError = error instanceof Error ? error : new Error(String(error))

      // Use detailed error message for better debugging
      console.error('Failed to initialize database connection:', error)

      // Schedule reconnect
      this.scheduleReconnect()

      // The DatabaseConnectionError constructor will use createDetailedDbConnectionErrorMessage
      // to generate a detailed error message that includes connection details
      throw new DatabaseConnectionError('Failed to initialize database connection', error)
    }
  }

  /**
   * Get the database client
   * @returns Drizzle ORM instance
   */
  public getDb() {
    if (!this.client || this.status !== 'CONNECTED') {
      throw new DatabaseConnectionError(
        'Database connection is not available',
        this.connectionError || undefined
      )
    }

    return drizzle(this.client, { schema })
  }

  /**
   * Get the raw postgres client
   * @returns Postgres client
   */
  public getRawClient() {
    if (!this.client || this.status !== 'CONNECTED') {
      throw new DatabaseConnectionError(
        'Database connection is not available',
        this.connectionError || undefined
      )
    }

    return this.client
  }

  /**
   * Get the current connection status
   * @returns Connection status
   */
  public getStatus(): ConnectionStatus {
    return this.status
  }

  /**
   * Get the last connection error
   * @returns Connection error or null
   */
  public getError(): Error | null {
    return this.connectionError
  }

  /**
   * Close the database connection
   */
  public async close(): Promise<void> {
    this.stopHealthCheck()

    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer)
      this.reconnectTimer = null
    }

    if (this.client) {
      try {
        await this.client.end()
        console.log('Database connection closed')
      } catch (error) {
        console.error('Error closing database connection:', error)
      } finally {
        this.client = null
        this.status = 'DISCONNECTED'
      }
    }
  }

  /**
   * Schedule a reconnection attempt
   */
  private scheduleReconnect(): void {
    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer)
    }

    if (this.reconnectAttempts >= this.maxReconnectAttempts) {
      console.error(`Max reconnect attempts (${this.maxReconnectAttempts}) reached. Giving up.`)
      return
    }

    const delay = this.reconnectDelay * Math.pow(2, this.reconnectAttempts)
    console.log(`Scheduling database reconnect attempt ${this.reconnectAttempts + 1} in ${delay}ms`)

    this.reconnectTimer = setTimeout(async () => {
      this.reconnectAttempts++

      try {
        console.log(
          `Attempting to reconnect to database (attempt ${this.reconnectAttempts}/${this.maxReconnectAttempts})`
        )
        await this.initialize()
        console.log('Database reconnection successful')
      } catch (error) {
        console.error('Database reconnection failed:', error)
        this.scheduleReconnect()
      }
    }, delay)
  }

  /**
   * Start periodic health checks
   * @param intervalMs Interval in milliseconds
   */
  private startHealthCheck(intervalMs: number): void {
    this.stopHealthCheck()

    this.healthCheckInterval = setInterval(async () => {
      if (this.status !== 'CONNECTED' || !this.client) {
        return
      }

      try {
        // Simple query to check connection
        await this.client`SELECT 1`
      } catch (error) {
        console.error('Database health check failed:', error)
        this.status = 'ERROR'
        this.connectionError = error instanceof Error ? error : new Error(String(error))

        // Try to close the connection and reconnect
        await this.close()
        this.scheduleReconnect()
      }
    }, intervalMs)
  }

  /**
   * Stop health checks
   */
  private stopHealthCheck(): void {
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval)
      this.healthCheckInterval = null
    }
  }
}

// Export the singleton instance
export const connectionManager = ConnectionManager.getInstance()

/**
 * Initialize the database connection
 * @param connectionString Database connection string
 * @param options Connection options
 */
export async function initializeDatabase(
  connectionString?: string,
  options?: {
    ssl?: boolean
    max?: number
    idle_timeout?: number
    connect_timeout?: number
    max_lifetime?: number
    healthCheckIntervalMs?: number
  }
): Promise<void> {
  await connectionManager.initialize(connectionString, options)
}

/**
 * Get the database client
 * @returns Drizzle ORM instance
 */
export function getDb() {
  return connectionManager.getDb()
}

/**
 * Close the database connection
 */
export async function closeDatabase(): Promise<void> {
  await connectionManager.close()
}

/**
 * Get the database connection status
 * @returns Connection status
 */
export function getDatabaseStatus(): {
  status: 'INITIALIZING' | 'CONNECTED' | 'DISCONNECTED' | 'ERROR'
  error: Error | null
} {
  return {
    status: connectionManager.getStatus(),
    error: connectionManager.getError(),
  }
}

/**
 * Database error handling utilities
 */

import { normalizeError } from '../error/error-utils'

/**
 * Error codes that might be present in database connection errors
 */
export enum DbErrorCode {
  CONNECTION_REFUSED = 'ECONNREFUSED',
  CONNECTION_RESET = 'ECONNRESET',
  CONNECTION_TIMEOUT = 'ETIMEDOUT',
  HOST_NOT_FOUND = 'ENOTFOUND',
  AUTHENTICATION_FAILED = 'AUTHENTICATION_FAILED',
  PERMISSION_DENIED = 'PERMISSION_DENIED',
  DATABASE_NOT_FOUND = 'DATABASE_NOT_FOUND',
}

/**
 * Interface for database error details
 */
export interface DbErrorDetails {
  message: string
  code?: string
  errno?: number
  sqlState?: string
  host?: string
  port?: number
  database?: string
  user?: string
  query?: string
  stack?: string
}

/**
 * Creates a detailed database connection error message
 * @param err The original error object
 * @returns A detailed error message string
 */
export function createDetailedDbConnectionErrorMessage(err: unknown): string {
  // Handle case where err is not an object
  if (!err || typeof err !== 'object') {
    return `Database connection error: ${normalizeError(err)}`
  }

  const errorObj = err as Record<string, any>
  const details: DbErrorDetails = {
    message: errorObj.message || 'Unknown error',
    code: errorObj.code,
    errno: errorObj.errno,
    sqlState: errorObj.sqlState,
    host: errorObj.host || errorObj.address,
    port: errorObj.port,
    database: errorObj.database,
    user: errorObj.user,
  }

  // Build a detailed error message
  let errorMessage = `Database connection error: ${details.message}`

  // Add error code if available
  if (details.code) {
    errorMessage += ` (Code: ${details.code})`
  }

  // Add connection details if available
  const connectionDetails = []
  if (details.host) connectionDetails.push(`Host: ${details.host}`)
  if (details.port) connectionDetails.push(`Port: ${details.port}`)
  if (details.database) connectionDetails.push(`Database: ${details.database}`)
  if (details.user) connectionDetails.push(`User: ${details.user}`)

  if (connectionDetails.length > 0) {
    errorMessage += ` | Connection details: ${connectionDetails.join(', ')}`
  }

  // Add SQL state if available (useful for SQL standard errors)
  if (details.sqlState) {
    errorMessage += ` | SQL State: ${details.sqlState}`
  }

  return errorMessage
}

/**
 * Creates a structured error object for database connection errors
 * @param err The original error object
 * @returns A structured error object with detailed information
 */
export function createDbConnectionErrorObject(
  err: unknown
): DbErrorDetails & { originalError: unknown } {
  // Handle case where err is not an object
  if (!err || typeof err !== 'object') {
    return {
      message: `Database connection error: ${normalizeError(err)}`,
      originalError: err,
    }
  }

  const errorObj = err as Record<string, any>
  const details: DbErrorDetails = {
    message: errorObj.message || 'Unknown error',
    code: errorObj.code,
    errno: errorObj.errno,
    sqlState: errorObj.sqlState,
    host: errorObj.host || errorObj.address,
    port: errorObj.port,
    database: errorObj.database,
    user: errorObj.user,
    stack: errorObj.stack,
  }

  // Add the original error for reference
  return {
    ...details,
    originalError: err,
  }
}

/**
 * Determines if an error is a specific type of database error
 * @param err The error to check
 * @param errorCode The error code to check for
 * @returns True if the error matches the specified code
 */
export function isDbErrorOfType(err: unknown, errorCode: DbErrorCode): boolean {
  if (!err || typeof err !== 'object') {
    return false
  }

  const errorObj = err as Record<string, any>
  return errorObj.code === errorCode
}

/**
 * Provides a user-friendly message for common database errors
 * @param err The database error
 * @returns A user-friendly error message
 */
export function getUserFriendlyDbErrorMessage(err: unknown): string {
  if (!err || typeof err !== 'object') {
    return 'Unable to connect to the database. Please try again later.'
  }

  const errorObj = err as Record<string, any>
  const code = errorObj.code

  switch (code) {
    case DbErrorCode.CONNECTION_REFUSED:
      return 'Database server is not accepting connections. Please check if the database server is running.'
    case DbErrorCode.CONNECTION_RESET:
      return 'Database connection was reset. This might be due to network issues or server restart.'
    case DbErrorCode.CONNECTION_TIMEOUT:
      return 'Database connection timed out. Please check network connectivity and server load.'
    case DbErrorCode.HOST_NOT_FOUND:
      return 'Database host not found. Please check the hostname and DNS configuration.'
    case DbErrorCode.AUTHENTICATION_FAILED:
      return 'Failed to authenticate with the database. Please check credentials.'
    case DbErrorCode.PERMISSION_DENIED:
      return 'Permission denied when connecting to database. Please check user permissions.'
    case DbErrorCode.DATABASE_NOT_FOUND:
      return 'The specified database was not found on the server.'
    default:
      return `Database error: ${errorObj.message || 'Unknown error'}`
  }
}

