import * as dotenv from 'dotenv'

import * as schema from '../../drizzle/schema'

import {
  connectionManager,
  getDb,
  initializeDatabase,
  closeDatabase,
  getDatabaseStatus,
} from './connection-manager'
import { DatabaseConnectionError } from './error-handler'

// Load environment variables
dotenv.config()

// Initialize the database connection
try {
  // This will be executed when this module is imported
  // We use a self-invoking async function to handle the async initialization
  ;(async () => {
    try {
      await initializeDatabase(process.env.DATABASE_URL, {
        ssl: process.env.NODE_ENV === 'production',
        max: 10, // Default pool size
        idle_timeout: 30, // 30 seconds
        connect_timeout: 10, // 10 seconds
        max_lifetime: 60 * 60, // 1 hour
        healthCheckIntervalMs: 30000, // 30 seconds
      })
    } catch (error) {
      console.error('Database initialization error:', error)
      // We don't throw here to allow the application to start
      // The connection manager will attempt to reconnect
    }
  })()
} catch (error) {
  console.error('Error in database initialization:', error)
}

// Export the database client
// This will throw an error if the connection is not available
// Consumers should handle this error appropriately
export const db = getDb()

// Export schema for convenience
export { schema }

// Export connection management functions
export { initializeDatabase, closeDatabase, getDatabaseStatus }

/**
 * Check if the database connection is healthy
 * @returns True if the connection is healthy, false otherwise
 */
export function isDatabaseHealthy(): boolean {
  const status = getDatabaseStatus()
  return status.status === 'CONNECTED'
}

/**
 * Get a database client with connection check
 * @param throwOnError Whether to throw an error if the connection is not available
 * @returns Database client or null if not available and throwOnError is false
 */
export function getDatabase(throwOnError = true) {
  try {
    return getDb()
  } catch (error) {
    if (throwOnError) {
      throw error
    }
    return null
  }
}

/**
 * Execute a function with a database connection
 * @param fn Function to execute with the database connection
 * @param retries Number of retries if the connection fails
 * @returns Result of the function
 */
export async function withDatabase<T>(
  fn: (db: ReturnType<typeof getDb>) => Promise<T>,
  retries = 3
): Promise<T> {
  let lastError: Error | null = null

  for (let attempt = 0; attempt <= retries; attempt++) {
    try {
      const database = getDb()
      return await fn(database)
    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error))

      // If it's not a connection error, just throw it
      if (!(error instanceof DatabaseConnectionError)) {
        throw error
      }

      // Don't retry if we've reached max retries
      if (attempt === retries) {
        break
      }

      // Wait before retrying
      const delay = 1000 * Math.pow(2, attempt)
      console.log(`Database connection failed, retrying in ${delay}ms...`)
      await new Promise(resolve => setTimeout(resolve, delay))
    }
  }

  throw (
    lastError || new DatabaseConnectionError('Failed to execute database operation after retries')
  )
}

// db/nextjs-utils.ts
import { NextRequest, NextResponse } from 'next/server'

import {
  DatabaseConnectionError,
  DatabaseQueryError,
  DatabaseConstraintError,
  DatabaseTimeoutError,
} from './error-handler'

import { isDatabaseHealthy, getDatabaseStatus, withDatabase } from './index'

/**
 * Type for API response
 */
type ApiResponse<T = any> = {
  success: boolean
  data?: T
  error?: {
    message: string
    code?: string
    details?: any
  }
}

/**
 * Create a successful API response
 * @param data The data to include in the response
 * @returns A NextResponse with standardized format
 */
export function successResponse<T>(data: T): NextResponse<ApiResponse<T>> {
  return NextResponse.json({
    success: true,
    data,
  })
}

/**
 * Create an error API response
 * @param message Error message
 * @param status HTTP status code
 * @param code Optional error code
 * @param details Optional error details
 * @returns A NextResponse with standardized format
 */
export function errorResponse(
  message: string,
  status: number = 500,
  code?: string,
  details?: any
): NextResponse<ApiResponse<never>> {
  return NextResponse.json(
    {
      success: false,
      error: {
        message,
        ...(code && { code }),
        ...(details && { details }),
      },
    },
    { status }
  )
}

/**
 * HTTP status codes for common API responses
 */
export const HttpStatus = {
  OK: 200,
  CREATED: 201,
  NO_CONTENT: 204,
  BAD_REQUEST: 400,
  UNAUTHORIZED: 401,
  FORBIDDEN: 403,
  NOT_FOUND: 404,
  METHOD_NOT_ALLOWED: 405,
  CONFLICT: 409,
  UNPROCESSABLE_ENTITY: 422,
  INTERNAL_SERVER_ERROR: 500,
  SERVICE_UNAVAILABLE: 503,
  GATEWAY_TIMEOUT: 504,
} as const

/**
 * Check database health before processing requests
 * @param req Next.js request
 * @returns NextResponse if database is unhealthy, null otherwise
 */
export function checkDatabaseHealth(req: NextRequest): NextResponse | null {
  // Skip health check endpoint
  if (req.nextUrl.pathname === '/api/health' || req.nextUrl.pathname.startsWith('/api/health/')) {
    return null
  }

  if (!isDatabaseHealthy()) {
    const status = getDatabaseStatus()

    // Return 503 Service Unavailable
    return errorResponse(
      'Database service is currently unavailable',
      HttpStatus.SERVICE_UNAVAILABLE,
      'DATABASE_UNAVAILABLE',
      {
        status: status.status,
        error: status.error ? status.error.message : null,
      }
    )
  }

  return null
}

/**
 * Handle database errors in Next.js API routes
 * @param error The error to handle
 * @returns NextResponse with appropriate error details
 */
export function handleDatabaseError(error: unknown): NextResponse {
  // Handle database connection errors
  if (error instanceof DatabaseConnectionError) {
    console.error('Database connection error:', error)
    return errorResponse(
      'Database connection error',
      HttpStatus.SERVICE_UNAVAILABLE,
      'DATABASE_CONNECTION_ERROR'
    )
  }

  // Handle database constraint errors
  if (error instanceof DatabaseConstraintError) {
    console.error('Database constraint error:', error)
    return errorResponse(
      'Database constraint violation',
      HttpStatus.BAD_REQUEST,
      'DATABASE_CONSTRAINT_ERROR',
      {
        constraint: error.constraint,
      }
    )
  }

  // Handle database timeout errors
  if (error instanceof DatabaseTimeoutError) {
    console.error('Database timeout error:', error)
    return errorResponse(
      'Database operation timed out',
      HttpStatus.GATEWAY_TIMEOUT,
      'DATABASE_TIMEOUT_ERROR'
    )
  }

  // Handle database query errors
  if (error instanceof DatabaseQueryError) {
    console.error('Database query error:', error)
    return errorResponse(
      'Database query error',
      HttpStatus.INTERNAL_SERVER_ERROR,
      'DATABASE_QUERY_ERROR'
    )
  }

  // Handle other errors
  console.error('Unhandled error:', error)
  return errorResponse(
    'An unexpected error occurred',
    HttpStatus.INTERNAL_SERVER_ERROR,
    'INTERNAL_SERVER_ERROR'
  )
}

/**
 * Higher-order function to wrap Next.js API route handlers with database error handling
 * @param handler The API route handler
 * @returns A wrapped handler with database error handling
 */
export function withDatabaseHandler(handler: (req: NextRequest) => Promise<NextResponse>) {
  return async (req: NextRequest) => {
    // Check database health
    const healthCheck = checkDatabaseHealth(req)
    if (healthCheck) {
      return healthCheck
    }

    try {
      // Execute the handler with database connection
      return await withDatabase(async () => {
        return await handler(req)
      })
    } catch (error) {
      return handleDatabaseError(error)
    }
  }
}

import dotenv from 'dotenv'
import { eq, and, isNull } from 'drizzle-orm'

import { generateEmbedding, generateEmbeddingBatch } from '../embedding'

import { db, schema } from './index'

// Load environment variables
dotenv.config()

// Get embedding provider from environment variables
const embeddingProvider = (process.env.EMBEDDING_PROVIDER as 'openai' | 'lmstudio') || 'lmstudio'
const embeddingModel = process.env.LLM_MODEL || 'bge-base-en'

/**
 * Generate a text representation of a property for embedding
 */
export function generatePropertyText(property: any): string {
  const parts = [
    property.address,
    `${property.bedrooms || ''} bedroom`,
    property.type || '',
    `${property.price || ''}`,
  ]

  return parts.filter(Boolean).join(', ')
}

/**
 * Process a single property to generate and store its embedding
 */
export async function processPropertyEmbedding(propertyId: string): Promise<void> {
  // Get the property from the database
  const properties = await db
    .select()
    .from(schema.property)
    .where(eq(schema.property.id, propertyId))

  if (properties.length === 0) {
    throw new Error(`Property not found: ${propertyId}`)
  }

  const property = properties[0]

  // Generate text representation
  const text = generatePropertyText(property)

  // Generate embedding
  const embedding = await generateEmbedding(text, {
    provider: embeddingProvider,
    model: embeddingModel,
  })

  // Update property with embedding
  // The vector column will be updated automatically by the trigger if it exists
  await db.update(schema.property).set({ embedding }).where(eq(schema.property.id, propertyId))

  console.log(`Generated embedding for property ${propertyId}`)
}

/**
 * Process all properties for a specific upload
 */
export async function processUploadEmbeddings(uploadId: string): Promise<void> {
  console.log(`Starting embedding generation for upload ${uploadId}`)

  try {
    // First, update the upload status to processing
    await db
      .update(schema.uploads)
      .set({
        status: 'processing_embeddings',
        updatedAt: new Date(),
      })
      .where(eq(schema.uploads.id, uploadId))

    // Get all properties for this upload that don't have embeddings yet
    const properties = await db
      .select()
      .from(schema.property)
      .where(and(eq(schema.property.uploadId, uploadId), isNull(schema.property.embedding)))

    if (properties.length === 0) {
      console.log(`No properties found for upload ${uploadId} that need embeddings`)

      // Update upload status to complete
      await db
        .update(schema.uploads)
        .set({
          status: 'complete',
          updatedAt: new Date(),
        })
        .where(eq(schema.uploads.id, uploadId))

      return
    }

    console.log(`Processing ${properties.length} properties for upload ${uploadId}`)

    // Process in batches to avoid potential memory issues with large uploads
    const batchSize = 100
    let processedCount = 0

    for (let i = 0; i < properties.length; i += batchSize) {
      const batch = properties.slice(i, i + batchSize)

      try {
        // Generate text representations for this batch
        const texts = batch.map(generatePropertyText)

        // Generate embeddings in batch
        const embeddings = await generateEmbeddingBatch(texts, {
          provider: embeddingProvider,
          model: embeddingModel,
        })

        // Update each property with its embedding
        for (let j = 0; j < batch.length; j++) {
          await db
            .update(schema.property)
            .set({ embedding: embeddings[j] })
            .where(eq(schema.property.id, batch[j].id))
        }

        processedCount += batch.length
        console.log(
          `Processed ${processedCount}/${properties.length} embeddings for upload ${uploadId}`
        )
      } catch (batchError) {
        console.error(`Error processing batch for upload ${uploadId}:`, {
          error: batchError instanceof Error ? batchError.message : 'Unknown error',
          stack: batchError instanceof Error ? batchError.stack : undefined,
          batchSize: batch.length,
          batchStart: i,
          uploadId,
        })

        // Continue with the next batch instead of failing the entire process
        // This way we can still process as many properties as possible
      }

      // Add a small delay between batches to avoid rate limits
      if (i + batchSize < properties.length) {
        await new Promise(resolve => setTimeout(resolve, 1000))
      }
    }

    console.log(
      `Completed embedding generation for upload ${uploadId}: ${processedCount}/${properties.length} successful`
    )

    // Update upload status based on success rate
    const status = processedCount === properties.length ? 'complete' : 'partial_embeddings'

    await db
      .update(schema.uploads)
      .set({
        status,
        updatedAt: new Date(),
        processingStats: JSON.stringify({
          total: properties.length,
          processed: processedCount,
          failed: properties.length - processedCount,
          completedAt: new Date().toISOString(),
        }),
      })
      .where(eq(schema.uploads.id, uploadId))
  } catch (error) {
    console.error(`Failed to process embeddings for upload ${uploadId}:`, {
      error: error instanceof Error ? error.message : 'Unknown error',
      stack: error instanceof Error ? error.stack : undefined,
      uploadId,
    })

    // Update upload status to failed
    try {
      await db
        .update(schema.uploads)
        .set({
          status: 'embedding_failed',
          updatedAt: new Date(),
          processingStats: JSON.stringify({
            error: error instanceof Error ? error.message : 'Unknown error',
            failedAt: new Date().toISOString(),
          }),
        })
        .where(eq(schema.uploads.id, uploadId))
    } catch (updateError) {
      console.error(`Failed to update status for upload ${uploadId}:`, updateError)
    }

    // Re-throw the error so the caller can handle it
    throw error
  }
}

/**
 * Process all properties in the database that don't have embeddings
 */
export async function processAllMissingEmbeddings(): Promise<void> {
  // Get all properties that don't have embeddings
  const properties = await db
    .select()
    .from(schema.property)
    .where(isNull(schema.property.embedding))

  if (properties.length === 0) {
    console.log('No properties found that need embeddings')
    return
  }

  console.log(`Processing ${properties.length} properties that need embeddings`)

  // Process in batches of 50
  const batchSize = 50
  for (let i = 0; i < properties.length; i += batchSize) {
    const batch = properties.slice(i, i + batchSize)

    // Generate text representations for this batch
    const texts = batch.map(generatePropertyText)

    // Generate embeddings in batch
    const embeddings = await generateEmbeddingBatch(texts, {
      provider: embeddingProvider,
      model: embeddingModel,
    })

    // Update each property with its embedding
    // The vector column will be updated automatically by the trigger if it exists
    for (let j = 0; j < batch.length; j++) {
      await db
        .update(schema.property)
        .set({ embedding: embeddings[j] })
        .where(eq(schema.property.id, batch[j].id))
    }

    console.log(
      `Processed batch ${i / batchSize + 1} of ${Math.ceil(properties.length / batchSize)}`
    )

    // Add a delay between batches to avoid rate limits
    if (i + batchSize < properties.length) {
      await new Promise(resolve => setTimeout(resolve, 2000))
    }
  }

  console.log(`Generated embeddings for ${properties.length} properties`)
}

import { eq, and, desc, sql } from 'drizzle-orm'
import { v4 as uuidv4 } from 'uuid'

import { withDatabaseErrorHandling, DatabaseQueryError } from './error-handler'
import { bindJsonbArray } from './utils'

import { db, schema } from './index'

// Types based on our schema
export type UploadRecord = typeof schema.uploadRecord.$inferSelect
export type NewUploadRecord = typeof schema.uploadRecord.$inferInsert

export type Property = typeof schema.property.$inferSelect
export type NewProperty = typeof schema.property.$inferInsert

// Upload Record Queries
export async function createUploadRecord(data: NewUploadRecord): Promise<UploadRecord> {
  return withDatabaseErrorHandling(
    async () => {
      // Validate required fields
      if (!data.uploaderId) {
        throw new Error('uploaderId is required')
      }

      const result = await db.insert(schema.uploadRecord).values(data).returning()

      if (!result.length) {
        throw new DatabaseQueryError('Failed to create upload record')
      }

      return result[0]
    },
    { query: 'createUploadRecord' }
  )
}

export async function getUploadRecordById(id: string): Promise<UploadRecord | undefined> {
  return withDatabaseErrorHandling(
    async () => {
      if (!id) {
        throw new Error('id is required')
      }

      const results = await db
        .select()
        .from(schema.uploadRecord)
        .where(eq(schema.uploadRecord.id, id))

      return results[0]
    },
    { query: 'getUploadRecordById', params: [id] }
  )
}

export async function getUploadRecordsByUploader(uploaderId: string): Promise<UploadRecord[]> {
  return withDatabaseErrorHandling(
    async () => {
      if (!uploaderId) {
        throw new Error('uploaderId is required')
      }

      return db
        .select()
        .from(schema.uploadRecord)
        .where(eq(schema.uploadRecord.uploaderId, uploaderId))
        .orderBy(desc(schema.uploadRecord.createdAt))
    },
    { query: 'getUploadRecordsByUploader', params: [uploaderId] }
  )
}

export async function updateUploadRecordStatus(
  id: string,
  status: string
): Promise<UploadRecord | undefined> {
  return withDatabaseErrorHandling(
    async () => {
      if (!id) {
        throw new Error('id is required')
      }

      if (!status) {
        throw new Error('status is required')
      }

      const results = await db
        .update(schema.uploadRecord)
        .set({ status })
        .where(eq(schema.uploadRecord.id, id))
        .returning()

      return results[0]
    },
    { query: 'updateUploadRecordStatus', params: [id, status] }
  )
}

// Property Queries
export async function createProperty(data: NewProperty): Promise<Property> {
  return withDatabaseErrorHandling(
    async () => {
      // Validate required fields
      if (!data.uploadId) {
        throw new Error('uploadId is required')
      }

      if (!data.address) {
        throw new Error('address is required')
      }

      const result = await db.insert(schema.property).values(data).returning()

      if (!result.length) {
        throw new DatabaseQueryError('Failed to create property')
      }

      return result[0]
    },
    { query: 'createProperty' }
  )
}

/**
 * Get a property by its ID
 * @param id The property ID
 * @returns The property if found, undefined otherwise
 */
export async function getPropertyById(id: string): Promise<Property | undefined> {
  return withDatabaseErrorHandling(
    async () => {
      if (!id) {
        throw new Error('id is required')
      }

      const results = await db.select().from(schema.property).where(eq(schema.property.id, id))

      if (!results.length) {
        return undefined
      }

      // Ensure the returned object conforms to the Property type
      const property = results[0]

      // Convert dates to ISO strings if they exist
      return {
        ...property,
        createdAt:
          property.createdAt instanceof Date
            ? property.createdAt.toISOString()
            : property.createdAt,
        updatedAt:
          property.updatedAt instanceof Date
            ? property.updatedAt.toISOString()
            : property.updatedAt,
      } as Property
    },
    { query: 'getPropertyById', params: [id] }
  )
}

/**
 * Get properties by upload ID
 * @param uploadId The upload ID
 * @returns Array of properties associated with the upload
 */
export async function getPropertiesByUploadId(uploadId: string): Promise<Property[]> {
  return withDatabaseErrorHandling(
    async () => {
      if (!uploadId) {
        throw new Error('uploadId is required')
      }

      const results = await db
        .select()
        .from(schema.property)
        .where(eq(schema.property.uploadId, uploadId))

      // Ensure all returned objects conform to the Property type
      return results.map(
        property =>
          ({
            ...property,
            createdAt:
              property.createdAt instanceof Date
                ? property.createdAt.toISOString()
                : property.createdAt,
            updatedAt:
              property.updatedAt instanceof Date
                ? property.updatedAt.toISOString()
                : property.updatedAt,
          }) as Property
      )
    },
    { query: 'getPropertiesByUploadId', params: [uploadId] }
  )
}

/**
 * Update a property by ID
 * @param id The property ID
 * @param data The data to update
 * @returns The updated property if found, undefined otherwise
 */
export async function updateProperty(
  id: string,
  data: Partial<NewProperty>
): Promise<Property | undefined> {
  return withDatabaseErrorHandling(
    async () => {
      if (!id) {
        throw new Error('id is required')
      }

      if (Object.keys(data).length === 0) {
        throw new Error('No data provided for update')
      }

      // Add updatedAt if not provided
      const updateData = {
        ...data,
        updatedAt: data.updatedAt ? new Date(data.updatedAt) : new Date(),
      }

      const results = await db
        .update(schema.property)
        .set(updateData)
        .where(eq(schema.property.id, id))
        .returning()

      if (!results.length) {
        return undefined
      }

      // Ensure the returned object conforms to the Property type
      const property = results[0]

      return {
        ...property,
        createdAt:
          property.createdAt instanceof Date
            ? property.createdAt.toISOString()
            : property.createdAt,
        updatedAt:
          property.updatedAt instanceof Date
            ? property.updatedAt.toISOString()
            : property.updatedAt,
      } as Property
    },
    { query: 'updateProperty', params: [id] }
  )
}

export async function deleteProperty(id: string): Promise<void> {
  return withDatabaseErrorHandling(
    async () => {
      if (!id) {
        throw new Error('id is required')
      }

      await db.delete(schema.property).where(eq(schema.property.id, id))
    },
    { query: 'deleteProperty', params: [id] }
  )
}

// Count properties by upload ID
export async function countPropertiesByUploadId(uploadId: string): Promise<number> {
  return withDatabaseErrorHandling(
    async () => {
      if (!uploadId) {
        throw new Error('uploadId is required')
      }

      const result = await db
        .select({ count: sql<number>`count(*)` })
        .from(schema.property)
        .where(eq(schema.property.uploadId, uploadId))

      return result[0].count
    },
    { query: 'countPropertiesByUploadId', params: [uploadId] }
  )
}

// Vector search for properties (if you have embeddings)
export async function searchPropertiesByEmbedding(
  embedding: number[],
  limit: number = 10
): Promise<Property[]> {
  return await prisma.$queryRaw`
    SELECT * FROM "Property"
    WHERE embedding IS NOT NULL
    ORDER BY embedding <-> ${JSON.stringify(embedding)}::vector
    LIMIT ${limit}
  `
}

/**
 * Execute a database transaction with proper error handling
 * @param operations Function containing the operations to execute in a transaction
 * @returns The result of the transaction
 */
export async function withTransaction<T>(operations: () => Promise<T>): Promise<T> {
  return withDatabaseErrorHandling(
    async () => {
      return await db.transaction(async tx => {
        try {
          return await operations()
        } catch (error) {
          // Ensure transaction is rolled back on error
          throw error
        }
      })
    },
    { query: 'withTransaction' }
  )
}

/**
 * Batch insert properties with error handling and transaction support
 * @param properties Array of properties to insert
 * @returns Array of inserted properties
 */
export async function batchInsertProperties(properties: NewProperty[]): Promise<Property[]> {
  return withTransaction(async () => {
    if (!properties.length) {
      return []
    }

    // Validate all properties have required fields
    properties.forEach((property, index) => {
      if (!property.uploadId) {
        throw new Error(`Property at index ${index} is missing uploadId`)
      }
      if (!property.address) {
        throw new Error(`Property at index ${index} is missing address`)
      }
    })

    const result = await db.insert(schema.property).values(properties).returning()

    return result
  })
}

