/**
 * Utility functions for safe error handling
 */

/**
 * Safely converts any error to a string representation
 * @param error Any type of error
 * @returns A string representation of the error
 */
export function normalizeError(error: unknown): string {
  return String(error)
}

/**
 * Safely extracts the message from any error type
 * @param error Any type of error
 * @returns The error message as a string
 */
export function getErrorMessage(error: unknown): string {
  if (error instanceof Error) {
    return error.message
  }
  return String(error)
}

/**
 * Safely extracts structured information from any error type
 * @param error Any type of error
 * @returns An object containing error details
 */
export function getErrorDetails(error: unknown): {
  message: string
  stack?: string
  code?: string
  name?: string
  status?: number
  type?: string
} {
  // Handle Error objects
  if (error instanceof Error) {
    const details: Record<string, any> = {
      message: error.message,
      name: error.name,
      stack: error.stack,
    }

    // Extract additional properties that might exist on error objects
    // Common properties in various error types
    const errorObj = error as any
    if ('code' in errorObj) details.code = String(errorObj.code)
    if ('status' in errorObj) details.status = Number(errorObj.status)
    if ('statusCode' in errorObj) details.status = Number(errorObj.statusCode)
    if ('type' in errorObj) details.type = String(errorObj.type)

    return details
  }

  // Handle non-Error objects
  if (error !== null && typeof error === 'object') {
    try {
      const errorObj = error as Record<string, any>
      return {
        message: errorObj.message ? String(errorObj.message) : JSON.stringify(error),
        ...(errorObj.code && { code: String(errorObj.code) }),
        ...(errorObj.status && { status: Number(errorObj.status) }),
        ...(errorObj.type && { type: String(errorObj.type) }),
      }
    } catch {
      return { message: String(error) }
    }
  }

  // Handle primitive values
  return { message: String(error) }
}

/**
 * Safely logs any type of error with consistent formatting
 * @param context Description of where the error occurred
 * @param error Any type of error
 */
export function logError(context: string, error: unknown): void {
  console.error(`[ERROR] ${context}:`, String(error))

  // Log additional details if available
  if (error instanceof Error && error.stack) {
    console.error(`[STACK] ${context}:`, error.stack)
  }
}

/**
 * Creates a new error with the original error information preserved
 * @param message New error message
 * @param originalError The original error that was caught
 * @returns A new Error object with combined information
 */
export function createErrorWithCause(message: string, originalError: unknown): Error {
  const newError = new Error(`${message}: ${String(originalError)}`)

  // Preserve the original stack if possible
  if (originalError instanceof Error && originalError.stack) {
    // Append original stack to the new error
    newError.stack = `${newError.stack}\nCaused by: ${originalError.stack}`
  }

  // Add a cause property (supported in newer JS environments)
  ;(newError as any).cause = originalError

  return newError
}

/**
 * Wraps an async function with standardized error handling
 * @param fn The async function to wrap
 * @param errorMessage Optional custom error message prefix
 * @returns A wrapped function with standardized error handling
 */
export function withErrorHandling<T, Args extends any[]>(
  fn: (...args: Args) => Promise<T>,
  errorMessage = 'Operation failed'
): (...args: Args) => Promise<T> {
  return async (...args: Args): Promise<T> => {
    try {
      return await fn(...args)
    } catch (error) {
      throw createErrorWithCause(errorMessage, error)
    }
  }
}

// lib/logging/advanced-winston-logger.ts
import { createLogger, format, transports } from 'winston'

import 'winston-daily-rotate-file'
import path from 'path'
import fs from 'fs'

// Create logs directory if it doesn't exist
const logsDir = path.join(process.cwd(), 'logs')
if (!fs.existsSync(logsDir)) {
  fs.mkdirSync(logsDir, { recursive: true })
}

/**
 * Sensitive fields that should never be logged
 * Add any field names that might contain sensitive information
 */
const SENSITIVE_FIELDS = [
  'password',
  'token',
  'secret',
  'authorization',
  'apiKey',
  'api_key',
  'key',
  'credential',
  'ssn',
  'socialSecurity',
  'creditCard',
  'cardNumber',
  'cvv',
]

/**
 * Sanitize data for logging by removing sensitive fields
 */
const sanitizeForLogging = format(info => {
  if (info.context) {
    const sanitized = { ...info.context }

    // Recursively sanitize objects
    const sanitizeObject = (obj: Record<string, any>): Record<string, any> => {
      const result: Record<string, any> = {}

      for (const [key, value] of Object.entries(obj)) {
        // Check if this is a sensitive field
        if (SENSITIVE_FIELDS.some(field => key.toLowerCase().includes(field.toLowerCase()))) {
          result[key] = '[REDACTED]'
        }
        // Recursively sanitize nested objects
        else if (value && typeof value === 'object' && !Array.isArray(value)) {
          result[key] = sanitizeObject(value)
        }
        // Sanitize arrays
        else if (Array.isArray(value)) {
          result[key] = value.map(item =>
            typeof item === 'object' && item !== null ? sanitizeObject(item) : item
          )
        }
        // Pass through other values
        else {
          result[key] = value
        }
      }

      return result
    }

    info.context = sanitizeObject(sanitized)
  }

  return info
})

/**
 * Custom format for console output
 * Colorizes the level and formats the message in a readable way
 */
const consoleFormat = format.combine(
  sanitizeForLogging(),
  format.colorize(),
  format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
  format.printf(({ level, message, timestamp, context, stack }) => {
    // Format the context
    let contextStr = ''
    if (context) {
      contextStr = `\n${JSON.stringify(context, null, 2)}`
    }

    // Include stack trace for errors if available
    const stackStr = stack ? `\n${stack}` : ''

    // Format the log message
    return `${timestamp} ${level}: ${message}${contextStr}${stackStr}`
  })
)

/**
 * Format for file output
 * Includes timestamp, level, message, and metadata in JSON format
 */
const fileFormat = format.combine(
  sanitizeForLogging(),
  format.timestamp(),
  format.errors({ stack: true }),
  format.json()
)

// Create daily rotate file transports
const combinedDailyRotateTransport = new transports.DailyRotateFile({
  filename: path.join(logsDir, 'combined-%DATE%.log'),
  datePattern: 'YYYY-MM-DD',
  zippedArchive: true,
  maxSize: '20m',
  maxFiles: '14d',
  format: fileFormat,
})

const errorDailyRotateTransport = new transports.DailyRotateFile({
  filename: path.join(logsDir, 'error-%DATE%.log'),
  datePattern: 'YYYY-MM-DD',
  zippedArchive: true,
  maxSize: '20m',
  maxFiles: '30d',
  level: 'error',
  format: fileFormat,
})

/**
 * Create the Winston logger
 */
export const advancedLogger = createLogger({
  level: process.env.LOG_LEVEL || 'info',
  defaultMeta: {
    service: 'property-portal',
    environment: process.env.NODE_ENV || 'development',
  },
  transports: [
    // Console transport with custom format
    new transports.Console({
      format: consoleFormat,
    }),

    // Daily rotate file transports
    combinedDailyRotateTransport,
    errorDailyRotateTransport,
  ],

  // Don't exit on handled exceptions
  exitOnError: false,
})

// Add event listeners for transport errors
combinedDailyRotateTransport.on('error', error => {
  console.error('Error in combined log transport:', error)
})

errorDailyRotateTransport.on('error', error => {
  console.error('Error in error log transport:', error)
})

// Add a stream for Morgan HTTP request logging
export const logStream = {
  write: (message: string) => {
    advancedLogger.http(message.trim())
  },
}

/**
 * Add request context to log entries
 * @param req Express request object
 * @returns Logger with request context
 */
export function loggerWithRequest(req: any) {
  return {
    debug: (message: string, meta: Record<string, any> = {}) =>
      advancedLogger.debug(message, {
        context: {
          ...meta,
          requestId: req.id,
          path: req.path,
          method: req.method,
        },
      }),

    info: (message: string, meta: Record<string, any> = {}) =>
      advancedLogger.info(message, {
        context: {
          ...meta,
          requestId: req.id,
          path: req.path,
          method: req.method,
        },
      }),

    warn: (message: string, meta: Record<string, any> = {}) =>
      advancedLogger.warn(message, {
        context: {
          ...meta,
          requestId: req.id,
          path: req.path,
          method: req.method,
        },
      }),

    error: (message: string, error?: Error, meta: Record<string, any> = {}) =>
      advancedLogger.error(message, {
        context: {
          ...meta,
          requestId: req.id,
          path: req.path,
          method: req.method,
        },
        stack: error?.stack,
      }),
  }
}

