// lib/monitoring/performance.ts
import logger from '@/lib/logging/logger'

/**
 * Performance monitoring utility
 *
 * This module provides utilities for monitoring performance metrics
 * and reporting them to the logging system.
 */

// Performance metric types
export enum MetricType {
  NAVIGATION = 'navigation',
  RESOURCE = 'resource',
  PAINT = 'paint',
  LAYOUT = 'layout',
  FIRST_INPUT = 'first-input',
  CUSTOM = 'custom',
}

// Performance metric interface
export interface PerformanceMetric {
  name: string
  type: MetricType
  value: number
  unit: 'ms' | 'bytes' | 'count'
  timestamp: number
}

/**
 * Initialize performance monitoring
 */
export function initPerformanceMonitoring() {
  if (typeof window === 'undefined' || !window.performance) {
    return
  }

  // Report navigation timing metrics
  reportNavigationTiming()

  // Set up performance observers
  setupPerformanceObservers()

  // Report metrics on page unload
  window.addEventListener('unload', () => {
    reportResourceTiming()
  })
}

/**
 * Report navigation timing metrics
 */
function reportNavigationTiming() {
  if (typeof window === 'undefined' || !window.performance) {
    return
  }

  // Wait for the page to be fully loaded
  window.addEventListener('load', () => {
    setTimeout(() => {
      const navigation = performance.getEntriesByType(
        'navigation'
      )[0] as PerformanceNavigationTiming

      if (!navigation) {
        return
      }

      const metrics: PerformanceMetric[] = [
        {
          name: 'time_to_first_byte',
          type: MetricType.NAVIGATION,
          value: navigation.responseStart - navigation.requestStart,
          unit: 'ms',
          timestamp: Date.now(),
        },
        {
          name: 'dom_load',
          type: MetricType.NAVIGATION,
          value: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
          unit: 'ms',
          timestamp: Date.now(),
        },
        {
          name: 'page_load',
          type: MetricType.NAVIGATION,
          value: navigation.loadEventEnd - navigation.loadEventStart,
          unit: 'ms',
          timestamp: Date.now(),
        },
        {
          name: 'total_page_load',
          type: MetricType.NAVIGATION,
          value: navigation.loadEventEnd - navigation.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        },
      ]

      // Log the metrics
      logger.info(
        'Navigation timing metrics',
        {
          metrics,
          url: window.location.href,
          pathname: window.location.pathname,
        },
        ['performance', 'navigation']
      )
    }, 0)
  })
}

/**
 * Report resource timing metrics
 */
function reportResourceTiming() {
  if (typeof window === 'undefined' || !window.performance) {
    return
  }

  const resources = performance.getEntriesByType('resource')

  if (!resources || resources.length === 0) {
    return
  }

  // Group resources by type
  const resourcesByType: Record<string, PerformanceResourceTiming[]> = {}

  resources.forEach(resource => {
    const resourceTiming = resource as PerformanceResourceTiming
    const url = new URL(resourceTiming.name)
    const fileExtension = url.pathname.split('.').pop() || 'unknown'

    if (!resourcesByType[fileExtension]) {
      resourcesByType[fileExtension] = []
    }

    resourcesByType[fileExtension].push(resourceTiming)
  })

  // Calculate metrics for each resource type
  const metrics: Record<
    string,
    {
      count: number
      totalSize: number
      totalDuration: number
      averageDuration: number
    }
  > = {}

  Object.entries(resourcesByType).forEach(([type, resources]) => {
    const totalSize = resources.reduce((sum, resource) => {
      return sum + (resource.transferSize || 0)
    }, 0)

    const totalDuration = resources.reduce((sum, resource) => {
      return sum + (resource.responseEnd - resource.startTime)
    }, 0)

    metrics[type] = {
      count: resources.length,
      totalSize,
      totalDuration,
      averageDuration: totalDuration / resources.length,
    }
  })

  // Log the metrics
  logger.info(
    'Resource timing metrics',
    {
      metrics,
      url: window.location.href,
      pathname: window.location.pathname,
    },
    ['performance', 'resource']
  )
}

/**
 * Set up performance observers
 */
function setupPerformanceObservers() {
  if (typeof window === 'undefined' || !window.PerformanceObserver) {
    return
  }

  // Observe paint metrics (FP, FCP)
  try {
    const paintObserver = new PerformanceObserver(entries => {
      entries.getEntries().forEach(entry => {
        const metric: PerformanceMetric = {
          name: entry.name,
          type: MetricType.PAINT,
          value: entry.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        }

        logger.info(
          `Paint metric: ${entry.name}`,
          {
            metric,
            url: window.location.href,
            pathname: window.location.pathname,
          },
          ['performance', 'paint']
        )
      })
    })

    paintObserver.observe({ entryTypes: ['paint'] })
  } catch (error) {
    logger.warn('Failed to observe paint metrics', { error })
  }

  // Observe layout shift metrics (CLS)
  try {
    let cumulativeLayoutShift = 0

    const layoutShiftObserver = new PerformanceObserver(entries => {
      entries.getEntries().forEach((entry: any) => {
        // Only count layout shifts without recent user input
        if (!entry.hadRecentInput) {
          cumulativeLayoutShift += entry.value
        }
      })

      const metric: PerformanceMetric = {
        name: 'cumulative_layout_shift',
        type: MetricType.LAYOUT,
        value: cumulativeLayoutShift,
        unit: 'count',
        timestamp: Date.now(),
      }

      logger.info(
        'Layout shift metric',
        {
          metric,
          url: window.location.href,
          pathname: window.location.pathname,
        },
        ['performance', 'layout']
      )
    })

    layoutShiftObserver.observe({ entryTypes: ['layout-shift'] })
  } catch (error) {
    logger.warn('Failed to observe layout shift metrics', { error })
  }

  // Observe first input delay (FID)
  try {
    const firstInputObserver = new PerformanceObserver(entries => {
      entries.getEntries().forEach(entry => {
        const metric: PerformanceMetric = {
          name: 'first_input_delay',
          type: MetricType.FIRST_INPUT,
          value: entry.processingStart - entry.startTime,
          unit: 'ms',
          timestamp: Date.now(),
        }

        logger.info(
          'First input delay metric',
          {
            metric,
            url: window.location.href,
            pathname: window.location.pathname,
          },
          ['performance', 'first-input']
        )
      })
    })

    firstInputObserver.observe({ entryTypes: ['first-input'] })
  } catch (error) {
    logger.warn('Failed to observe first input delay metrics', { error })
  }
}

/**
 * Measure the execution time of a function
 * @param fn Function to measure
 * @param name Name of the metric
 * @returns Result of the function
 */
export function measureExecutionTime<T>(fn: () => T, name: string): T {
  const start = performance.now()
  const result = fn()
  const duration = performance.now() - start

  const metric: PerformanceMetric = {
    name,
    type: MetricType.CUSTOM,
    value: duration,
    unit: 'ms',
    timestamp: Date.now(),
  }

  logger.debug(
    `Execution time for ${name}`,
    {
      metric,
      url: typeof window !== 'undefined' ? window.location.href : undefined,
      pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
    },
    ['performance', 'execution-time']
  )

  return result
}

/**
 * Measure the execution time of an async function
 * @param fn Async function to measure
 * @param name Name of the metric
 * @returns Promise that resolves to the result of the function
 */
export async function measureAsyncExecutionTime<T>(fn: () => Promise<T>, name: string): Promise<T> {
  const start = performance.now()

  try {
    const result = await fn()
    const duration = performance.now() - start

    const metric: PerformanceMetric = {
      name,
      type: MetricType.CUSTOM,
      value: duration,
      unit: 'ms',
      timestamp: Date.now(),
    }

    logger.debug(
      `Async execution time for ${name}`,
      {
        metric,
        url: typeof window !== 'undefined' ? window.location.href : undefined,
        pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
      },
      ['performance', 'execution-time']
    )

    return result
  } catch (error) {
    const duration = performance.now() - start

    const metric: PerformanceMetric = {
      name: `${name}_error`,
      type: MetricType.CUSTOM,
      value: duration,
      unit: 'ms',
      timestamp: Date.now(),
    }

    logger.warn(
      `Error in async execution for ${name}`,
      {
        metric,
        error,
        url: typeof window !== 'undefined' ? window.location.href : undefined,
        pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
      },
      ['performance', 'execution-time', 'error']
    )

    throw error
  }
}

/**
 * Create a performance monitoring hook for React components
 * @param componentName Name of the component
 * @returns Object with performance monitoring methods
 */
export function usePerformanceMonitoring(componentName: string) {
  const renderStart = performance.now()

  return {
    /**
     * Report component render time
     */
    reportRenderTime: () => {
      const renderTime = performance.now() - renderStart

      const metric: PerformanceMetric = {
        name: `${componentName}_render`,
        type: MetricType.CUSTOM,
        value: renderTime,
        unit: 'ms',
        timestamp: Date.now(),
      }

      logger.debug(
        `Render time for ${componentName}`,
        {
          metric,
          url: typeof window !== 'undefined' ? window.location.href : undefined,
          pathname: typeof window !== 'undefined' ? window.location.pathname : undefined,
        },
        ['performance', 'render-time']
      )
    },

    /**
     * Measure the execution time of a function
     * @param fn Function to measure
     * @param name Name of the metric
     * @returns Result of the function
     */
    measureExecutionTime: <T>(fn: () => T, name: string): T => {
      return measureExecutionTime(fn, `${componentName}_${name}`)
    },

    /**
     * Measure the execution time of an async function
     * @param fn Async function to measure
     * @param name Name of the metric
     * @returns Promise that resolves to the result of the function
     */
    measureAsyncExecutionTime: <T>(fn: () => Promise<T>, name: string): Promise<T> => {
      return measureAsyncExecutionTime(fn, `${componentName}_${name}`)
    },
  }
}

import { headers } from 'next/headers'
import { NextRequest, NextResponse } from 'next/server'

interface RateLimitConfig {
  // Maximum number of requests allowed within the interval
  limit: number
  // Time window in seconds
  interval: number
  // Optional identifier function to determine the rate limit key (defaults to IP address)
  identifierFn?: (req: NextRequest) => string
}

// In-memory store for rate limiting
// In production, you should use Redis or another distributed cache
const rateLimitStore = new Map<string, { count: number; resetTime: number }>()

// Clean up expired entries periodically
setInterval(() => {
  const now = Date.now()
  for (const [key, value] of rateLimitStore.entries()) {
    if (value.resetTime < now) {
      rateLimitStore.delete(key)
    }
  }
}, 60000) // Clean up every minute

/**
 * Rate limiting middleware for Next.js API routes
 */
export function rateLimit(config: RateLimitConfig) {
  const { limit, interval, identifierFn } = config

  return async (req: NextRequest): Promise<NextResponse | null> => {
    // Get identifier (default to IP address)
    const identifier = identifierFn
      ? identifierFn(req)
      : headers().get('x-forwarded-for') || 'unknown-ip'

    const now = Date.now()
    const windowKey = `${identifier}`

    // Get or initialize rate limit data for this identifier
    const rateData = rateLimitStore.get(windowKey) || {
      count: 0,
      resetTime: now + interval * 1000,
    }

    // If the reset time has passed, reset the counter
    if (rateData.resetTime < now) {
      rateData.count = 0
      rateData.resetTime = now + interval * 1000
    }

    // Increment request count
    rateData.count += 1
    rateLimitStore.set(windowKey, rateData)

    // Set rate limit headers
    const headers = new Headers()
    headers.set('X-RateLimit-Limit', limit.toString())
    headers.set('X-RateLimit-Remaining', Math.max(0, limit - rateData.count).toString())
    headers.set('X-RateLimit-Reset', Math.ceil(rateData.resetTime / 1000).toString())

    // If over limit, return 429 Too Many Requests
    if (rateData.count > limit) {
      return new NextResponse(
        JSON.stringify({
          error: 'Too many requests',
          message: 'Rate limit exceeded. Please try again later.',
        }),
        {
          status: 429,
          headers,
        }
      )
    }

    return null // Continue to the API handler
  }
}

/**
 * Apply rate limiting to a Next.js API route handler
 */
export function withRateLimit(
  handler: (req: NextRequest) => Promise<NextResponse> | NextResponse,
  config: RateLimitConfig
) {
  const rateLimiter = rateLimit(config)

  return async (req: NextRequest) => {
    // Apply rate limiting
    const rateLimitResponse = await rateLimiter(req)
    if (rateLimitResponse) {
      return rateLimitResponse
    }

    // Continue to the handler if rate limit not exceeded
    return handler(req)
  }
}

// lib/rate-limit/factory.ts
import { winstonLogger as logger } from '../logging/winston-logger'

import { redisRateLimit } from './redis'

import { RateLimitOptions, rateLimit } from './index'

/**
 * Create a rate limiter based on the environment
 * @param options Rate limit options
 * @returns Rate limiter instance
 */
export function createRateLimiter(options: RateLimitOptions) {
  // Use Redis in production, in-memory in development
  const useRedis = process.env.NODE_ENV === 'production' || process.env.USE_REDIS === 'true'

  if (useRedis) {
    logger.info('Using Redis-based rate limiter')
    return redisRateLimit(options)
  } else {
    logger.info('Using in-memory rate limiter')
    return rateLimit(options)
  }
}

/**
 * Create a rate limit middleware based on the environment
 * @param options Rate limit options
 * @returns Express middleware function
 */
export function createRateLimitMiddleware(options: RateLimitOptions) {
  const useRedis = process.env.NODE_ENV === 'production' || process.env.USE_REDIS === 'true'

  if (useRedis) {
    const { redisRateLimitMiddleware } = require('./redis')
    return redisRateLimitMiddleware(options)
  } else {
    const { rateLimitMiddleware } = require('./index')
    return rateLimitMiddleware(options)
  }
}

export default {
  createRateLimiter,
  createRateLimitMiddleware,
}

// lib/rate-limit/index.ts
import { winstonLogger as logger } from '../logging/winston-logger'

/**
 * Interface for rate limit options
 */
export interface RateLimitOptions {
  /**
   * Time window in milliseconds
   */
  interval: number

  /**
   * Maximum number of requests allowed in the interval
   */
  maxRequests: number

  /**
   * Optional prefix for the cache key
   */
  prefix?: string

  /**
   * Whether to log rate limit events
   */
  enableLogging?: boolean
}

/**
 * Interface for rate limit check result
 */
export interface RateLimitResult {
  /**
   * Whether the request is allowed
   */
  success: boolean

  /**
   * Number of remaining requests in the current interval
   */
  remaining: number

  /**
   * Time in milliseconds until the rate limit resets
   */
  resetIn: number

  /**
   * Total number of requests made in the current interval
   */
  total: number
}

/**
 * In-memory store for rate limit data
 */
const store = new Map<string, { count: number; timestamp: number }>()

/**
 * Cleanup old entries periodically
 */
const cleanupInterval = 10 * 60 * 1000 // 10 minutes
setInterval(() => {
  const now = Date.now()
  for (const [key, data] of store.entries()) {
    // Remove entries older than 1 hour
    if (now - data.timestamp > 60 * 60 * 1000) {
      store.delete(key)
    }
  }
}, cleanupInterval)

/**
 * Rate limiter class
 */
export class RateLimiter {
  private options: Required<RateLimitOptions>

  /**
   * Create a new rate limiter
   * @param options Rate limit options
   */
  constructor(options: RateLimitOptions) {
    this.options = {
      prefix: 'ratelimit:',
      enableLogging: true,
      ...options,
    }
  }

  /**
   * Check if a request is allowed
   * @param identifier Unique identifier for the client (e.g., IP address)
   * @returns Rate limit check result
   */
  async check(identifier: string): Promise<RateLimitResult> {
    const key = `${this.options.prefix}${identifier}`
    const now = Date.now()

    // Get current data or create new entry
    const current = store.get(key) || { count: 0, timestamp: now }

    // Check if the interval has passed and reset if needed
    if (now - current.timestamp > this.options.interval) {
      current.count = 0
      current.timestamp = now
    }

    // Increment the counter
    current.count++

    // Calculate remaining requests and reset time
    const remaining = Math.max(0, this.options.maxRequests - current.count)
    const resetIn = this.options.interval - (now - current.timestamp)
    const success = current.count <= this.options.maxRequests

    // Update the store
    store.set(key, current)

    // Log rate limit events if enabled
    if (this.options.enableLogging) {
      if (!success) {
        logger.warn('Rate limit exceeded', {
          context: {
            identifier,
            maxRequests: this.options.maxRequests,
            interval: this.options.interval,
            count: current.count,
          },
        })
      } else if (remaining < 5) {
        logger.info('Rate limit approaching', {
          context: {
            identifier,
            maxRequests: this.options.maxRequests,
            remaining,
            resetIn,
          },
        })
      }
    }

    return {
      success,
      remaining,
      resetIn,
      total: current.count,
    }
  }

  /**
   * Reset the rate limit for a specific identifier
   * @param identifier Unique identifier for the client
   */
  reset(identifier: string): void {
    const key = `${this.options.prefix}${identifier}`
    store.delete(key)

    if (this.options.enableLogging) {
      logger.info('Rate limit reset', {
        context: {
          identifier,
        },
      })
    }
  }
}

/**
 * Create a new rate limiter
 * @param options Rate limit options
 * @returns Rate limiter instance
 */
export function rateLimit(options: RateLimitOptions): RateLimiter {
  return new RateLimiter(options)
}

/**
 * Express middleware for rate limiting
 * @param options Rate limit options
 * @returns Express middleware function
 */
export function rateLimitMiddleware(options: RateLimitOptions) {
  const limiter = new RateLimiter(options)

  return async (req: any, res: any, next: any) => {
    // Get client identifier (IP address or user ID)
    const identifier =
      req.ip ||
      req.headers['x-forwarded-for'] ||
      req.headers['x-real-ip'] ||
      req.connection.remoteAddress ||
      'anonymous'

    // Check rate limit
    const result = await limiter.check(identifier)

    // Set rate limit headers
    res.setHeader('X-RateLimit-Limit', options.maxRequests.toString())
    res.setHeader('X-RateLimit-Remaining', result.remaining.toString())
    res.setHeader('X-RateLimit-Reset', Math.ceil(result.resetIn / 1000).toString())

    // If rate limit exceeded, return 429 Too Many Requests
    if (!result.success) {
      return res.status(429).json({
        error: {
          message: 'Too many requests, please try again later',
          details: {
            retryAfter: Math.ceil(result.resetIn / 1000),
          },
        },
      })
    }

    // Continue to the next middleware
    next()
  }
}

export default {
  rateLimit,
  rateLimitMiddleware,
}

