/**
 * Location Service
 *
 * Provides geocoding and location search functionality for UK addresses and postcodes
 * using the MapTiler Geocoding API.
 */

/**
 * Interface for location search results
 */
export interface LocationResult {
  label: string
  lat: number
  lng: number
}

/**
 * Autocomplete UK locations and postcodes
 *
 * @param query The search query (partial postcode, place name, etc.)
 * @returns Array of matching locations with coordinates
 */
export async function autocompleteUKLocation(query: string): Promise<LocationResult[]> {
  // Validate input
  if (!query || query.trim().length === 0) {
    return []
  }

  const MAPTILER_KEY = process.env.MAPTILER_API_KEY!

  // Validate API key
  if (!MAPTILER_KEY) {
    throw new Error('MAPTILER_API_KEY environment variable is not set')
  }

  try {
    const url = `https://api.maptiler.com/geocoding/${encodeURIComponent(
      query
    )}.json?key=${MAPTILER_KEY}&country=gb&language=en&limit=5`

    const res = await fetch(url)

    // Handle API errors
    if (!res.ok) {
      const errorData = await res.json().catch(() => null)
      throw new Error(`MapTiler API error: ${res.status} ${errorData?.message || res.statusText}`)
    }

    const json = await res.json()

    // Transform the response to our simplified format
    return json.features.map((f: any) => ({
      label: f.place_name,
      lat: f.center[1],
      lng: f.center[0],
    }))
  } catch (error) {
    console.error('Error in autocompleteUKLocation:', error)
    throw error
  }
}

/**
 * Get detailed information about a specific UK postcode
 *
 * @param postcode The full UK postcode
 * @returns Detailed location information
 */
export async function getPostcodeDetails(postcode: string): Promise<LocationResult | null> {
  const MAPTILER_KEY = process.env.MAPTILER_API_KEY!

  if (!MAPTILER_KEY) {
    throw new Error('MAPTILER_API_KEY environment variable is not set')
  }

  try {
    // Format the postcode by removing spaces and converting to uppercase
    const formattedPostcode = postcode.replace(/\s+/g, '').toUpperCase()

    const url = `https://api.maptiler.com/geocoding/${encodeURIComponent(
      formattedPostcode
    )}.json?key=${MAPTILER_KEY}&country=gb&language=en&limit=1`

    const res = await fetch(url)

    if (!res.ok) {
      const errorData = await res.json().catch(() => null)
      throw new Error(`MapTiler API error: ${res.status} ${errorData?.message || res.statusText}`)
    }

    const json = await res.json()

    // If no results found, return null
    if (!json.features || json.features.length === 0) {
      return null
    }

    const feature = json.features[0]

    return {
      label: feature.place_name,
      lat: feature.center[1],
      lng: feature.center[0],
    }
  } catch (error) {
    console.error('Error in getPostcodeDetails:', error)
    throw error
  }
}

/**
 * Error handling utilities for upload processing
 */
import { normalizeError } from '../error/error-utils'

/**
 * Interface for upload data
 */
export interface UploadData {
  id: string
  fileName?: string
  fileSize?: number
  uploaderId?: string
  status?: string
  createdAt?: Date
  updatedAt?: Date
  propertyCount?: number
  [key: string]: any // Allow for additional properties
}

/**
 * Interface for error context
 */
export interface ErrorContext {
  operation: string
  uploadId?: string
  fileName?: string
  additionalInfo?: Record<string, any>
}

/**
 * Safely logs upload processing errors with consistent formatting
 * @param error The error that occurred
 * @param context Context information about the operation
 * @param includeUploaderId Whether to include the uploader ID in logs (defaults to false for privacy)
 */
export function logUploadError(
  error: unknown,
  context: ErrorContext,
  includeUploaderId = false
): void {
  // Create a structured error object for logging
  const errorObj = {
    message: normalizeError(error),
    operation: context.operation,
    uploadId: context.uploadId || 'unknown',
    fileName: context.fileName || 'unknown',
    timestamp: new Date().toISOString(),
    ...context.additionalInfo,
  }

  // Remove sensitive information if not explicitly included
  if (!includeUploaderId && context.additionalInfo && 'uploaderId' in context.additionalInfo) {
    // Use type assertion or index notation to avoid TypeScript error
    delete (errorObj as Record<string, any>)['uploaderId']
  }

  // Log differently based on environment
  if (process.env.NODE_ENV === 'development') {
    // In development, include more details including the stack trace
    console.error(`[UPLOAD ERROR] ${context.operation}:`, errorObj, error)
  } else {
    // In production, log a structured object without the full error
    console.error(`[UPLOAD ERROR] ${context.operation}:`, JSON.stringify(errorObj))
  }
}

/**
 * Creates a safe fallback response when property count determination fails
 * @param upload The upload data object
 * @param error The error that occurred
 * @returns A sanitized upload object with fallback values
 */
export function createPropertyCountFallback(
  upload: UploadData | null | undefined,
  error: unknown
): UploadData {
  // Log the error with appropriate context
  logUploadError(error, {
    operation: 'getPropertyCount',
    uploadId: upload?.id,
    fileName: upload?.fileName,
    additionalInfo: {
      fileSize: upload?.fileSize,
      status: upload?.status,
    },
  })

  // Return a sanitized object with fallback values
  return {
    ...upload,
    id: upload?.id || 'unknown',
    propertyCount: 0,
    countError: true, // Flag to indicate the count is unreliable
    uploaderId: undefined, // Remove uploader ID for privacy
    errorMessage:
      process.env.NODE_ENV === 'development'
        ? normalizeError(error)
        : 'Failed to determine property count',
  }
}

/**
 * Creates a safe response for upload processing errors
 * @param upload The upload data object
 * @param error The error that occurred
 * @param operation The operation that failed
 * @returns A sanitized upload object with error information
 */
export function createUploadErrorResponse(
  upload: UploadData | null | undefined,
  error: unknown,
  operation: string
): UploadData & { error: boolean } {
  // Log the error with appropriate context
  logUploadError(error, {
    operation,
    uploadId: upload?.id,
    fileName: upload?.fileName,
  })

  // Return a sanitized object with error information
  return {
    ...upload,
    id: upload?.id || 'unknown',
    error: true,
    status: 'error',
    errorMessage:
      process.env.NODE_ENV === 'development'
        ? normalizeError(error)
        : `Failed to process upload: ${operation}`,
    uploaderId: undefined, // Remove uploader ID for privacy
  }
}

/**
 * Safely extracts upload information with privacy considerations
 * @param upload The upload data object
 * @param includePrivateData Whether to include private data
 * @returns A sanitized upload object
 */
export function sanitizeUploadData(
  upload: UploadData | null | undefined,
  includePrivateData = false
): Partial<UploadData> {
  if (!upload) {
    return { id: 'unknown' }
  }

  // Create a base object with non-sensitive data
  const sanitized: Partial<UploadData> = {
    id: upload.id,
    fileName: upload.fileName,
    fileSize: upload.fileSize,
    status: upload.status,
    createdAt: upload.createdAt,
    updatedAt: upload.updatedAt,
    propertyCount: upload.propertyCount,
  }

  // Include private data if explicitly requested
  if (includePrivateData) {
    sanitized.uploaderId = upload.uploaderId
    // Add other private fields as needed
  }

  return sanitized
}

import { NextResponse } from 'next/server'
import type { NextRequest } from 'next/server'

/**
 * Middleware to add security headers to all responses
 */
export function middleware(request: NextRequest) {
  // Get the response
  const response = NextResponse.next()

  // Add security headers
  const headers = response.headers

  // Content Security Policy
  headers.set(
    'Content-Security-Policy',
    "default-src 'self'; script-src 'self' 'unsafe-inline' 'unsafe-eval' https://cdn.jsdelivr.net https://unpkg.com; style-src 'self' 'unsafe-inline' https://cdn.jsdelivr.net https://fonts.googleapis.com; img-src 'self' data: https://*; font-src 'self' https://fonts.gstatic.com; connect-src 'self' https://api.mapbox.com https://events.mapbox.com https://*.clerk.accounts.dev https://api.clerk.dev; frame-src 'self'"
  )

  // Prevent MIME type sniffing
  headers.set('X-Content-Type-Options', 'nosniff')

  // Prevent clickjacking
  headers.set('X-Frame-Options', 'DENY')

  // Enable strict XSS protection
  headers.set('X-XSS-Protection', '1; mode=block')

  // Disable browser features
  headers.set(
    'Permissions-Policy',
    'camera=(), microphone=(), geolocation=(self), interest-cohort=()'
  )

  // Strict Transport Security
  headers.set('Strict-Transport-Security', 'max-age=63072000; includeSubDomains; preload')

  // Referrer Policy
  headers.set('Referrer-Policy', 'strict-origin-when-cross-origin')

  return response
}

/**
 * Configure which paths this middleware runs on
 */
export const config = {
  matcher: [
    /*
     * Match all request paths except:
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public folder
     */
    '/((?!_next/static|_next/image|favicon.ico|public/).*)',
  ],
}

