// Types based on our schema
export type UploadRecord = typeof schema.uploadRecord.$inferSelect;
export type NewUploadRecord = typeof schema.uploadRecord.$inferInsert;

export type Property = typeof schema.property.$inferSelect;
export type NewProperty = typeof schema.property.$inferInsert;

// Upload Record Queries
export async function createUploadRecord(data: NewUploadRecord): Promise<UploadRecord> {
  const result = await db.insert(schema.uploadRecord).values(data).returning();

  return result[0];
}

export async function getUploadRecordById(id: string): Promise<UploadRecord | undefined> {
  const results = await db.select().from(schema.uploadRecord).where(eq(schema.uploadRecord.id, id));

  return results[0];
}

export async function getUploadRecordsByUploader(uploaderId: string): Promise<UploadRecord[]> {
  return db
    .select()
    .from(schema.uploadRecord)
    .where(eq(schema.uploadRecord.uploaderId, uploaderId))
    .orderBy(desc(schema.uploadRecord.createdAt));
}

export async function updateUploadRecordStatus(
  id: string,
  status: string
): Promise<UploadRecord | undefined> {
  const results = await db
    .update(schema.uploadRecord)
    .set({ status })
    .where(eq(schema.uploadRecord.id, id))
    .returning();

  return results[0];
}

// Property Queries
export async function createProperty(data: NewProperty): Promise<Property> {
  const result = await db.insert(schema.property).values(data).returning();

  return result[0];
}

export async function getPropertyById(id: string): Promise<Property | undefined> {
  const results = await db.select().from(schema.property).where(eq(schema.property.id, id));

  return results[0];
}

export async function getPropertiesByUploadId(uploadId: string): Promise<Property[]> {
  return db.select().from(schema.property).where(eq(schema.property.uploadId, uploadId));
}

export async function updateProperty(
  id: string,
  data: Partial<NewProperty>
): Promise<Property | undefined> {
  const results = await db
    .update(schema.property)
    .set(data)
    .where(eq(schema.property.id, id))
    .returning();

  return results[0];
}

export async function deleteProperty(id: string): Promise<void> {
  await db.delete(schema.property).where(eq(schema.property.id, id));
}

// Count properties by upload ID
export async function countPropertiesByUploadId(uploadId: string): Promise<number> {
  const result = await db
    .select({ count: sql<number>`count(*)` })
    .from(schema.property)
    .where(eq(schema.property.uploadId, uploadId));

  return result[0].count;
}

// Vector search for properties (if you have embeddings)
export async function searchPropertiesByEmbedding(
  embedding: number[],
  limit: number = 10
): Promise<Property[]> {
  // This uses PostgreSQL's vector operations to find similar properties
  // Note: This requires the pgvector extension to be installed in your database
  return db
    .select()
    .from(schema.property)
    .where(sql`embedding is not null`)
    .orderBy(sql`embedding <-> ${JSON.stringify(embedding)}::jsonb`)
    .limit(limit);
}

import axios from 'axios';
import dotenv from 'dotenv';
import OpenAI from 'openai';

// Load environment variables
dotenv.config();

// Define embedding model options
export type EmbeddingProvider = 'openai' | 'lmstudio';

// Interface for embedding options
interface EmbeddingOptions {
  provider?: EmbeddingProvider;
  model?: string;
  apiKey?: string;
  apiUrl?: string;
}

// Default options
const defaultOptions: EmbeddingOptions = {
  provider: 'openai',
  model: 'text-embedding-3-small',
  apiKey: process.env.OPENAI_API_KEY,
  apiUrl: process.env.LLM_API_URL || 'http://localhost:11434/api/embeddings',
};

/**
 * Generate embeddings for a text using either OpenAI or LM Studio
 * @param text The text to generate embeddings for
 * @param options Configuration options
 * @returns An array of numbers representing the embedding vector
 */
export async function generateEmbedding(
  text: string,
  options: EmbeddingOptions = {}
): Promise<number[]> {
  // Merge default options with provided options
  const config = { ...defaultOptions, ...options };

  // Validate input
  if (!text || text.trim() === '') {
    throw new Error('Text cannot be empty');
  }

  try {
    // Use OpenAI API
    if (config.provider === 'openai') {
      return await generateOpenAIEmbedding(text, config);
    }
    // Use LM Studio API
    else if (config.provider === 'lmstudio') {
      return await generateLMStudioEmbedding(text, config);
    } else {
      throw new Error(`Unsupported provider: ${config.provider}`);
    }
  } catch (error) {
    console.error('Error generating embedding:', error);
    throw error;
  }
}

/**
 * Generate embeddings using OpenAI API
 */
async function generateOpenAIEmbedding(text: string, options: EmbeddingOptions): Promise<number[]> {
  if (!options.apiKey) {
    throw new Error('OpenAI API key is required');
  }

  const openai = new OpenAI({
    apiKey: options.apiKey,
  });

  const response = await openai.embeddings.create({
    model: options.model || 'text-embedding-3-small',
    input: text,
    encoding_format: 'float',
  });

  return response.data[0].embedding;
}

/**
 * Generate embeddings using LM Studio API
 */
async function generateLMStudioEmbedding(
  text: string,
  options: EmbeddingOptions
): Promise<number[]> {
  if (!options.apiUrl) {
    throw new Error('LM Studio API URL is required');
  }

  const response = await axios.post(
    options.apiUrl,
    {
      model: options.model || 'bge-base-en',
      prompt: text,
    },
    {
      headers: {
        'Content-Type': 'application/json',
      },
    }
  );

  if (response.data && response.data.embedding) {
    return response.data.embedding;
  } else {
    throw new Error('Invalid response from LM Studio API');
  }
}

/**
 * Generate embeddings for multiple texts in batch
 * @param texts Array of texts to generate embeddings for
 * @param options Configuration options
 * @returns Array of embedding vectors
 */
export async function generateEmbeddingBatch(
  texts: string[],
  options: EmbeddingOptions = {}
): Promise<number[][]> {
  // Process in batches to avoid rate limits
  const batchSize = 20;
  const results: number[][] = [];

  for (let i = 0; i < texts.length; i += batchSize) {
    const batch = texts.slice(i, i + batchSize);
    const promises = batch.map((text) => generateEmbedding(text, options));
    const batchResults = await Promise.all(promises);
    results.push(...batchResults);

    // Add a small delay between batches to avoid rate limits
    if (i + batchSize < texts.length) {
      await new Promise((resolve) => setTimeout(resolve, 1000));
    }
  }

  return results;
}

/**
 * Calculate cosine similarity between two vectors
 * @param a First vector
 * @param b Second vector
 * @returns Similarity score between -1 and 1
 */
export function cosineSimilarity(a: number[], b: number[]): number {
  if (a.length !== b.length) {
    throw new Error('Vectors must have the same length');
  }

  let dotProduct = 0;
  let normA = 0;
  let normB = 0;

  for (let i = 0; i < a.length; i++) {
    dotProduct += a[i] * b[i];
    normA += a[i] * a[i];
    normB += b[i] * b[i];
  }

  if (normA === 0 || normB === 0) {
    return 0;
  }

  return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
}

// lib/logging/logger.ts
/**
 * Structured logging utility for the application
 *
 * This module provides a consistent interface for logging across the application.
 * It supports different log levels, structured metadata, and can be configured
 * to send logs to external services like Sentry or LogRocket.
 */

// Log levels in order of severity
export enum LogLevel {
  DEBUG = 'debug',
  INFO = 'info',
  WARN = 'warn',
  ERROR = 'error',
  FATAL = 'fatal',
}

// Log entry interface
export interface LogEntry {
  level: LogLevel;
  message: string;
  timestamp: string;
  context?: Record<string, any>;
  tags?: string[];
  error?: Error;
}

// Logger configuration interface
export interface LoggerConfig {
  minLevel: LogLevel;
  enableConsole: boolean;
  sentryDsn?: string;
  logRocketAppId?: string;
  environment: 'development' | 'test' | 'production';
  release?: string;
  userId?: string;
  userEmail?: string;
}

// Default configuration
const defaultConfig: LoggerConfig = {
  minLevel: LogLevel.DEBUG,
  enableConsole: true,
  environment: (process.env.NODE_ENV as 'development' | 'test' | 'production') || 'development',
  release: process.env.NEXT_PUBLIC_APP_VERSION,
};

// Global logger configuration
let loggerConfig: LoggerConfig = { ...defaultConfig };

// External logging services
let sentryInitialized = false;
let logRocketInitialized = false;

/**
 * Initialize Sentry for error tracking
 * This is done lazily to avoid importing Sentry in environments where it's not needed
 */
async function initSentry(dsn: string, config: LoggerConfig) {
  if (sentryInitialized) return;

  try {
    // Dynamically import Sentry to avoid bundling it unnecessarily
    const Sentry = await import('@sentry/nextjs');

    Sentry.init({
      dsn,
      environment: config.environment,
      release: config.release,
      tracesSampleRate: config.environment === 'production' ? 0.2 : 1.0,
      // Only send errors and above to Sentry
      beforeSend(event) {
        if (event.level && ['error', 'fatal'].includes(event.level)) {
          return event;
        }
        return null;
      },
    });

    // Set user information if available
    if (config.userId || config.userEmail) {
      Sentry.setUser({
        id: config.userId,
        email: config.userEmail,
      });
    }

    sentryInitialized = true;

    return Sentry;
  } catch (error) {
    console.error('Failed to initialize Sentry:', error);
    return null;
  }
}

/**
 * Initialize LogRocket for session replay
 * This is done lazily to avoid importing LogRocket in environments where it's not needed
 */
async function initLogRocket(appId: string, config: LoggerConfig) {
  if (logRocketInitialized) return;

  try {
    // Dynamically import LogRocket to avoid bundling it unnecessarily
    const LogRocket = (await import('logrocket')).default;

    LogRocket.init(appId, {
      release: config.release,
      console: {
        isEnabled: true,
        methods: ['error', 'warn'],
      },
      network: {
        isEnabled: true,
        requestSanitizer: (request) => {
          // Don't log request bodies for sensitive endpoints
          if (request.url.includes('/api/auth') || request.url.includes('/api/user')) {
            request.body = null;
          }
          return request;
        },
        responseSanitizer: (response) => {
          // Don't log response bodies for sensitive endpoints
          if (response.url.includes('/api/auth') || response.url.includes('/api/user')) {
            response.body = null;
          }
          return response;
        },
      },
    });

    // Set user information if available
    if (config.userId || config.userEmail) {
      LogRocket.identify(config.userId || 'anonymous', {
        email: config.userEmail,
      });
    }

    // Connect LogRocket with Sentry if both are enabled
    if (sentryInitialized) {
      const Sentry = await import('@sentry/nextjs');
      LogRocket.getSessionURL((sessionURL) => {
        Sentry.configureScope((scope) => {
          scope.setExtra('logRocketSessionURL', sessionURL);
        });
      });
    }

    logRocketInitialized = true;

    return LogRocket;
  } catch (error) {
    console.error('Failed to initialize LogRocket:', error);
    return null;
  }
}

/**
 * Configure the logger
 * @param config Logger configuration
 */
export function configureLogger(config: Partial<LoggerConfig>) {
  loggerConfig = { ...loggerConfig, ...config };

  // Initialize external logging services if configured
  if (loggerConfig.sentryDsn && typeof window !== 'undefined') {
    initSentry(loggerConfig.sentryDsn, loggerConfig);
  }

  if (loggerConfig.logRocketAppId && typeof window !== 'undefined') {
    initLogRocket(loggerConfig.logRocketAppId, loggerConfig);
  }
}

/**
 * Set user information for logging
 * @param userId User ID
 * @param userEmail User email
 */
export async function setLogUser(userId?: string, userEmail?: string) {
  loggerConfig.userId = userId;
  loggerConfig.userEmail = userEmail;

  // Update user information in external services
  if (sentryInitialized) {
    const Sentry = await import('@sentry/nextjs');
    Sentry.setUser({
      id: userId,
      email: userEmail,
    });
  }

  if (logRocketInitialized && typeof window !== 'undefined') {
    const LogRocket = (await import('logrocket')).default;
    LogRocket.identify(userId || 'anonymous', {
      email: userEmail,
    });
  }
}

/**
 * Create a log entry
 * @param level Log level
 * @param message Log message
 * @param context Additional context
 * @param tags Tags for categorizing logs
 * @param error Error object
 * @returns Log entry
 */
function createLogEntry(
  level: LogLevel,
  message: string,
  context?: Record<string, any>,
  tags?: string[],
  error?: Error
): LogEntry {
  return {
    level,
    message,
    timestamp: new Date().toISOString(),
    context,
    tags,
    error,
  };
}

/**
 * Log to the console with appropriate formatting
 * @param entry Log entry
 */
function logToConsole(entry: LogEntry) {
  if (!loggerConfig.enableConsole) return;

  const { level, message, timestamp, context, tags, error } = entry;

  // Format the log message
  const formattedMessage = `[${timestamp}] [${level.toUpperCase()}] ${message}`;

  // Log with appropriate console method
  switch (level) {
    case LogLevel.DEBUG:
      console.debug(formattedMessage, { context, tags });
      break;
    case LogLevel.INFO:
      console.info(formattedMessage, { context, tags });
      break;
    case LogLevel.WARN:
      console.warn(formattedMessage, { context, tags });
      break;
    case LogLevel.ERROR:
    case LogLevel.FATAL:
      console.error(formattedMessage, { context, tags, error });
      break;
  }
}

/**
 * Send log to external services
 * @param entry Log entry
 */
async function logToExternalServices(entry: LogEntry) {
  const { level, message, context, tags, error } = entry;

  // Send to Sentry if configured and level is ERROR or FATAL
  if (
    loggerConfig.sentryDsn &&
    sentryInitialized &&
    (level === LogLevel.ERROR || level === LogLevel.FATAL)
  ) {
    try {
      const Sentry = await import('@sentry/nextjs');

      // Set extra context
      Sentry.configureScope((scope) => {
        if (context) {
          Object.entries(context).forEach(([key, value]) => {
            scope.setExtra(key, value);
          });
        }

        if (tags) {
          tags.forEach((tag) => {
            scope.setTag(tag, 'true');
          });
        }
      });

      // Capture the error or message
      if (error) {
        Sentry.captureException(error);
      } else {
        Sentry.captureMessage(message, level as any);
      }
    } catch (err) {
      console.error('Failed to log to Sentry:', err);
    }
  }

  // Send to LogRocket if configured
  if (loggerConfig.logRocketAppId && logRocketInitialized) {
    try {
      const LogRocket = (await import('logrocket')).default;

      // Log the message
      if (level === LogLevel.ERROR || level === LogLevel.FATAL) {
        LogRocket.captureException(error || new Error(message), {
          tags,
          extra: context,
        });
      } else if (level === LogLevel.WARN) {
        LogRocket.warn(message, context);
      } else {
        LogRocket.log(message, context);
      }
    } catch (err) {
      console.error('Failed to log to LogRocket:', err);
    }
  }
}

/**
 * Log a message
 * @param level Log level
 * @param message Log message
 * @param context Additional context
 * @param tags Tags for categorizing logs
 * @param error Error object
 */
async function log(
  level: LogLevel,
  message: string,
  context?: Record<string, any>,
  tags?: string[],
  error?: Error
) {
  // Skip if below minimum log level
  if (
    (level === LogLevel.DEBUG && loggerConfig.minLevel !== LogLevel.DEBUG) ||
    (level === LogLevel.INFO &&
      [LogLevel.WARN, LogLevel.ERROR, LogLevel.FATAL].includes(loggerConfig.minLevel)) ||
    (level === LogLevel.WARN && [LogLevel.ERROR, LogLevel.FATAL].includes(loggerConfig.minLevel)) ||
    (level === LogLevel.ERROR && loggerConfig.minLevel === LogLevel.FATAL)
  ) {
    return;
  }

  // Create log entry
  const entry = createLogEntry(level, message, context, tags, error);

  // Log to console
  logToConsole(entry);

  // Log to external services
  await logToExternalServices(entry);
}

/**
 * Log a debug message
 * @param message Log message
 * @param context Additional context
 * @param tags Tags for categorizing logs
 */
export function debug(message: string, context?: Record<string, any>, tags?: string[]) {
  log(LogLevel.DEBUG, message, context, tags);
}

/**
 * Log an info message
 * @param message Log message
 * @param context Additional context
 * @param tags Tags for categorizing logs
 */
export function info(message: string, context?: Record<string, any>, tags?: string[]) {
  log(LogLevel.INFO, message, context, tags);
}

/**
 * Log a warning message
 * @param message Log message
 * @param context Additional context
 * @param tags Tags for categorizing logs
 */
export function warn(message: string, context?: Record<string, any>, tags?: string[]) {
  log(LogLevel.WARN, message, context, tags);
}

/**
 * Log an error message
 * @param message Log message
 * @param error Error object
 * @param context Additional context
 * @param tags Tags for categorizing logs
 */
export function error(
  message: string,
  error?: Error,
  context?: Record<string, any>,
  tags?: string[]
) {
  log(LogLevel.ERROR, message, context, tags, error);
}

/**
 * Log a fatal error message
 * @param message Log message
 * @param error Error object
 * @param context Additional context
 * @param tags Tags for categorizing logs
 */
export function fatal(
  message: string,
  error?: Error,
  context?: Record<string, any>,
  tags?: string[]
) {
  log(LogLevel.FATAL, message, context, tags, error);
}

// Export a default logger object
const logger = {
  debug,
  info,
  warn,
  error,
  fatal,
  configureLogger,
  setLogUser,
};

export default logger;

