  error?: {
    message: string;
    code?: string;
    details?: any;
  };
};

// Dummy database or service
const itemsDb: CreateItemInput[] = [];

export default async function handler(req: NextApiRequest, res: NextApiResponse<ApiResponse>) {
  if (req.method === 'POST') {
    try {
      // 1. Validate the request body
      const validationResult = createItemSchema.safeParse(req.body);

      if (!validationResult.success) {
        // If validation fails, return 422 with error messages
        return res.status(422).json({
          success: false,
          error: {
            message: 'Validation error',
            code: 'VALIDATION_ERROR',
            details: validationResult.error.flatten().fieldErrors,
          },
        });
      }

      // 2. Use the validated data (it's now type-safe!)
      const validatedData: CreateItemInput = validationResult.data;

      // Simulate saving to database
      console.log('Received valid data:', validatedData);
      itemsDb.push(validatedData); // In a real app, you'd interact with Drizzle ORM here

      // Return standardized success response
      return res.status(201).json({
        success: true,
        data: {
          message: 'Item created successfully',
          item: validatedData,
        },
      });
    } catch (error) {
      // Catch any other unexpected errors during processing
      console.error('Error creating item:', error);
      return res.status(500).json({
        success: false,
        error: {
          message: 'Internal Server Error',
          code: 'INTERNAL_ERROR',
        },
      });
    }
  } else {
    // Method not allowed - standardized error response
    res.setHeader('Allow', ['POST']);
    return res.status(405).json({
      success: false,
      error: {
        message: `Method ${req.method} Not Allowed`,
        code: 'METHOD_NOT_ALLOWED',
      },
    });
  }
}

/**
 * Script to apply database optimizations
 *
 * This script:
 * 1. Applies the latest Prisma migrations
 * 2. Verifies the HNSW index was created correctly
 * 3. Tests the connection pool
 */

const { execSync } = require('child_process')

const { PrismaClient } = require('@prisma/client')
const { Pool } = require('pg')

// Load environment variables
require('dotenv').config()

const DATABASE_URL = process.env.DATABASE_URL
const POOL_SIZE = parseInt(process.env.POOL_SIZE || '10', 10)

// Create a connection pool
const pool = new Pool({
  connectionString: DATABASE_URL,
  max: POOL_SIZE,
  ssl: {
    rejectUnauthorized: false,
  },
})

// Create a Prisma client
const prisma = new PrismaClient()

async function main() {
  try {
    console.log('Starting database optimization process...')

    // Step 1: Apply Prisma migrations
    console.log('\n1. Applying Prisma migrations...')
    execSync('npx prisma migrate deploy', { stdio: 'inherit' })
    console.log(' Migrations applied successfully')

    // Step 2: Verify the HNSW index was created
    console.log('\n2. Verifying HNSW index...')
    const indexResult = await pool.query(`
      SELECT indexname, indexdef 
      FROM pg_indexes 
      WHERE tablename = 'Property' AND indexname = 'Property_embedding_hnsw_idx';
    `)

    if (indexResult.rows.length > 0) {
      console.log(' HNSW index verified:', indexResult.rows[0].indexdef)
    } else {
      console.error(' HNSW index not found. Please check the migration.')

      // Try to create the index manually if it doesn't exist
      console.log('Attempting to create the index manually...')
      await pool.query(`
        CREATE INDEX IF NOT EXISTS "Property_embedding_hnsw_idx" ON "Property" 
        USING hnsw (embedding vector_cosine_ops) 
        WITH (m = 16, ef_construction = 64);
      `)
      console.log('Manual index creation attempted. Please verify.')
    }

    // Step 3: Test the connection pool
    console.log('\n3. Testing connection pool...')
    const startTime = Date.now()

    // Run 10 concurrent queries to test the pool
    const promises = Array(10)
      .fill(0)
      .map((_, i) =>
        pool
          .query('SELECT NOW() as time')
          .then(res => console.log(`Query ${i + 1} completed at ${res.rows[0].time}`))
      )

    await Promise.all(promises)

    const duration = Date.now() - startTime
    console.log(` Connection pool test completed in ${duration}ms`)

    console.log('\nDatabase optimization process completed successfully!')
  } catch (error) {
    console.error('Error during database optimization:', error)
    process.exit(1)
  } finally {
    // Clean up
    await prisma.$disconnect()
    await pool.end()
  }
}

main()

import { exec as execCallback } from 'child_process'
import * as util from 'util'

// Use console for this script since we can't find the logger
// We'll look for console statements in other files, not this one
const exec = util.promisify(execCallback)

// Patterns to search for
const PATTERNS = [
  {
    name: 'Console statements',
    regex: /console\.(log|error|warn|info|debug)/g,
    command:
      'grep -r "console\\." --include="*.{ts,tsx,js,jsx}" . --exclude-dir={node_modules,.next,dist,build,coverage,.git,drizzle}',
    severity: 'warning',
  },
  {
    name: 'Any type',
    regex: /: any(\s|\)|\]|;|,)/g,
    command:
      'grep -r ": any" --include="*.{ts,tsx}" . --exclude-dir={node_modules,.next,dist,build,coverage,.git,drizzle}',
    severity: 'warning',
  },
  {
    name: 'Explicit null checks',
    regex: /([a-zA-Z0-9_]+)\s*===\s*null|\s*null\s*===\s*([a-zA-Z0-9_]+)/g,
    command:
      'grep -r "=== null\\|null ===" --include="*.{ts,tsx,js,jsx}" . --exclude-dir={node_modules,.next,dist,build,coverage,.git,drizzle}',
    severity: 'info',
  },
  {
    name: 'Direct error string concatenation',
    regex: /\+\s*error|\+\s*err|error\s*\+|err\s*\+/g,
    command:
      'grep -r "\\+ error\\|error \\+" --include="*.{ts,tsx,js,jsx}" . --exclude-dir={node_modules,.next,dist,build,coverage,.git,drizzle}',
    severity: 'warning',
  },
  {
    name: 'TODO comments',
    regex: /\/\/\s*TODO|\/\*\s*TODO/g,
    command:
      'grep -r "TODO" --include="*.{ts,tsx,js,jsx}" . --exclude-dir={node_modules,.next,dist,build,coverage,.git,drizzle}',
    severity: 'info',
  },
]

// Define types for better type safety
interface ExecError extends Error {
  code?: number
  stdout?: string
}

interface EslintResult {
  errorCount: number
  warningCount: number
  filePath: string
}

async function runHealthCheck() {
  console.info('Starting codebase health check...')

  const results: Record<string, { count: number; files: string[] }> = {}

  // Run checks in parallel
  await Promise.all(
    PATTERNS.map(async pattern => {
      try {
        const { stdout } = await exec(pattern.command)
        const lines = stdout.split('\n').filter(Boolean)

        results[pattern.name] = {
          count: lines.length,
          files: lines.map(line => line.split(':')[0]).filter((v, i, a) => a.indexOf(v) === i),
        }

        console.info(`Found ${lines.length} instances of ${pattern.name}`, {
          severity: pattern.severity,
          fileCount: results[pattern.name].files.length,
        })

        if (lines.length > 0 && lines.length <= 10) {
          console.info(`Sample occurrences of ${pattern.name}:`, {
            samples: lines.slice(0, 5),
          })
        }
      } catch (error) {
        // grep returns non-zero exit code when no matches are found
        const execError = error as ExecError
        if (execError.code === 1 && execError.stdout === '') {
          results[pattern.name] = { count: 0, files: [] }
          console.info(`No instances of ${pattern.name} found`, { severity: pattern.severity })
        } else {
          console.error(
            `Error checking for ${pattern.name}`,
            error instanceof Error ? error : new Error(String(error))
          )
        }
      }
    })
  )

  // Run ESLint check
  try {
    console.info('Running ESLint check...')
    const { stdout } = await exec('npx eslint --ext .ts,.tsx,.js,.jsx . --format json')

    // Parse ESLint JSON output
    const eslintResults = JSON.parse(stdout) as EslintResult[]
    const errorCount = eslintResults.reduce((sum: number, file) => sum + file.errorCount, 0)
    const warningCount = eslintResults.reduce((sum: number, file) => sum + file.warningCount, 0)

    console.info('ESLint check completed', {
      errorCount,
      warningCount,
      filesWithIssues: eslintResults.filter(file => file.errorCount > 0 || file.warningCount > 0)
        .length,
    })
  } catch (error) {
    console.error(
      'Error running ESLint check',
      error instanceof Error ? error : new Error(String(error))
    )
  }

  // Summary
  console.info('Health check summary', {
    results: Object.entries(results).map(([name, data]) => ({
      name,
      count: data.count,
      fileCount: data.files.length,
    })),
  })
}

runHealthCheck().catch(error => {
  console.error('Health check failed', error instanceof Error ? error : new Error(String(error)))
  process.exit(1)
})

// scripts/generate-property-embeddings.ts
import dotenv from 'dotenv'
import { Pool } from 'pg'
import pgvector from 'pgvector'

import { generateEmbeddings } from '../services/vectorSearch'

// Load environment variables
dotenv.config()

// Initialize pgvector
pgvector.init()

// Create a connection pool
const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false,
})

/**
 * Generate embeddings for all properties that don't have them
 */
async function generatePropertyEmbeddings() {
  const client = await pool.connect()

  try {
    console.log('Starting embedding generation for properties...')

    // Start a transaction
    await client.query('BEGIN')

    // Get properties without embeddings
    const { rows: properties } = await client.query(`
      SELECT id, title, description, location, property_type
      FROM properties
      WHERE embedding IS NULL
      LIMIT 100
    `)

    console.log(`Found ${properties.length} properties without embeddings`)

    // Process each property
    for (const [index, property] of properties.entries()) {
      try {
        // Create a text representation of the property for embedding
        const textToEmbed = `
          Property: ${property.title}
          Description: ${property.description || ''}
          Location: ${property.location || ''}
          Type: ${property.property_type || ''}
        `.trim()

        // Generate embeddings
        const embedding = await generateEmbeddings(textToEmbed)

        // Update the property with the embedding
        await client.query(`UPDATE properties SET embedding = $1 WHERE id = $2`, [
          pgvector.toSql(embedding),
          property.id,
        ])

        console.log(
          `[${index + 1}/${properties.length}] Generated embedding for property ${property.id}`
        )
      } catch (error) {
        console.error(`Failed to generate embedding for property ${property.id}:`, error)
      }
    }

    // Commit the transaction
    await client.query('COMMIT')

    console.log('Embedding generation completed')

    // If there are more properties to process, suggest running the script again
    if (properties.length === 100) {
      console.log(
        'There may be more properties without embeddings. Run the script again to process them.'
      )
    }
  } catch (error) {
    // Rollback the transaction on error
    await client.query('ROLLBACK')
    console.error('Embedding generation failed:', error)
  } finally {
    // Release the client back to the pool
    client.release()

    // Close the pool
    await pool.end()
  }
}

// Run the script
generatePropertyEmbeddings().catch(error => {
  console.error('Script failed:', error)
  process.exit(1)
})

