    }
  }
}

/**
 * Get a property by ID
 */
export async function getProperty(id: string): Promise<{
  success: boolean
  data?: Property
  error?: {
    message: string
    details?: string
  }
}> {
  try {
    const property = await prisma.property.findUnique({
      where: { id },
    })

    if (!property) {
      return {
        success: false,
        error: {
          message: 'Property not found',
        },
      }
    }

    // Convert the database model to the Property type
    const typedProperty: Property = {
      id: property.id,
      title: property.metadata?.title || property.address,
      location: `${property.address}, ${property.city}, ${property.state}`,
      price: property.price,
      imageUrl: property.metadata?.mainImageUrl || 'https://placehold.co/600x400/png?text=Property',
      lat: property.latitude || 51.505,
      lng: property.longitude || -0.09,
    }

    return {
      success: true,
      data: typedProperty,
    }
  } catch (error) {
    console.error('Error fetching property:', error)
    return {
      success: false,
      error: {
        message: 'Failed to fetch property',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
    }
  }
}

/**
 * Get all properties with optional filtering
 */
export async function getProperties(options?: {
  listingType?: 'sale' | 'rent'
  minPrice?: number
  maxPrice?: number
  bedrooms?: number
  propertyType?: string
  location?: string
  status?: string
  limit?: number
  offset?: number
}): Promise<{
  success: boolean
  data?: {
    properties: Property[]
    totalCount: number
  }
  error?: {
    message: string
    details?: string
  }
}> {
  try {
    const where: Record<string, unknown> = {}

    // Apply filters
    if (options?.listingType) {
      where.metadata = {
        path: ['listingType'],
        equals: options.listingType,
      }
    }

    if (options?.minPrice) {
      where.price = {
        ...where.price,
        gte: options.minPrice,
      }
    }

    if (options?.maxPrice) {
      where.price = {
        ...where.price,
        lte: options.maxPrice,
      }
    }

    if (options?.bedrooms) {
      where.bedrooms = {
        gte: options.bedrooms,
      }
    }

    if (options?.propertyType) {
      where.type = options.propertyType
    }

    if (options?.location) {
      where.OR = [
        { address: { contains: options.location, mode: 'insensitive' } },
        { city: { contains: options.location, mode: 'insensitive' } },
        { state: { contains: options.location, mode: 'insensitive' } },
        { zipCode: { contains: options.location, mode: 'insensitive' } },
      ]
    }

    if (options?.status) {
      where.status = options.status
    }

    // Get total count for pagination
    const totalCount = await prisma.property.count({ where })

    // Get properties with pagination
    const properties = await prisma.property.findMany({
      where,
      take: options?.limit || 10,
      skip: options?.offset || 0,
      orderBy: { createdAt: 'desc' },
    })

    // Convert the database models to the Property type
    const typedProperties: Property[] = properties.map(property => ({
      id: property.id,
      title: property.metadata?.title || property.address,
      location: `${property.address}, ${property.city}, ${property.state}`,
      price: property.price,
      imageUrl: property.metadata?.mainImageUrl || 'https://placehold.co/600x400/png?text=Property',
      lat: property.latitude || 51.505,
      lng: property.longitude || -0.09,
    }))

    return {
      success: true,
      data: {
        properties: typedProperties,
        totalCount,
      },
    }
  } catch (error) {
    console.error('Error fetching properties:', error)
    return {
      success: false,
      error: {
        message: 'Failed to fetch properties',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
    }
  }
}

import { NextRequest, NextResponse } from 'next/server'

import { prisma } from '@/lib/db'
import { generatePropertyEmbedding } from '@/lib/embeddings'
import { createPropertySchema } from '@/lib/schemas/propertySchemas'

/**
 * API route to generate embeddings for a property
 * POST /api/embeddings
 */
export async function POST(request: NextRequest) {
  try {
    // Parse request body
    const body = await request.json()

    // Validate property data
    const validationResult = createPropertySchema.safeParse(body)
    if (!validationResult.success) {
      return NextResponse.json(
        { error: 'Invalid property data', details: validationResult.error.format() },
        { status: 400 }
      )
    }

    const propertyData = validationResult.data

    // Generate embedding
    const embedding = await generatePropertyEmbedding({
      title: propertyData.title,
      location: propertyData.location,
      description: body.description || '',
    })

    // Return the embedding
    return NextResponse.json({ embedding })
  } catch (error) {
    console.error('Error generating embedding:', error)
    return NextResponse.json({ error: 'Failed to generate embedding' }, { status: 500 })
  }
}

