'use client'

import { useRouter, useSearchParams } from 'next/navigation'

import LoadMore from '@/components/LoadMore'
import Pagination from '@/components/Pagination'
import PropertyCard from '@/components/PropertyCard'
import SearchInput from '@/components/SearchInput'
import { Property } from '@/types/property'

interface SearchPageProps {
  properties: Property[]
  totalCount: number
  query: string
  page: number
  totalPages: number
}

export default function SearchPage({
  properties,
  totalCount,
  query,
  page,
  totalPages,
}: SearchPageProps) {
  const router = useRouter()
  const searchParams = useSearchParams()
  const useInfiniteScroll = searchParams.get('scroll') === 'infinite'

  const toggleScrollMode = () => {
    const params = new URLSearchParams(searchParams.toString())
    if (useInfiniteScroll) {
      params.delete('scroll')
    } else {
      params.set('scroll', 'infinite')
    }
    router.push(`/search?${params.toString()}`)
  }

  return (
    <main className="max-w-7xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-4">Search Results</h1>

      <SearchInput />

      <div className="mb-4 flex justify-between items-center">
        <div>
          {query ? (
            <p className="text-gray-600">
              Found {totalCount} properties matching{' '}
              <span className="font-semibold">"{query}"</span>
            </p>
          ) : (
            <p className="text-gray-600">Showing all properties</p>
          )}
        </div>

        <button
          onClick={toggleScrollMode}
          className="px-3 py-1 text-sm bg-blue-100 text-blue-800 rounded hover:bg-blue-200"
        >
          {useInfiniteScroll ? 'Switch to Pagination' : 'Switch to Infinite Scroll'}
        </button>
      </div>

      {properties.length === 0 ? (
        <p className="text-gray-500 py-10 text-center">
          No properties found. Try a different search term.
        </p>
      ) : useInfiniteScroll ? (
        <LoadMore initial={properties} query={query} />
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-8">
            {properties.map(property => (
              <PropertyCard key={property.id} {...property} />
            ))}
          </div>

          {totalPages > 1 && (
            <Pagination
              currentPage={page}
              totalPages={totalPages}
              baseUrl={`/search?q=${encodeURIComponent(query)}&page=`}
            />
          )}
        </>
      )}
    </main>
  )
}

'use client'

import debounce from 'lodash.debounce'
import { useRouter, useSearchParams } from 'next/navigation'
import { useState, useCallback, useTransition } from 'react'

import { useSearchStore } from '@/store/searchStore'

export default function SearchInput() {
  const [query, setQuery] = useState('')
  const { history, addQuery, clearHistory } = useSearchStore()
  const router = useRouter()
  const searchParams = useSearchParams()
  const [isPending, startTransition] = useTransition()

  const updateQuery = useCallback(
    debounce((q: string) => {
      const params = new URLSearchParams(searchParams.toString())
      q ? params.set('q', q) : params.delete('q')
      startTransition(() => {
        router.push(`/search?${params.toString()}`)
        addQuery(q)
      })
    }, 300),
    []
  )

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const val = e.target.value
    setQuery(val)
    updateQuery(val)
  }

  return (
    <div className="mb-4 space-y-2">
      <input
        type="text"
        className="w-full p-3 border rounded"
        value={query}
        onChange={onChange}
        placeholder="Search properties..."
        aria-label="Search input"
      />
      {history.length > 0 && (
        <div className="text-sm text-gray-500">
          Recent:{' '}
          {history.map((q, idx) => (
            <button
              key={idx}
              className="underline mx-1 text-blue-600"
              onClick={() => {
                setQuery(q)
                updateQuery(q)
              }}
            >
              {q}
            </button>
          ))}
          <button className="ml-4 text-red-500" onClick={() => clearHistory()}>
            Clear
          </button>
        </div>
      )}
    </div>
  )
}

/**
 * Centralized API error handling and standardized error responses
 */
import { NextResponse } from 'next/server'

import { logger } from './logger'

/**
 * Custom API Error class for standardized error handling
 */
export class APIError extends Error {
  constructor(
    public statusCode: number,
    public message: string,
    public details?: Record<string, unknown>
  ) {
    super(message)
    this.name = 'APIError'
    // This maintains proper stack trace in modern JS engines
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, APIError)
    }
  }
}

/**
 * Validation error for handling invalid input data
 */
export class ValidationError extends APIError {
  constructor(message: string, details?: Record<string, unknown>) {
    super(400, message, details)
    this.name = 'ValidationError'
  }
}

/**
 * Not found error for handling missing resources
 */
export class NotFoundError extends APIError {
  constructor(message: string) {
    super(404, message)
    this.name = 'NotFoundError'
  }
}

/**
 * Unauthorized error for handling authentication issues
 */
export class UnauthorizedError extends APIError {
  constructor(message: string = 'Unauthorized') {
    super(401, message)
    this.name = 'UnauthorizedError'
  }
}

/**
 * Forbidden error for handling permission issues
 */
export class ForbiddenError extends APIError {
  constructor(message: string = 'Forbidden') {
    super(403, message)
    this.name = 'ForbiddenError'
  }
}

/**
 * Handle API errors and return standardized response
 * @param error The error to handle
 * @returns NextResponse with appropriate status code and error details
 */
export function handleApiError(error: unknown): NextResponse {
  // Handle known API errors
  if (error instanceof APIError) {
    // Log error with appropriate level based on status code
    if (error.statusCode >= 500) {
      logger.error(`API Error [${error.statusCode}]: ${error.message}`, {
        details: error.details,
        stack: error.stack,
      })
    } else if (error.statusCode >= 400) {
      logger.warn(`API Error [${error.statusCode}]: ${error.message}`, {
        details: error.details,
      })
    }

    // Return standardized error response
    return NextResponse.json(
      {
        error: {
          message: error.message,
          ...(error.details && { details: error.details }),
        },
      },
      { status: error.statusCode }
    )
  }

  // Handle unexpected errors
  const message = error instanceof Error ? error.message : 'An unexpected error occurred'
  const stack = error instanceof Error ? error.stack : undefined

  // Log unexpected errors as critical
  logger.error('Unexpected API Error:', {
    message,
    stack,
    error,
  })

  // Return generic error for unexpected errors
  return NextResponse.json(
    {
      error: {
        message: 'Internal Server Error',
      },
    },
    { status: 500 }
  )
}

/**
 * Wrap an API handler with error handling
 * @param handler The API handler function to wrap
 * @returns A wrapped handler with error handling
 */
export function withErrorHandling(handler: Function) {
  return async (...args: any[]) => {
    try {
      return await handler(...args)
    } catch (error) {
      return handleApiError(error)
    }
  }
}

/**
 * Centralized logging utility for consistent logging across the application
 */
import { createLogger, format, transports } from 'winston'

// Determine if we're in production
const isProduction = process.env.NODE_ENV === 'production'

// Create the logger with the desired configuration
export const logger = createLogger({
  level: isProduction ? 'info' : 'debug',
  format: format.combine(
    format.timestamp({
      format: 'YYYY-MM-DD HH:mm:ss',
    }),
    format.errors({ stack: true }),
    format.splat(),
    format.json()
  ),
  defaultMeta: { service: 'property-portal' },
  transports: [
    // Write all logs to console
    new transports.Console({
      format: format.combine(
        format.colorize(),
        format.printf(({ level, message, timestamp, ...metadata }) => {
          let metaStr = ''
          if (metadata && Object.keys(metadata).length > 0) {
            if (metadata.stack) {
              // Format stack traces specially
              metaStr = `\n${metadata.stack}`
              delete metadata.stack
            }

            // Format remaining metadata if any
            if (Object.keys(metadata).length > 0) {
              metaStr += `\n${JSON.stringify(metadata, null, 2)}`
            }
          }
          return `${timestamp} ${level}: ${message}${metaStr}`
        })
      ),
    }),
  ],
})

// Add file transports in production
if (isProduction) {
  // Log everything to a combined log file
  logger.add(
    new transports.File({
      filename: 'logs/combined.log',
      maxsize: 5242880, // 5MB
      maxFiles: 5,
    })
  )

  // Log errors separately
  logger.add(
    new transports.File({
      filename: 'logs/error.log',
      level: 'error',
      maxsize: 5242880, // 5MB
      maxFiles: 5,
    })
  )
}

// Create a stream object for Morgan HTTP request logging
export const logStream = {
  write: (message: string) => {
    logger.info(message.trim())
  },
}

// Export convenience methods for use throughout the application
export default {
  debug: (message: string, meta?: any) => logger.debug(message, meta),
  info: (message: string, meta?: any) => logger.info(message, meta),
  warn: (message: string, meta?: any) => logger.warn(message, meta),
  error: (message: string, meta?: any) => logger.error(message, meta),
}

import { z } from 'zod'

import { createPropertySchema } from './schemas/propertySchemas'

// Type for CSV validation errors
export type CSVValidationError = {
  row: number
  errors: Record<string, string[]>
}

// Type for CSV validation result
export type CSVValidationResult = {
  valid: boolean
  data: z.infer<typeof createPropertySchema>[]
  errors: CSVValidationError[]
}

/**
 * Parse and validate a CSV string containing property data
 * @param csvString The CSV string to parse and validate
 * @returns Validation result with parsed data and any errors
 */
export function validatePropertyCSV(csvString: string): CSVValidationResult {
  // Initialize the result
  const result: CSVValidationResult = {
    valid: true,
    data: [],
    errors: [],
  }

  // Split the CSV into lines
  const lines = csvString.trim().split('\n')

  // Ensure there's at least a header and one data row
  if (lines.length < 2) {
    result.valid = false
    result.errors.push({
      row: 0,
      errors: {
        file: ['CSV file must contain a header row and at least one data row'],
      },
    })
    return result
  }

  // Parse the header row
  const header = parseCSVLine(lines[0])

  // Validate required columns
  const requiredColumns = ['title', 'location', 'price', 'bedrooms', 'bathrooms']
  const missingColumns = requiredColumns.filter(col => !header.includes(col))

  if (missingColumns.length > 0) {
    result.valid = false
    result.errors.push({
      row: 0,
      errors: {
        header: [`Missing required columns: ${missingColumns.join(', ')}`],
      },
    })
    return result
  }

  // Process each data row
  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim()
    if (!line) continue // Skip empty lines

    const rowData = parseCSVLine(line)

    // Create an object from the CSV row
    const propertyData: Record<string, any> = {}
    header.forEach((column, index) => {
      if (index < rowData.length) {
        // Convert numeric values
        if (['price', 'bedrooms', 'bathrooms', 'area', 'lat', 'lng'].includes(column)) {
          propertyData[column] = parseFloat(rowData[index])
        } else {
          propertyData[column] = rowData[index]
        }
      }
    })

    // Validate the property data
    const validationResult = createPropertySchema.safeParse(propertyData)

    if (validationResult.success) {
      result.data.push(validationResult.data)
    } else {
      result.valid = false
      result.errors.push({
        row: i,
        errors: validationResult.error.flatten().fieldErrors,
      })
    }
  }

  return result
}

/**
 * Parse a CSV line into an array of values
 * Handles quoted values with commas inside them
 * @param line The CSV line to parse
 * @returns Array of values
 */
function parseCSVLine(line: string): string[] {
  const result: string[] = []
  let current = ''
  let inQuotes = false

  for (let i = 0; i < line.length; i++) {
    const char = line[i]

    if (char === '"') {
      // Toggle quote state
      inQuotes = !inQuotes
    } else if (char === ',' && !inQuotes) {
      // End of field
      result.push(current.trim())
      current = ''
    } else {
      // Add character to current field
      current += char
    }
  }

  // Add the last field
  result.push(current.trim())

  return result
}

import { ClassValue, clsx } from 'clsx'
import { twMerge } from 'tailwind-merge'

/**
 * Combines multiple class names using clsx and tailwind-merge
 * This allows for conditional classes and prevents class conflicts
 */
export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

import { Prisma } from '@prisma/client'
import { describe, it, expect, vi, beforeEach, afterEach } from 'vitest'

import { prisma } from '@/lib/db'

// Mock the Prisma client
vi.mock('@/lib/db', () => ({
  prisma: {
    $queryRaw: vi.fn(),
    property: {
      findMany: vi.fn(),
    },
  },
}))

// Create a helper function to simulate the pgvector query
async function findSimilarProperties(
  embedding: number[],
  limit: number = 5,
  excludeId?: string
): Promise<any[]> {
  // Build the where clause
  const whereClause = excludeId
    ? Prisma.sql`WHERE id != ${excludeId} AND embedding IS NOT NULL`
    : Prisma.sql`WHERE embedding IS NOT NULL`

  // Execute the query
  return prisma.$queryRaw(Prisma.sql`
    SELECT 
      id, title, description, price, bedrooms, bathrooms, 
      area, location, address, lat, lng, images, features,
      councilTaxBand, epcRating, tenure, createdAt, updatedAt,
      embedding <=> ${Prisma.sql`${embedding}`}::vector AS similarity
    FROM "Property"
    ${whereClause}
    ORDER BY similarity
    LIMIT ${Prisma.sql`${limit}`}
  `)
}

describe('Vector Search', () => {
  beforeEach(() => {
    vi.resetAllMocks()
  })

  afterEach(() => {
    vi.clearAllMocks()
  })

  it('should execute a pgvector similarity search query', async () => {
    // Mock embedding vector
    const mockEmbedding = Array(1536).fill(0.1)

    // Mock the query result
    const mockProperties = [
      { id: '1', title: 'Property 1', similarity: 0.1 },
      { id: '2', title: 'Property 2', similarity: 0.2 },
    ]
    vi.mocked(prisma.$queryRaw).mockResolvedValueOnce(mockProperties)

    // Execute the search
    const result = await findSimilarProperties(mockEmbedding, 2)

    // Verify the result
    expect(result).toEqual(mockProperties)

    // Verify the query was called with the correct parameters
    expect(prisma.$queryRaw).toHaveBeenCalledWith(
      expect.objectContaining({
        values: expect.arrayContaining([mockEmbedding, 2]),
      })
    )
  })

  it('should exclude a specific property ID when provided', async () => {
    // Mock embedding vector
    const mockEmbedding = Array(1536).fill(0.1)
    const excludeId = '123'

    // Mock the query result
    const mockProperties = [
      { id: '1', title: 'Property 1', similarity: 0.1 },
      { id: '2', title: 'Property 2', similarity: 0.2 },
    ]
    vi.mocked(prisma.$queryRaw).mockResolvedValueOnce(mockProperties)

    // Execute the search with an excluded ID
    const result = await findSimilarProperties(mockEmbedding, 2, excludeId)

    // Verify the result
    expect(result).toEqual(mockProperties)

    // Verify the query was called with the correct parameters
    expect(prisma.$queryRaw).toHaveBeenCalledWith(
      expect.objectContaining({
        values: expect.arrayContaining([excludeId, mockEmbedding, 2]),
      })
    )
  })

  it('should handle empty result sets', async () => {
    // Mock embedding vector
    const mockEmbedding = Array(1536).fill(0.1)

    // Mock an empty result
    vi.mocked(prisma.$queryRaw).mockResolvedValueOnce([])

    // Execute the search
    const result = await findSimilarProperties(mockEmbedding)

    // Verify the result is empty
    expect(result).toEqual([])
    expect(result.length).toBe(0)
  })

  it('should propagate database errors', async () => {
    // Mock embedding vector
    const mockEmbedding = Array(1536).fill(0.1)

    // Mock a database error
    const dbError = new Error('Database connection error')
    vi.mocked(prisma.$queryRaw).mockRejectedValueOnce(dbError)

    // Execute the search and expect it to throw
    await expect(findSimilarProperties(mockEmbedding)).rejects.toThrow('Database connection error')
  })
})
